const vec2 k = vec2(1.,0.);
const float pi = acos(-1.);

float dist(vec2 p_0,vec2 p_1,vec2 p_2,vec2 x,float t)
{
    return length(x-pow(1.-t,2.)*p_0-2.*(1.-t)*t*p_1-t*t*p_2);
}

float DistToCurve(vec2 p_0,vec2 p_1,vec2 p_2, vec2 uv)
{
	//coefficients for 0 = t^3 + a * t^2 + b * t + c
    vec2 E = uv-p_0;
    vec2 F = p_2-2.*p_1+p_0;
    vec2 G = p_1-p_0;
    vec3 coeff = vec3(3.*dot(G,F), 2.*dot(G,G)-dot(E,F), -dot(E,G))/dot(F,F);

    float a = coeff.x;
    float b = coeff.y;
    float c = coeff.z;

	//discriminant and helpers
    float p = b-a*a/3.;
    float q = 2.*a*a*a/27.-a*b/3.+c;
    float dis = q*q/4.+p*p*p/27.;
    float tau = a/3.;

    //solution with cardan formula
    float sqrtDis = sqrt(dis);
    float k1 = -.5*q+sqrtDis;
    float k2 = -.5*q-sqrtDis;
    float u = sign(k1)*pow(abs(k1), 1./3.);
    float v = sign(k2)*pow(abs(k2), 1./3.);

    float t_min = 1.;

    if(dis > 0.)
    {
        t_min = u+v-tau;
        t_min = clamp(t_min, 0.,1.);
    }
    else if(dis < 0.)
    {


        float fac = sqrt(-4./3.*p);
        float arg = -.5*q*sqrt(-27./p/p/p);
        float acosArg = acos(arg)/3.;

           float t1 = -fac*cos(acosArg+pi/3.)-tau,
            t2 = fac*cos(acosArg)-tau,
            t3 = -fac*cos(acosArg-pi/3.)-tau;
        t1 = clamp(t1,0.,1.);
        t2 = clamp(t2,0.,1.);
        t3 = clamp(t3,0.,1.);
        float b1 = dist(p_0,p_1,p_2,uv,t1),
            b2 = dist(p_0,p_1,p_2,uv,t2),
            b3 = dist(p_0,p_1,p_2,uv,t3);
        t_min = mix(mix(t1, t2, step(b2, b1)), t3, step(b3, min(b1, b2)));
    } 

    return dist(p_0,p_1,p_2,uv,t_min);
}

// *************************************** 

vec2 Rotate(in vec2 uv, in float angle)
{

	float si = sin(angle);
	float co = cos(angle);
	float tx = uv.x;
	float ty = uv.y;
	uv.x = (co * tx) - (si * ty);
	uv.y = (si * tx) + (co * ty);	

    return uv;
}

float DistToLine(vec2 pos, vec2 a, vec2 b) {
		vec2 pa = pos - a;
		vec2 ba = b - a;
		float t = clamp(dot(pa, ba)/dot(ba,ba), 0., 1.);
		return length(pa - ba * t);
	}

vec4 GetColorPalette(in float x)
{
    float r = smoothstep(0.33, 0., x) + smoothstep(0.66, 1., x);
    float g = smoothstep(0., 0.33, x)* smoothstep(0.66, 0.33, x);
    float b = smoothstep(0.33, 0.66, x)* smoothstep(1., 0.66, x);

    vec3 col = sqrt(vec3(r,g,b)); // not sure about this

	return vec4(col, 1);
}
