// Mostly a generic framebuffer feedback thing, with some
// scribbling over the top.
// To help edge towards rounded shapes, the smoothed and
// sharpened buffer is fed back into this as well, so that
// it gradually sharpens itself as it mixes and the shapes
// should become progressively rounder from frame to frame
// (if not for the stirring).


vec4 funborder(sampler2D s, vec2 uv) {
    if (any(notEqual(uv, fract(uv)))) {
        uv -= 0.5;
        float th = atan(uv.y, uv.x) + iTime;
        return max(vec4(0), sin(vec4(0.4, -0.7, -0.9, 0.1) * th) - 0.6);
    }
    return texture(s, uv);
}

// Just some random perturbation of the coordinates to make things swirl.
vec2 f(in vec2 uv) {
    const vec2 centre = vec2(0.35, 0.35);
    const float th = 0.003;
    const float r = 0.9995;
    float c = cos(th), s = sin(th);
    uv = (uv - centre) * mat2(c, s, -s, c) * r + centre;
    vec2 wiggle = vec2(sin(iTime + uv.y * 15.1), cos(uv.x * 14.1));
    uv += wiggle * 0.002;

    return uv;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iChannelResolution[0].xy;
    vec2 vu = uv.yx;
    uv = f(uv);
    vu = f(vu).yx;
    vec4 q = 0.5 * vec4(funborder(iChannel1, uv).xz,
                        funborder(iChannel1, vu).yw).xzyw;
    vec4 col = vec4(funborder(iChannel0, uv).xz,
                    funborder(iChannel0, vu).yw).xzyw;

    col = mix(col, q, 0.008);

    // Try to enlarge colour fringes a bit.
    col *= vec4(1.005, 0.995, 0.995, 1.005);

    fragColor = col;
}

buffer_d_code:
// Read a blurred Buffer A (blurring aims to represent surface tension),
// and quantise it according to the average of the previous frame.
// The idea is that the average of the result should seek the median
// weight of Buffer A so that half the buffer is lit.
// This is fed back into buffer A to encourage it to gradually gain
// more defined edges and the expected weight, while still being able
// to move and blur.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;

    // Sample the main framebuffer with a whole lot of smoothing.
    // Simply using the LOD bias on its own produces horrible artefacts,
    // so try to do the smoothing manually here.
    // TODO: make a better approximation of Gaussian blur
    vec4 colour = 0.50 * texture(iChannel0, uv, 6.5)
                + 0.25 * texture(iChannel0, uv, 4.5)
                + 0.16 * texture(iChannel0, uv, 2.5);
                + 0.09 * texture(iChannel0, uv, 0.5);

#if 0
    // Take the average of the whole of the previous output;
    // if this is low then too few pixels were 'set', and
    // if it is high then too many pixels were 'set'.
    vec4 threshold = textureLod(iChannel1, vec2(0.5), 100.5);
#else
    // Take the average of the whole of the current input;
    // This divides the output into above and below average,
    // which is not the same as the median, but I think it will
    // seek the median more stably than above.
    vec4 threshold = textureLod(iChannel0, vec2(0.5), 100.5);
#endif

    // I didn't really think this through.  We need to make an informed
    // guess at where the correct threshold should be, but it's fiddly
    // trying to be stateful about previous guesses, so I just mashed
    // the keyboard until the flashing and blinking didn't happened so
    // much.
    threshold = mix(threshold, vec4(0.7), 0.1);
    colour = smoothstep(-0.005, 0.005, colour - threshold);

    fragColor = colour;
}

