float opCheapBendBox(in vec3 p, in vec3 b )
{
    const float k = 0.6; // or some other amount
    float c = cos(k*p.x);
    float s = sin(k*p.x);
    mat2  m = mat2(c,-s,s,c);
    vec3  q = vec3(m*p.xy,p.z);
    return sdBox(q, b);
}


