    float bodyA = sdSphere(posP - vec3(0.0, 0.0, 0.0), 0.56);
    float bodyB = sdSphere(posP - vec3(-0.55, 0.0, 0.0), 0.25);
    float bodyC = sdBox(posP - vec3(-1.0, 0.244, 0.0), vec3(0.35, 0.04, 0.2)) - 0.04;
    float body = bodyA;
    body = sminCubic(body, bodyB, 1.8);
    body = sminCubic(body, bodyC, 0.6);
	res = opU( res, vec2(body, 2.0));

    float neck = sdCylinder(posP - vec3(0.0, 0.5, 0.0), vec2(0.15, 0.5 *hh));
	res = opU( res, vec2(neck, 3.0));

    float bill = opCheapBendBox(posP - vec3(0.3, -0.04 + hh, 0.0), vec3(0.4, 0.04, 0.125)) - 0.03;
    float pSc = 0.1;

    // Local coordinate for bill
    vec3 posB = posP - vec3(0.3,0.1 + hh, 0.0);

    vec3 posBPyr = vec3(rot2d(0.55 * PI) * posB.xy, posB.z);
    float pyrBill = sdPyramid( 1./pSc*posBPyr, 2.0 )*pSc - 0.1;
    bill = sminCubic(bill, pyrBill, 0.2);
	res = opU( res, vec2(bill, 4.0));

    //res = opU( res, vec2(sminCubic(pyr, s, 0.7), 2.0) );

    float bez = sdBezier(
        vec3(0.4),
        vec3(1.0, 0.0, 0.0),
        vec3(1.0, -0.2, 0.0),
        pos
    ).x - 0.25;
    //res = opU(res, vec2(bez, 1.0));


    return res;
}

// https://iquilezles.org/articles/boxfunctions
vec2 iBox( in vec3 ro, in vec3 rd, in vec3 rad ) 
{
    vec3 m = 1.0/rd;
    vec3 n = m*ro;
    vec3 k = abs(m)*rad;
    vec3 t1 = -n - k;
    vec3 t2 = -n + k;
	return vec2( max( max( t1.x, t1.y ), t1.z ),
	             min( min( t2.x, t2.y ), t2.z ) );
}

const float maxHei = 0.8;

vec2 castRay( in vec3 ro, in vec3 rd )
{
    vec2 res = vec2(-1.0,-1.0);

    float tmin = 1.0;
    float tmax = 20.0;

    // raytrace floor plane
    float tp1 = (0.0-ro.y)/rd.y;
    if( tp1>0.0 )
    {
        tmax = min( tmax, tp1 );
        res = vec2( tp1, 1.0 );
    }
    //else return res;

    // raymarch primitives   
    vec2 tb = iBox( ro-vec3(0.5,0.5,-0.5), rd, vec3(2.0, 1.0,3.0) );
    if( tb.x<tb.y && tb.y>0.0 && tb.x<tmax)
    {
        tmin = max(tb.x,tmin);
        tmax = min(tb.y,tmax);

        float t = tmin;
        for( int i=0; i<STEPS && t<tmax; i++ )
        {
            vec2 h = map( ro+rd*t );
            if( abs(h.x)<(0.0001*t) )
            { 
                res = vec2(t,h.y); 
                 break;
            }
            t += h.x;
        }
    }

    return res;
}


// https://iquilezles.org/articles/rmshadows
float calcSoftshadow( in vec3 ro, in vec3 rd, in float mint, in float tmax )
{
    // bounding volume
    float tp = (maxHei-ro.y)/rd.y; if( tp>0.0 ) tmax = min( tmax, tp );

    float res = 1.0;
    float t = mint;
    for( int i=ZERO; i<16; i++ )
    {
		float h = map( ro + rd*t ).x;
        float s = clamp(8.0*h/t,0.0,1.0);
        res = min( res, s*s*(3.0-2.0*s) );
        t += clamp( h, 0.02, 0.10 );
        if( res<0.005 || t>tmax ) break;
    }
    return clamp( res, 0.0, 1.0 );
}

// https://iquilezles.org/articles/normalsSDF
vec3 calcNormal( in vec3 pos )
{
#if 0
    vec2 e = vec2(1.0,-1.0)*0.5773*0.0005;
    return normalize( e.xyy*map( pos + e.xyy ).x + 
