        vec2 F = Fv(U, cp.x) +
            	 Fv(U, cp.y) +
            	 Fv(U, cp.z) +
                 Fv(U, cp.w) +
            	 -20.*imageF(U.xy);

        if(iMouse.z > 0.) 
        {
