    vec3 rayOrigin = vec3(cos(iTime * 0.2) * 12.0, 1.5 + sin(iTime * 0.5), sin(iTime * 0.2) * 12.0); // Camera position

    vec3 camForward = normalize(VIEW_TARGET - rayOrigin);
    vec3 camRight = normalize(cross(WORLD_UP, camForward));
    vec3 camUp = cross(camForward, camRight);

    vec3 screenCenter = rayOrigin + camForward * ZOOM_FAC; // Figure out where the center of the screen is in 3D space
    vec3 intersectPoint = screenCenter + (uv.x * camRight) + (uv.y * camUp);

    vec3 rayDir = intersectPoint - rayOrigin;

    float rayDist = rayMarch(rayOrigin, rayDir);
    vec3 point = rayOrigin + rayDir * rayDist; // Get the point in 3D space

    vec3 lightPos;
    lightPos = vec3(0.0 + sin(iTime * 1.5) * 8.0, 5.0, 6.0 + cos(iTime * 1.5) * 8.0);
