        material.emissive = vec3(0.0f, 0.0f, 0.0f);
    	if (TestPlaneTrace(rayPos, rayDir, hitInfo, vec4( normalize(vec3(0.0f, 1.0f, 0.0f)), -5.0f), material))
        {
