        col.xyz += BRDF(-r, vec3(0,cos(1.),sin(1.)), n.xyz, a);
        col.xyz += BRDF(-r, vec3(0,sin(1.),cos(1.)), n.xyz, a)*3.;
        col.xyz += BRDF(-r, vec3(0,-sin(1.),-cos(1.)), n.xyz, a);
        col.xyz*=ao*sky(vec3(1.,0.,0.));
    }
    col = vec4(HDRmapping(col.xyz, 6.),1.);
}

