void mengerFold(inout vec4 z) 
{
	float a = min(z.x - z.y, 0.0);
	z.x -= a; z.y += a;
	a = min(z.x - z.z, 0.0);
	z.x -= a; z.z += a;
	a = min(z.y - z.z, 0.0);
	z.y -= a; z.z += a;
}

vec2 minid(vec2 a, vec2 b)
{
