#define SR iSampleRate
#define e 1e-3
float intfract(int, float);
// synth
float synth(int samp, float rt)
{
    uint uta=uint(.125*rt);
    float rs2=(2.*rt),ft=mod(rs2,1.),
          a=sqrt(2.*ft*exp(log2((1.+1e-5)-ft)/(.5+ft)));//attack/decay
    float p0ar[4u]=float[4u](491.,291.,251.,e);
    float p1ar[12u]=float[12u](1000.,e,e,e,e,e,e,1120.,880.,e,600.,e);
    //freq
    float[4u]p0a=float[4u](0.,0.,0.,0.);
    p0a[0u]=440.;p0a[1u]=720.;
    p0a[2u]=samp>=1411200?p0ar[((1315313u%(uta+1u))%3u)]:491.;//44100*32
    p0a[3u]=991.;
    return(sin(6.283185*intfract(samp,SR/p0a[uint(rs2)%4u]))
           +sin(6.283185*intfract(samp,SR/p1ar[uta%12u])))*a;
}
// reverberation
const float[6u]ha=float[6u](.184089,.118295,.074423,.046412,.029080,.018004);
const uint sz = 6u; //uint(ha.length());
vec2[6u]z; //=vec2[6u](vec2(1e-3),vec2(1e-3),vec2(1e-3),vec2(1e-3),vec2(1e-3),vec2(1e-3));
vec2[sz]stdly(in vec2 v,out float t,in int sn)
{
    float rsn=(float(sn)/SR),cds=rsn+(.25*t);//copy dry timesignal
    t+=ha[uint(rsn/SR)%sz];
    rsn+=t;
    // wet + dry * rot
    v.x=synth(sn/2,rsn)+.251*synth(sn,cds)*(cos(t)+sin(t));
    v.y=synth(sn/2,rsn)+.251*synth(sn,cds)*(-sin(t)+cos(t));
    // unroll for mobile and similar z =vec2[](v, v, v, v, v, v);
    z[0]=v;z[1]=v;z[2]=v;z[3]=v;z[4]=v;z[5]=v;
    return z;
}
vec2 mainSound( in int s,float time)
{
   // int block=int((iBlockOffset+0.5)*iSampleRate/(512.*512.));
   // int s=512*512*block+512*int(gl_FragCoord.y)+int(gl_FragCoord.x);
    vec2 snd=vec2(1e-3),v=vec2(1e-3);
    float t=float(s)/SR;
    for(uint j=0u;j<3u;++j)
        for(uint i=0u;i<sz;++i)
            snd+=stdly(v,t,s)[i];
    // --------out
    return snd*0.027778;//(6.*float(sz))
}
float intfract(int s,float n)
{
    // Explanation and copyright: shadertoy.com/view/4ltfRN
    int sn=int(n),a=s%sn,b=(s/sn);
    const float MAXF=float(uint(-1))+1.;
    return fract((float(a)/n)+float((uint(b)*uint(MAXF*float(sn)/n)))/MAXF);
}

