/*
 Looking inside the Mandelbox and Mandalay fractals.
 Step-through showing slices overlaid on full view.
 Mouse-x rotates, -y zooms
*/

#define M_TYPE   0  // = 0/1 for Mandelbox / Mandalay fractals

#define VAR_ZERO min (iFrame, 0)

float PrBoxDf (vec3 p, vec3 b);
vec3 HsvToRgb (vec3 c);
float Minv3 (vec3 p);
float Maxv3 (vec3 p);
mat3 StdVuMat (float el, float az);
vec2 Rot2D (vec2 q, float a);

vec3 ltDir, pFold;
float tCur, dstFar, mScale, bxLim, slPos, slWid;
int mType;
const int itMax = 12;
bool isTrans;
const float pi = 3.14159;

float PPFoldD (vec3 p)
{
  vec3 s;
  p.y = max (p.y, p.z);
  s = vec3 (p.x, max (abs (p.x - pFold.x) - pFold.x, p.y - 4. * pFold.x),
     max (p.x - 2. * pFold.x - pFold.y, p.y - pFold.z));
  return Minv3 (s);
}

vec3 PPFold (vec3 p)
{
  return vec3 (PPFoldD (p), PPFoldD (p.yzx), PPFoldD (p.zxy));
}

float ObjDf (vec3 p)
{
  vec4 p4;
  float d;
  p4 = vec4 (p, 1.);
  for (int j = 0; j < itMax; j ++) {
    p4.xyz = 2. * clamp (p4.xyz, -1., 1.) - p4.xyz;
    if (mType == 1) p4.xyz = - sign (p4.xyz) * PPFold (abs (p4.xyz));
    p4 = mScale * p4 / clamp (dot (p4.xyz, p4.xyz), 0.25, 1.) + vec4 (p, 1.);
  }
  d = max (length (p4.xyz) / p4.w, PrBoxDf (p, vec3 (bxLim)));
  if (! isTrans) d = max (d, abs (p.z - slPos) - slWid);
  return d;
}

vec3 ObjTDist (vec3 p)
{
  vec4 p4;
  vec3 pMin;
  pMin = vec3 (1.);
  p4 = vec4 (p, 1.);
  for (int j = 0; j < itMax; j ++) {
    p4.xyz = 2. * clamp (p4.xyz, -1., 1.) - p4.xyz;
    if (mType == 1) p4.xyz = - sign (p4.xyz) * PPFold (abs (p4.xyz));
    pMin = min (pMin, abs (p4.xyz));
    p4 = mScale * p4 / clamp (dot (p4.xyz, p4.xyz), 0.25, 1.) + vec4 (p, 1.);
  }
  return pMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, h, s, sLo, sHi, eps;
  eps = 0.0005;
  s = 0.;
  sLo = 0.;
  dHit = dstFar;
  for (int j = VAR_ZERO; j < 160; j ++) {
    h = ObjDf (ro + s * rd);
    if (h < eps || s > dstFar) {
      sHi = s;
      break;
    }
    sLo = s;
    s += h;
  }
  if (h < eps) {
    for (int j = VAR_ZERO; j < 5; j ++) {
      s = 0.5 * (sLo + sHi);
      if (ObjDf (ro + s * rd) > eps) sLo = s;
      else sHi = s;
    }
    dHit = sHi;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.0002, -0.0002);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec3 col, colT, vn, roo;
  float dstObj, dstObjT;
  bool isBg;
  mScale = (mType == 1) ? 3. : 2.5;
  roo = ro;
  isTrans = false;
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    isBg = false;
    ro += dstObj * rd;
    vn = ObjNf (ro);
    col = HsvToRgb (vec3 (mod (0.2 + 0.15 * Maxv3 (abs (ObjTDist (ro))), 1.), 0.5, 1.)) *
       (0.3 + 0.7 * max (dot (vn, ltDir), 0.)) +
       0.1 * pow (max (0., dot (ltDir, reflect (rd, vn))), 16.);
   } else {
    isBg = true;
    col = vec3 (0.3, 0.3, 0.3);
  }
  isTrans = true;
  ro = roo;
  dstObjT = ObjRay (ro, rd);
  if (dstObjT < min (dstObj, dstFar)) {
    ro += dstObjT * rd;
    vn = ObjNf (ro);
    colT = HsvToRgb (vec3 (mod (0.2 + 0.15 * Maxv3 (abs (ObjTDist (ro))), 1.), 0.5, 1.)) *
       (0.3 + 0.7 * max (dot (vn, ltDir), 0.));
    colT = mix (colT, vec3 (0.6, 0.65, 0.6) * smoothstep (0., 1., Maxv3 (colT)), 0.7);
    col = mix (col, colT, (isBg ? 0.35 : 0.15));
  }
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col[2];
  vec2 canvas, uv;
  float el, az, zmFac, tCyc;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  mType = M_TYPE;
  bxLim = (mType == 1) ? 5. : 2.8;
  if (mType == 1) pFold = HsvToRgb (vec3 (0.5, 0.2, 1.));
  dstFar = 80.;
  az = -0.25 * pi;
  el = 0.;
  zmFac = (mType == 1) ? 5.6 : 5.2;
  if (mPtr.z > 0.) {
    az += 2. * pi * mPtr.x;
    zmFac += (mType == 1) ? 15. * (mPtr.y + 0.5) : 30. * (mPtr.y + 0.5);
  }
  vuMat = StdVuMat (el, az);
  ro = vuMat * vec3 (0., 0., -6.6 * bxLim);
  rd = vuMat * normalize (vec3 (uv, zmFac));
  ltDir = vuMat * normalize (vec3 (1., 2., -1.));
  slWid = 0.05;
  tCyc = 100.;
  tCur *= 1.5;
  for (int k = VAR_ZERO; k <= 1; k ++) {
    slPos = (bxLim - slWid) * (4. * (0.5 - abs (mod ((float (k) +
       floor (tCur)), tCyc) / tCyc - 0.5)) - 1.);
    col[k] = ShowScene (ro, rd);
  }
  fragColor = vec4 (mix (col[0], col[1], smoothstep (0.1, 0.9, fract (tCur))), 1.);
}

float PrBoxDf (vec3 p, vec3 b)
{
  vec3 d;
  d = abs (p) - b;
  return min (max (d.x, max (d.y, d.z)), 0.) + length (max (d, 0.));
}

vec3 HsvToRgb (vec3 c)
{
  return c.z * mix (vec3 (1.), clamp (abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. - 3.) - 1., 0., 1.), c.y);
}

float Minv3 (vec3 p)
{
  return min (p.x, min (p.y, p.z));
}

float Maxv3 (vec3 p)
{
  return max (p.x, max (p.y, p.z));
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

// Hold left mouse button to enable the flipping

//I found this useful to improve the visual quality of blue noise dithered particles in the game I'm working on.
//On odd frames it flips the blue noise around 0, making the dithering looking smoother over time
//It doesn't work in screenshots or if you pause.. just in motion
//It reminds me  FRC used in monitors https://en.wikipedia.org/wiki/Frame_rate_control (not sure how related it really is)


//from: https://www.shadertoy.com/view/WtfSDS
float VNoiseCell( in vec2 p ){
	vec2 uv = p;

    //Fetch 4 value noises--
    vec4 r = texture(iChannel0, uv/float(iChannelResolution[0])).rgba*2.0-1.0;

    //make some cells using geometric average
    float t = abs(r.a*r.b*r.g*r.r);

    float geo_avg = intBitsToFloat(798731503 + (floatBitsToInt(t)>>2)); 
    return geo_avg*2.0 -1.0;
}
//note: from https://github.com/Unity-Technologies/ScriptableRenderPipeline/blob/master/com.unity.render-pipelines.high-definition/Runtime/PostProcessing/Shaders/FinalPass.shader#L124
float remap_pdf_tri_unity( float v )
{
    v = v*2.0-1.0;
    return sign(v) * (1.0 - sqrt(1.0 - abs(v)));
}

vec2 GetFinalUV(vec2 uv){
   return uv*.078 + 1.1;//(.13 + cos(iTime*.3)*.03)  + 1.1 + sin(iTime*.25)*.03; 
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = fragCoord.xy / iResolution.xy;

	vec2 uv = p*vec2(iResolution.x/iResolution.y,1.0);

    vec2 bn = texture(iChannel1, fragCoord/vec2(iChannelResolution[1].xy) ).rg;

    //2 seperate trianglar mapped blue noise samples for high quality dither
	vec2 blueNoiseDither = vec2(remap_pdf_tri_unity(bn.x), remap_pdf_tri_unity(bn.y));

	float f = 0.0;
    float split = .5;
    float midPt = split*(iResolution.x/iResolution.y);


    bool mouseBtnPressed = (iMouse.z >= 1.0);

    //8 bit fixed point so adjust sub pixel precision by 1/256
    float ditherSize = (1.0/256.0);
    if(mouseBtnPressed)
    {
        //On odd frames flip the dither value around 0
        if(bool(iFrame&1)){
           blueNoiseDither = -blueNoiseDither;
        }
    }
	f = VNoiseCell(GetFinalUV(uv) + blueNoiseDither*ditherSize);


	f = 2.0 + 2.0*f;

	fragColor = vec4( f, f, f, 1.0 );
}

