bool inWall(vec2 pos) {
    return texture(iChannel0, pos/iResolution.xy).x > .5;
}

float getLight(vec2 pos, vec2 lpos) {
    vec2 dir = pos-lpos;
    float dist = length(dir);
    float light = 3./pow(dist,.5);
    float dotf = dist*.2;
    int dot = int(dotf);
    float walls = 0.;
    for (int i = 0; i <= dot; i++) {
        vec2 p = lpos + (float(i)/dotf)*dir;
        if (inWall(p)) {
            walls++;
            if (walls > 4.) {
                break;
            }
        }
    }

    return light * (5.-walls)/5.;
}

