#define RAD 5.
void mainImage( out vec4 fragColor, in vec2 coord )
{
    float time = iTime * .5;
    vec2 light1Pos = iResolution.xy * (vec2(.5) + vec2(cos(time), sin(time))*.3);
    vec2 light2Pos = iResolution.xy * (vec2(.5) + vec2(cos(time*.4), sin(time*.8))*.4);
    // need to add light together in squared space then squareroot again to properly add light values
    vec3 light = sqrt(pow(getLight(coord, iMouse.xy, vec3(1)), vec3(2.)) +
                      pow(getLight(coord, light1Pos, vec3(1, 1, 0)), vec3(2.)) +
                     pow(getLight(coord, light2Pos, vec3(0, 1, 1)), vec3(2.)));

    vec3 oldLightU = texelFetch(iChannel2, ivec2(coord.x, coord.y+RAD), 0).rgb;
    vec3 oldLightD = texelFetch(iChannel2, ivec2(coord.x, coord.y-RAD), 0).rgb;
    vec3 oldLightL = texelFetch(iChannel2, ivec2(coord.x+RAD, coord.y), 0).rgb;
    vec3 oldLightR = texelFetch(iChannel2, ivec2(coord.x-RAD, coord.y), 0).rgb;

    vec3 maxAround = max(max(oldLightU, oldLightD), max(oldLightL, oldLightR));

    vec3 oldLightUR = texelFetch(iChannel2, ivec2(coord.x-RAD, coord.y+RAD), 0).rgb;
    vec3 oldLightDR = texelFetch(iChannel2, ivec2(coord.x-RAD, coord.y-RAD), 0).rgb;
    vec3 oldLightUL = texelFetch(iChannel2, ivec2(coord.x+RAD, coord.y+RAD), 0).rgb;
    vec3 oldLightDL = texelFetch(iChannel2, ivec2(coord.x+RAD, coord.y-RAD), 0).rgb;

    vec3 maxAroundDiag = max(max(oldLightUR, oldLightDR), max(oldLightUL, oldLightDL));

    maxAround = max(maxAround, maxAroundDiag*.94);  // multiplier should be .9 ^ (1/sqrt(2)), not sure why but had to tweak to .94 to remove artifacts
    light = max(light, clamp(maxAround, 0., 1.)*.9);
    fragColor = vec4(light, 1);
}

