void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    float ratio = iResolution.x / iResolution.y;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;
    uv.x *= ratio;
    vec2 treeOffset = uv - vec2(0.33, 0.7);

    // Make a sky gradient, blue at the top, pink at the bottom
    vec3 col = mix(vec3(0.969, 0.659, 0.722), vec3(0.333, 0.804, 0.988), pow(uv.y, 1.0 / 1.4));

    // Make a sun and day/night cycle
    float sunDist = length(uv - vec2(sin(iTime * 0.2) + (ratio * 0.5), cos(iTime * 0.2) - 0.2)) * 0.7;
    col += vec3(1.0, 1.0, 1.0) * (0.5 - sunDist);

    // Make leaves
    const float LEAF_COUNT = 10.0;
    float leafSize = 0.1 + sin(iTime) * 0.01;
    float radius = 0.2 + (leafSize * cos(atan(treeOffset.y, treeOffset.x) * LEAF_COUNT + (20.0 * treeOffset.x + 1.0 + cos(iTime * 0.8))));
    col *= smoothstep(radius, radius + 0.01, length(treeOffset));

    // Make a tree trunk
    float treeTrunkWidth = 0.015;
    treeTrunkWidth += 0.002 * cos(120.0 * treeOffset.y); // Make the trunk wobbly
    treeTrunkWidth += exp(-40.0 * uv.y); // Make the trunk smoothly grow to make the ground
    col *= 1.0 -
        (
         1.0 -
         smoothstep(
             // Define the width of the trunk
             treeTrunkWidth,
             treeTrunkWidth + 0.002,

             // Vary the X position of the trunk depending on the Y position, to curve the trunk
             abs(treeOffset.x - (0.2 * sin(2.0 * treeOffset.y)))
             )
        ) *
        // Make the trunk end where the leaves start
        (
         1.0 -
         smoothstep(0.0, 0.01, treeOffset.y)
        );

    // Output to screen
    fragColor = vec4(col, 1.0);
}

#define ANGLE 2.1
#define DELTA 0.00635
#define XOFF .034531

#define time iTime
mat2 mm2(in float a){float c = cos(a), s = sin(a);return mat2(c,-s,s,c);}


float f(vec2 p, float featureSize)
{
	p.x = cos(p.x*0.6+time*0.2)*log(time+p.x*0.1)*ANGLE;	
    p += sin(p.x*0.5)*ANGLE;
    return smoothstep(-0.01,featureSize,abs(p.y));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float aspect = iResolution.x/iResolution.y;
    float featureSize = 135./((iResolution.x*aspect+iResolution.y));

    vec2 p = (fragCoord.xy/(ANGLE*XOFF)) / (iResolution.xy*ANGLE);
	p.x *= aspect;
	p.y = abs(p.y);

	vec3 col = vec3(.04);
    vec3 col2 = (cos(vec3(3.3,2.5,2.2)-2.*0.15)*0.74)*(1.-f(p,featureSize));
	for(float i=0.;i<26.;i+=(XOFF*12.))
	{
		col2 = (cos(vec3(3.3,2.5,2.2)-i*ANGLE)*0.74)*(1.-f(p,featureSize));
		col = max(col,col2);

        p.x -= XOFF;
        p.y -= tanh(time*0.31+1.5)*3.;//((ANGLE/XOFF)*XOFF); //1.5+1.5;
		p*= mm2(i*DELTA+ANGLE);

        vec2 pa = vec2(abs(p.x-0.9),abs(p.y));
        vec2 pb = vec2(p.x,abs(p.y));

        p = mix(pa,pb,smoothstep(-.47,.67,cosh(time*0.14)-ANGLE));
	}
	fragColor = vec4(col,1.0);
}

