// License CC0
//  Coded while listening to some 1970s Tangerine Dream. Big thanks to ollj for introducing me to SABS
//  Code not very elegant and contains repeated concepts but I liked the result
//  Colors are better in non-chromium browsers


#define PI          3.141592654
#define TAU         (2.0*PI)
#define SCA(a)      vec2(sin(a), cos(a))
#define TTIME       ((iTime-232.0)*TAU)
#define LESS(a,b,c) mix(a,b,step(0.,c))
#define SABS(x,k)   LESS((.5/k)*x*x+k*.5,abs(x),abs(x)-k)


vec2 hash(vec2 p) {
  p = vec2(dot (p, vec2 (127.1, 311.7)), dot (p, vec2 (269.5, 183.3)));
  return -1. + 2.*fract (sin (p)*43758.5453123);
}

float noise(vec2 p) {
  const float K1 = .366025404;
  const float K2 = .211324865;

  vec2 i = floor (p + (p.x + p.y)*K1);

  vec2 a = p - i + (i.x + i.y)*K2;
  vec2 o = step (a.yx, a.xy);    
  vec2 b = a - o + K2;
  vec2 c = a - 1. + 2.*K2;

  vec3 h = max (.5 - vec3 (dot (a, a), dot (b, b), dot (c, c) ), .0);

  vec3 n = h*h*h*h*vec3 (dot (a, hash (i + .0)),dot (b, hash (i + o)), dot (c, hash (i + 1.)));

  return dot (n, vec3 (70.));
}

float fbm(vec2 pos, float tm) {
  vec2 offset = vec2(cos(tm), sin(tm*sqrt(0.5)));
  float aggr = 0.0;

  aggr += noise(pos);
  aggr += noise(pos + offset) * 0.5;
  aggr += noise(pos + offset.yx) * 0.25;
  aggr += noise(pos - offset) * 0.125;
  aggr += noise(pos - offset.yx) * 0.0625;

  aggr /= 1.0 + 0.5 + 0.25 + 0.125 + 0.0625;

  return (aggr * 0.5) + 0.5;    
}

vec3 lightning(vec2 pos, float offset, float dl) {
  vec3 col = vec3(0.0);
  vec2 f = 10.0*SCA(PI/2.0 + TTIME/1000.0);

  float width = 0.003*dl;

  for (int i = 0; i < 2; i++) {
    float btime = TTIME/35.0 + float(i);
    float rtime = TTIME/40.0 + float(i) + 10.0;
    float d1 = abs(offset * width / (0.0 + offset - fbm((pos + f) * 3.0, rtime)));
    float d2 = abs(offset * width / (0.0 + offset - fbm((pos + f) * 2.0, btime)));
    col += vec3(d1 * vec3(0.1, 0.3, 0.8));
    col += vec3(d2 * vec3(0.7, 0.3, 0.5));
  }

  return clamp(col, 0.0, 1.0);;
}


vec2 toSmith(vec2 p)  {
  // z = (p + 1)/(-p + 1)
  // (x,y) = ((1+x)*(1-x)-y*y,2y)/((1-x)*(1-x) + y*y)
  float d = (1.0 - p.x)*(1.0 - p.x) + p.y*p.y;
  float x = (1.0 + p.x)*(1.0 - p.x) - p.y*p.y;
  float y = 2.0*p.y;
  return vec2(x,y)/d;
}

vec2 fromSmith(vec2 p)  {
  // z = (p - 1)/(p + 1)
  // (x,y) = ((x+1)*(x-1)+y*y,2y)/((x+1)*(x+1) + y*y)
  float d = (p.x + 1.0)*(p.x + 1.0) + p.y*p.y;
  float x = (p.x + 1.0)*(p.x - 1.0) + p.y*p.y;
  float y = 2.0*p.y;
  return vec2(x,y)/d;
}

float circle(vec2 p, float r) {
  return length(p) - r;
}

float tile0(vec2 p, float hwidth, float lwidth) {
  float c0 = circle(p - vec2(hwidth), hwidth);  
  float c1 = circle(p + vec2(hwidth), hwidth);  
  c0 = abs(c0) - lwidth;
  c1 = abs(c1) - lwidth;

  float d = c0;
  d = min(d, c1);

  return d;
}

float tile1(vec2 p, float hwidth, float lwidth) {
  p = abs(p);
  float c0 = circle(p - vec2(hwidth), hwidth);  
  c0 = abs(c0) - lwidth;

  float d = c0;

  return d;
}

float tile2(vec2 p, float hwidth, float lwidth) {
  // Empty cell
  return hwidth*4.0;
}

float tile3(vec2 p, float hwidth, float lwidth) {
  p = abs(p);
  float c0 = abs(p.x) - lwidth;

  float d = c0;

  return d;
}

float tile4(vec2 p, float hwidth, float lwidth) {
  p.x = abs(p.x);
  float c0 = circle(p - vec2(hwidth, hwidth), hwidth);  
  c0 = abs(c0) - lwidth;

  float d = c0;

  return d;
}

float rand(vec2 co) {
  return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

vec2 toPolar(vec2 p) {
  return vec2(length(p), atan(p.y, p.x));
}

vec2 toRect(vec2 p) {
  return vec2(p.x*cos(p.y), p.x*sin(p.y));
}


float tile(vec2 p, vec2 n, float hwidth, float lwidth) {
  float rnd = rand(n);

  vec2 ap = abs(p);

  vec2 pp = toPolar(p);
  pp.y -= float(int(mod(1000.0*rnd, 4.0)))*PI/2.0;  
  p = toRect(pp);

  float d = 0.0;  

  if (rnd > 0.95) {
    d =  tile0(p, hwidth, lwidth);
  } else if (rnd > 0.85) {
    d = tile0(p, hwidth, lwidth);
  } else if (rnd > 0.75) {
    d =  tile3(p, hwidth, lwidth);
  } else if (rnd > 0.65) {
    d =  tile1(p, hwidth, lwidth);
  } else {
    d =  tile0(p, hwidth, lwidth);
  }

  float da1 = circle(ap - vec2(hwidth, 0.0), lwidth);  
  float da2 = circle(ap - vec2(0.0, hwidth), lwidth);  

  float da = min(da1, da2);

  return min(d, da);
}

vec2 mod2(inout vec2 p, vec2 size) {
  vec2 c = floor((p + size*0.5)/size);
  p = mod(p + size*0.5,size) - size*0.5;
  return c;
}

float df(vec2 p) {
  float hwidth = 1.0;
  float lwidth = 0.25;
  vec2 n = mod2(p, vec2(hwidth*2.0));  
  float d = tile(p, n, hwidth, lwidth);

  return d;
}

void rot(inout vec2 p, float a) {
  float c = cos(a);
  float s = sin(a);
  p = vec2(c*p.x + s*p.y, -s*p.x + c*p.y);
}

vec2 coordinateTransform(vec2 p) {
  p.x = SABS(p.x, 0.1);
  p *= 5.0;
  rot(p, TTIME/100.0);
  vec2 sp = toSmith(p);
  float x = 3.;
  sp.x += x;
  sp.y += x;
  p = fromSmith(sp);
  return p;
}

vec4 dCoordinateTransform(vec2 p) {
  vec4 nor;
  vec2 eps = vec2(0.0001, 0.0);

  nor.xy = (coordinateTransform(p + eps.xy) - coordinateTransform(p - eps.xy));
  nor.zw = (coordinateTransform(p + eps.yx) - coordinateTransform(p - eps.yx));

  return nor/eps.x;
}

vec3 postProcess(vec3 col, vec2 q) {
  col=pow(clamp(col,0.0,1.0),vec3(0.75)); 
  col=col*0.6+0.4*col*col*(3.0-2.0*col);  // contrast
  col=mix(col, vec3(dot(col, vec3(0.33))), -0.4);  // satuation
  col*=0.5+0.5*pow(19.0*q.x*q.y*(1.0-q.x)*(1.0-q.y),0.7);  // vigneting
  return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) { 
  vec2 q = fragCoord/iResolution.xy;
  vec2 p = -1.0 + 2.0*q;
  p.x *= iResolution.x/iResolution.y;


  float z = 0.5;

  vec3 col = vec3(0.0);

  vec4 dp = dCoordinateTransform(p);
  float dl = length(dp);
  p = coordinateTransform(p);
  p.y += TTIME/200.0;

  float d = df(p/z)*z;

  float pp = 0.3;
  vec3 li = lightning(p, tanh(pow(max(d, 0.0), pp)), 20.0*pow(1.5*dl, pp));
  col = 1.0 - li;

  float f = tanh(0.01*dl);
  col = pow(col, vec3(1.0 - f))*vec3(1.2, 0.7, 0.5);
  col = clamp(col, 0.0, 1.0);
  col = mix(col, vec3(0.), f);

  col = postProcess(col, q);

  fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (12.0+sin(iTime)*4.0) * (fragCoord - iResolution.xy / (2.0)) / min(iResolution.y, iResolution.x);

    float lar;

    for (float i=0.0; i < 6.0; i++)
    {
        lar = length(vec2(uv.x, uv.y));
		float uvx1= uv.x;
        uv.x = - uv.y + sin(uv.x)*(uv.x);
        uv.y = - uvx1 + sin(uv.y)*(uv.y);
    }
    // Output to screen
    fragColor = vec4(cos(lar * 2.0), cos(lar * 3.0), cos(lar * 1.0), 1.0);
}

//noise function from iq: https://www.shadertoy.com/view/Msf3WH
vec2 hash( vec2 p ) 
{
	p = vec2( dot(p,vec2(127.1,311.7)), dot(p,vec2(269.5,183.3)) );
	return -1.0 + 2.0*fract(sin(p)*43758.5453123);
}

//quintique
vec2 fade5(vec2 f) {return f * f * f * (f * (f * 6.0 - 15.0) + 10.0);}

//cubique
vec2 fade3(vec2 f) {return f*f*(3.0-2.0*f);}

float noise(vec2 p)
{
 	vec2 i = floor( p );
    vec2 f = fract( p );

    vec2 u = fade5(f);

    return mix( mix( dot( hash( i + vec2(0.0,0.0) ), f - vec2(0.0,0.0) ), 
