const int N = 6;
// Shuffle the array according to the rank.
// 0 <= k < N!
int unrankperm(int k, int t) {
  int a[N];
  for (int i = 0; i < N; i++) {
    a[i] = i;
  }
  for (int i = 0; i < N-1; i++) {
    int k1 = k%(N-i);
    k = k/(N-i);
    int tmp = a[i+k1];
    if (i == t) return tmp;
    a[i+k1] = a[i];
    a[i] = tmp;

  }
  return a[t];
}

// unrank in lexicographic order - shuffle, but instead of
// swapping the selected item with the front item, just
// close the gap, but keep the same order.
int unrankperm2(int k, int t) {
  int a[N];
  int p = 1;
  for (int i = 0; i < N; i++) {
    a[i] = i;
    p *= i+1;
  }
  // p is N!
  for (int i = N; i > 1; i--) {
    p /= i;
    // p is (i-1)!
    int k1 = k/p;
    k = k%p;
    int tmp = a[k1];
    for (int j = k1; j < i-1; j++) {
      a[j] = a[j+1];
    }
    a[i-1] = tmp;
  }
  return a[N-t-1];
}

