    float osc1 = cos(progress * PI2);
    vec2 uv = (fragCoord.xy / iResolution.xy) * (1.0 - osc1 * 0.1) + sin(progress * PI2 )* 0.05;

    vec3 c = cosPalette((snoise(uv) +  pow(uv.x, 4.0))  * (osc1 + 10.0),
        vec3(0.6, 0.4, 0.3),
        vec3(2.0, 0.4, 0.3),
        vec3(0.2, 0.4, 0.5),
        vec3(0.2, 0.2, osc1)
    );
