  vec4 m = max(0.6 - vec4(dot(x0,x0), dot(x1,x1), dot(x2,x2), dot(x3,x3)), 0.0);
  m = m * m;
  return 42.0 * dot( m*m, vec4( dot(p0,x0), dot(p1,x1), 
                                dot(p2,x2), dot(p3,x3) ) );
}

float voronoi(const in vec2 point )
{
    vec2 p = floor( point );
    vec2 f = fract( point );
    float res = 0.0;
    for( int j=-1; j<=1; j++ ) {
        for( int i=-1; i<=1; i++ ) {
            vec2 b = vec2( i, j );
            vec2 r = vec2( b ) - f + rhash( p + b);
            res += 1./pow(dot(r,r),8.);
        }
    }
    return pow(1./res, 0.0625);
}

vec3 voronoi( const in vec3 x ) {
    vec3 p = floor( x );
    vec3 f = fract( x );

    float id = 0.0;
    vec2 res = vec2( 100.0 );
    for( int k=-1; k<=1; k++ ) {
        for( int j=-1; j<=1; j++ ) {
            for( int i=-1; i<=1; i++ ) {
                vec3 b = vec3( float(i), float(j), float(k) );
                vec3 r = vec3( b ) - f + hash( p + b );
                float d = dot( r, r );

                float cond = max(sign(res.x - d), 0.0);
                float nCond = 1.0 - cond;

                float cond2 = nCond * max(sign(res.y - d), 0.0);
                float nCond2 = 1.0 - cond2;

                id = (dot(p + b, vec3(1.0, 57.0, 113.0)) * cond) + (id * nCond);
                res = vec2(d, res.x) * cond + res * nCond;

                res.y = cond2 * d + nCond2 * res.y;
            }
        }
    }

    return vec3( sqrt( res ), abs(id) );
}

//brownian
float fbm(float x, const in int it) {
    float v = 0.0;
    float a = 0.5;
    float shift = float(100);
    for (int i = 0; i < 32; ++i) {
        if(i<it) {
            v += a * noise(x);
            x = x * 2.0 + shift;
            a *= 0.5;
        }
    }
    return v;
}

float fbm(vec2 x, const in int it) {
    float v = 0.0;
    float a = 0.5;
    vec2 shift = vec2(100);
    // Rotate to reduce axial bias
    mat2 rot = mat2(cos(0.5), sin(0.5), -sin(0.5), cos(0.50));
    for (int i = 0; i < 32; ++i) {
        if(i<it) {
            v += a * noise(x);
            x = rot * x * 2.0 + shift;
            a *= 0.5;
        }
    }
    return v;
}

float fbm(vec3 x, const in int it) {
    float v = 0.0;
    float a = 0.5;
    vec3 shift = vec3(100);
    for (int i = 0; i < 32; ++i) {
        if(i<it) {
            v += a * noise(x);
            x = x * 2.0 + shift;
            a *= 0.5;
        }
    }
    return v;
}

//ridged multifractal
float rmf(vec2 uv, const in int it) {
    float l = 2.;
    float r = 0.;
    float a = 0.5;
    float f = 1.0;
    for(int i = 0; i < 32; i++) {
        if(i<it) {
            uv = uv.yx * l;
            float n = noise(uv);     
            n = abs(fract(n-.5)-.5);
            n *= n * a;
            a = clamp(0.,1., n*2.);
            r += n*pow(f, -1.);
            f *= l;
        }
    }
    return r*2.;
}

//voronoi fbm
float vfbm(const in vec2 uv, const in int it) {
    float n = 0.;
    float a = 0.5;
    float f = 1.0;
    for(int i = 0; i < 32; i++) {
        if(i<it) {
            n += voronoi(uv*f)*a;
            f *= 2.;
            a *= .5;
        }
    }
    return n;
}

//ridged multifractal
float vrmf(vec2 uv, const in int it) {
    float l = 2.;
    float r = 0.;
    float a = 0.5;
    float f = 1.0;
    for(int i = 0; i < 32; i++) {
        if(i<it) {
            uv = uv.yx * l;
            float n = voronoi(uv);     
            n = abs(fract(n-.5)-.5);
            n *= n * a;
            a = clamp(0.,1., n*2.);
            r += n*pow(f, -1.);
            f *= l;
        }
    }
    return r*2.;
}

const vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
vec3 hsv2rgb(vec3 c) {
  vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
  return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

// https://iquilezles.org/articles/palettes
// As t runs from 0 to 1 (our normalized palette index or domain), 
//the cosine oscilates c times with a phase of d. 
//The result is scaled and biased by a and b to meet the desired constrast and brightness.
vec3 cosPalette( float t, vec3 a, vec3 b, vec3 c, vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}

image_code:
vec2 rand2d(vec2 p) {
    return fract(sin(vec2(
        dot(p, vec2(12.345, 67.890)),
        dot(p, vec2(190.61, 312.21))
    )) * 41235.45) * 2.0 - 1.0;
}

float perlin(vec3 p) {
    vec2 u = floor(p.xz);
    vec2 f = fract(p.xz);
    vec2 s = smoothstep(0.0, 1.0, f);

    vec2 a = rand2d(u);
    vec2 b = rand2d(u + vec2(1.0, 0.0));
    vec2 c = rand2d(u + vec2(0.0, 1.0));
    vec2 d = rand2d(u + vec2(1.0, 1.0));

    return mix(
        mix(dot(a, f), dot(b, f - vec2(1.0, 0.0)), s.x),
        mix(dot(c, f - vec2(0.0, 1.0)), dot(d, f - vec2(1.0, 1.0)), s.x),
        s.y
    );
}

float fbm(vec3 p) {
    p.xz *= 2.0;
    int octaves = 2;
    float amplitude = 0.5;
    float value = 0.0;
    for (int i = 0; i < octaves; i++) {
        p += iTime * vec3(0.1, 0.0, 0.3);
        value += amplitude * perlin(p);
        p *= 2.0;
        amplitude *= 0.5;
    }
    return value;
}

float water(vec3 p) {
    p.xz *= 0.2;
    float f = fbm(p);
    float g = fbm(p + vec3(0.5, 1.0, 0.2));
    return p.y + fbm(vec3(f, 0.0, g)) - 0.5;
}

float distToCenter(vec3 p) {
    return clamp(1.5 - length(p.xz) / 5.0, 0.0, 1.5);
}

float sol(vec3 p) {
    float dtm = distToCenter(p);
    float per = perlin(p * 0.7) * 1.0 + 3.5;
    return p.y - per * dtm;
//    return p.y + distToMiddle;
}

vec2 map(vec3 p) {
    float closest = 1000.0;
    float id = -1.0;

    float dist = water(p);
    if (dist < closest) { closest = dist; id = 0.5; }

    dist = sol(p);
    if (dist < closest) { closest = dist; id = 1.5; }

    return vec2(closest, id);
}

vec2 intersect(vec3 ro, vec3 rd) {
    float depth = 0.0;
    float id = -1.0;
    for (int i = 0; i < 160; i++) {
        vec2 info = map(ro + rd * depth);
        if (info.x <= 0.001) {
            id = info.y;
            break;
        }
        depth += info.x;
    }
    return vec2(depth, id);
}

vec3 getIslandColor(vec3 p) {
    vec3 baseColor = vec3(0.5, 0.21, 0.1);
    float total = 1.0;
    float sandiness = 1.0 - clamp((p.y - 0.2) * (perlin(p) * 0.5 + 0.5) * 1.3, 0.0, 1.0);
    baseColor = mix(baseColor, vec3(0.76, 0.69, 0.5), sandiness);

    float grassiness = (total - sandiness) * clamp(1.0 - (p.y - 3.87), 0.0, 1.0);
    baseColor = mix(baseColor, vec3(0.28, 0.6, 0.21), grassiness);

    baseColor = mix(baseColor, vec3(1.0, 1.0, 1.0), total - sandiness - grassiness);
//    float dtm = distToMiddle(p);
    return vec3(baseColor);
}

vec3 getSkyColor(vec3 p, vec3 rd) {
    vec3 skyColor = vec3(0.3, 0.65, 0.99);
    skyColor = mix(skyColor, vec3(1.0), pow(clamp(rd.y, 0.0, 1.0), 0.7));
    return skyColor;
}

vec3 getColor(float id, vec3 p, vec3 rd) {
    if (id < -0.5) { return getSkyColor(p, rd); }
    if (id < 1.0) { return vec3(0.21, 0.67, 0.89) * 0.7; }
    if (id < 2.0) { return getIslandColor(p); }
    return vec3(1.0, 0.0, 0.0);
}

vec3 getNormal(vec3 p) {
    const float epsilon = 0.001;
    return normalize(vec3(
        map(vec3(p.x + epsilon, p.yz)).x - map(vec3(p.x - epsilon, p.yz)).x,
        map(vec3(p.x, p.y + epsilon, p.z)).x - map(vec3(p.x, p.y - epsilon, p.z)).x,
        map(vec3(p.x, p.y, p.z + epsilon)).x - map(vec3(p.x, p.y, p.z - epsilon)).x
    ));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord.xy / iResolution.xy * 2.0 - 1.0;
    float aspect = iResolution.x / iResolution.y;
    uv.x *= aspect;
    vec3 ro = vec3(13.0 - (sin(iTime) * 0.5 + 0.5), 3.0 + sin(iTime * 0.5), 1.0 + cos(iTime));
    vec3 center = vec3(0.0, 1.5, 0.0);

    vec3 lightDir = normalize(vec3(1.0, 1.0, 1.0));

    vec3 front = normalize(center - ro);
    vec3 right = normalize(cross(front, vec3(0.0, 1.0, 0.0)));
    vec3 up = normalize(cross(right, front));
    mat4 lookAt = mat4(
        vec4(right, 0.0),
        vec4(up, 0.0),
        vec4(front, 0.0),
        vec4(0.0, 0.0, 0.0, 1.0)
    );
    vec3 rd = normalize(vec3(lookAt * vec4(uv, 2.0, 1.0)));

    vec2 info = intersect(ro, rd);

    vec3 pos = ro + rd * info.x;

    float foginess = 1.0 - clamp(distToCenter(pos) * 6.0, 0.0, 1.0);
    if (foginess >= 1.0) {
        vec3 objColor = getSkyColor(pos, rd);
        objColor = pow(objColor, vec3(0.4545));
        fragColor = vec4(objColor, 1.0);
        return;
    }
    vec3 n = getNormal(pos);

    float ambient = 1.0;
    float diffuse = max(dot(n, lightDir), 0.0);
    float dome = 0.2 + 0.8 * clamp(n.y, 0.0, 1.0);
    float back = max(dot(n, vec3(-lightDir.x, 0.0, -lightDir.z)), 0.0);

    vec3 light = vec3(0.0);
    light += ambient * vec3(0.1, 0.1, 0.1);
    light += diffuse * vec3(1.0, 1.0, 1.12);
    light += dome * vec3(0.1, 0.1, 0.4);
    light += back * vec3(5.0, 2.9, 2.0);

    if (info.y < -0.5) {
        light = vec3(1.0);
    }

    vec3 objColor = getColor(info.y, pos, rd) * light;
    objColor = mix(objColor, getSkyColor(pos, rd), foginess);
    objColor = pow(objColor, vec3(0.4545));

    fragColor = vec4(objColor, 1.0);
}

//
// friol 2o2o
// music KV by Bloc https://soundcloud.com/kvmusicprod/bloc
// (watch this with music, please. be sure music is in sync)
// sdf functions by iq
// blob taken from mercury and modified
// da rest is by P.Mondrian
// 06.05.2020: reduced compilation time as for suggestions by iq
//

const int iterationAmount=256;
const float patternLength=14.5;
const float numPatterns=4.0;


float opSubtraction( float d1, float d2 ) { return max(-d1,d2); }

vec3 rotx(in vec3 p, float a) 
{
	return vec3(p.x,
                cos(a) * p.y + sin(a) * p.z,
                cos(a) * p.z - sin(a) * p.y);
}

vec3 roty(in vec3 p, float a) {
	return vec3(cos(a) * p.x + sin(a) * p.z,
                p.y,
                cos(a) * p.z - sin(a) * p.x);
}

vec3 rotz(in vec3 p, float a) {
	return vec3(cos(a) * p.x + sin(a) * p.y,
                cos(a) * p.y - sin(a) * p.x,
                p.z);
}

float opSmoothUnion( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); }

float sdSphere(vec3 p, float r)
{
    return length(p) - r;
}

float sdRoundBox( vec3 p, vec3 b, float r )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0) - r;
}

float sdPlane( vec3 p, vec4 n )
{
  return dot(p,n.xyz) + n.w;
}

float sdBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float sdPyramid( vec3 p, float h)
{
  float m2 = h*h + 0.25;

  p.xz = abs(p.xz);
  p.xz = (p.z>p.x) ? p.zx : p.xz;
  p.xz -= 0.5;

  vec3 q = vec3( p.z, h*p.y - 0.5*p.x, h*p.x + 0.5*p.y);

  float s = max(-q.x,0.0);
  float t = clamp( (q.y-0.5*p.z)/(m2+0.25), 0.0, 1.0 );

  float a = m2*(q.x+s)*(q.x+s) + q.y*q.y;
  float b = m2*(q.x+0.5*t)*(q.x+0.5*t) + (q.y-m2*t)*(q.y-m2*t);

  float d2 = min(q.y,-q.x*m2-q.y*0.5) > 0.0 ? 0.0 : min(a,b);

  return sqrt( (d2+q.z*q.z)/m2 ) * sign(max(q.z,-p.y));
}

float sdOctahedron( vec3 p, float s)
{
  p = abs(p);
  float m = p.x+p.y+p.z-s;
  vec3 q;
       if( 3.0*p.x < m ) q = p.xyz;
  else if( 3.0*p.y < m ) q = p.yzx;
  else if( 3.0*p.z < m ) q = p.zxy;
  else return m*0.57735027;

  float k = clamp(0.5*(q.z-q.y+s),0.0,s); 
  return length(vec3(q.x,q.y-s+k,q.z-k)); 
}

float sdTorus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

float fBlob(vec3 p) 
{
    float fft = texture( iChannel0, vec2(0.0,0.0) ).x;
    p*=(1.5+(fft*0.4));
    p=roty(p,iTime);
    p=rotx(p,iTime);

    float PHI=(sqrt(5.0)*0.5 + 0.5);
	p = abs(p);
	if (p.x < max(p.y, p.z)) p = p.yzx;
	if (p.x < min(p.y, p.z)) p = p.yzx;
	float b = max(max(min(
		dot(p, normalize(vec3(.3, 1., 1.))),
		dot(p.xz, normalize(vec2(PHI+1., 1.)))),
		dot(p.yx, normalize(vec2(1, PHI)))),
		dot(p.xz, normalize(vec2(1, PHI))));
	float l = length(p)/(1.0+.01*abs(sin(iTime)));
	return l - 1.5 - 0.2 * (1.5 / 2.0)* .4*sin(min(sqrt(1.01 - b / l)*(3.141592 / 0.25), 3.141592));
}

vec2 SDF(vec3 r)
{
    vec3 rOrig=r;
    float mat=0.0;

    float plane0=sdPlane(r					,vec4(0.0,1.0,0.0,1.0));
    float plane1=sdPlane(r-vec3(0.0,0.1,0.0),vec4(0.0,-1.0,0.0,1.0));

    float domainRep=10.0;
    vec3 c=vec3(domainRep);
    r = mod(r+0.5*c,c)-0.5*c;

    float b0=sdBox(r,vec3(1.0,1.1,1.0));
    float b1=sdBox(r,vec3(2.0,.9,.8));
    float b2=sdBox(r-vec3(0.0,0.,0.0),vec3(0.8,0.9,2.0));

    float b3=sdBox(r-vec3(0.0,-0.9,-0.9),vec3(10.0,0.1,0.1));
    float b4=sdBox(r-vec3(0.9,-0.9,0.0),vec3(0.1,0.1,10.0));
    float b5=sdBox(r-vec3(-0.9,-0.9,0.0),vec3(0.1,0.1,10.0));
    float b6=sdBox(r-vec3(0.0,-0.9,0.9),vec3(10.0,0.1,0.1));
    float t1=opSubtraction(b1,b0);
    float t0=opSubtraction(b2,t1);
    t0=min(t0,b3);
    t0=min(t0,b4);
    t0=min(t0,b5);
    t0=min(t0,b6);

    float s0=sdSphere(r-vec3(.6*sin(iTime),0.2*cos(iTime),.5*sin(iTime)),0.3);
    float s1=sdSphere(r-vec3(.5*cos(iTime),-0.2*sin(iTime),0.0),0.2);
    float s2=sdSphere(r-vec3(.7*cos(iTime),-0.5*cos(iTime),-.2*sin(iTime)),0.2);

    //vec2 uv = ((normal.xy + 1.0) * 0.5)*4.0;
	//float h = texture(iChannel0, uv).x/32.0;  

    float h0=0.,h1=0.,h2=0.;
    float unionCoeff=0.2;
    float spheres=opSmoothUnion(opSmoothUnion(s0-h0,s1-h1,unionCoeff),s2-h2,unionCoeff);

    float pyr0=sdOctahedron((((r)-vec3(2.0,-2.0,0.0))),1.5);
    float pyr1=sdOctahedron((((r)-vec3(-2.0,-2.0,0.0))),1.5);

    float roamingSphere;
    float sphTime=iTime*(patternLength/10.615);

    float modTime=mod(iTime,patternLength*numPatterns);    
    if (modTime<patternLength)
    {
    	roamingSphere=sdSphere(r-vec3(0.0,-10.6,1.5+abs(5.0*sin(sphTime))),0.4);
    }
    else
    {
    	roamingSphere=sdSphere(r-vec3(0.0,-.6,1.5+abs(5.0*sin(sphTime))),0.4);
    }

    vec3 rr=rotz(r,3.141592/2.0);
    float roamingSphere2=sdTorus(rr-vec3(-.5,0.0,-1.5-abs(5.0*cos(sphTime))),vec2(0.35,0.2));

    float mirror=sdBox(r-vec3(0.0,0.0,5.0),vec3(.8,1.1,.1));


    float t=min(min(min(min(min(min(min(min(t0,plane0),plane1),spheres),pyr0),pyr1),roamingSphere),mirror),roamingSphere2);
    if (t==plane0) mat=1.0;
    if (t==plane1) mat=1.0;
    if (t==spheres) mat=2.0;
    if (t==pyr0) mat=3.0;
    if (t==pyr1) mat=4.0;
	if (t==roamingSphere) mat=6.0;                                 
	if (t==roamingSphere2) mat=7.0;                                 
    if (t==mirror) mat=5.0;

    if (modTime>=patternLength*3.0)        
    {
        float centeredBlob=fBlob(rOrig-vec3(5.0,0.0,1.0+iTime));
        if (centeredBlob<t)
        {
            return vec2(centeredBlob,8.0);
        }
    }

    return vec2(t,mat);   
}

vec3 calcNormal(vec3 pos)
{
    float c = SDF(pos)[0];
    vec2 eps_zero = vec2(0.01, 0.0);
    return normalize(vec3( SDF(pos + eps_zero.xyy)[0], SDF(pos + eps_zero.yxy)[0], SDF(pos + eps_zero.yyx)[0] ) - c);
}

vec2 castRay(vec3 rayOrigin, vec3 rayDir)
{
    float t = 0.0;

    for (int i = 0; i < iterationAmount; i++)
    {
        vec2 res = SDF(rayOrigin + rayDir * t);
        if (res[0] < (0.0001*t))
        {
            return vec2(t,res[1]);
        }
        t += res[0];
    }

    return vec2(-1.0,-1.0);
}

vec3 bounceRender(vec3 rayOrigin, vec3 rayDir, vec2 uv)
{
    vec3 col;
    vec3 L=normalize(vec3(1.0,0.2,-2.0));

    vec2 rayHit = castRay(rayOrigin, rayDir);
    float t=rayHit[0];

    if (t!=-1.0)
    {
    	vec3 pHit=rayOrigin + rayDir * t;
        vec3 N=calcNormal(pHit);
        float NoL = max(dot(N, L), 0.0);
        float mat=rayHit[1];
        if (mat==0.0)
        {
            col=vec3(NoL);
        }
        else if (mat==1.0) // plane
        {
            float lDist=distance(pHit,L+rayOrigin);
        	vec2 a = vec2(1.)*smoothstep(-0.05, 0.05, mod(pHit.x, 1.))*smoothstep(-0.05, 0.05, mod(pHit.z, 1.));
        	col = vec3(1.0-a.x,1.0-a.y,1.0-a.x);

            //col+=vec3(pow(clamp(1.0-lDist/6.0,0.0,1.0),1.0));
        }

        else if (mat==2.0) // spheres
        {
            NoL+=pow(NoL,32.0);
            col+=vec3(.2,.3,.5)*vec3(NoL);
        }
        else if (mat==3.0)
        {
            NoL+=pow(NoL,32.0);
            col+=vec3(1.0,0.0,0.0)*vec3(NoL);
        }
        else if (mat==4.0)
        {
            NoL+=pow(NoL,32.0);
            col+=vec3(1.0,1.0,0.0)*vec3(NoL);
        }

        col/=distance(pHit-rayOrigin,vec3(0.))*.3;
    }

    col=pow(col,vec3(0.58));
    return col;
}

vec4 render(vec3 rayOrigin, vec3 rayDir, vec2 uv)
{
    vec3 col=vec3(0.);
    vec3 L=normalize(vec3(1.0,0.2,-2.0));

    vec2 rayHit = castRay(rayOrigin, rayDir);
    float t=rayHit[0];

    if (t!=-1.0)
    {
    	vec3 pHit=rayOrigin + rayDir * t;
        float mat=rayHit[1];
        vec3 N=calcNormal(rayOrigin + rayDir * t);
        float NoL = max(dot(N, L), 0.0);

        if (mat==0.0)
        {
            col=vec3(NoL);
        }
        else if (mat==1.0) // plane
        {
            float lDist=distance(pHit,L+rayOrigin);
        	vec2 a = vec2(1.)*smoothstep(-0.05, 0.05, mod(pHit.x, 1.))*smoothstep(-0.05, 0.05, mod(pHit.z, 1.));
        	col = vec3(1.0-a.x,1.0-a.y,1.0-a.x);

            col+=vec3(pow(clamp(1.0-lDist/6.0,0.0,1.0),2.2));
        }
        else if ((mat==2.0)||(mat==6.0)||(mat==7.0)) // meatballs/roaming spheres
        {
            float modTime=mod(iTime,30.0);    
    		if ((modTime<10.0)&&(mat==6.0))
            {
                return vec4(0.,0.,0.,0.);
            }

            NoL=pow(NoL,32.0);
            NoL=clamp(NoL,0.0,1.0);
            vec3 colReflect=bounceRender(pHit,N,uv);
            if (mat==2.0) col=vec3(1.0,1.0,1.0)*vec3(NoL);
            else if (mat==7.0) col=vec3(1.,1.,1.)*vec3(NoL);
            else col=vec3(.2,.2,.85)*vec3(NoL);
            //col=vec3(0.5,0.5,0.5)*vec3(NoL);
            col=mix(col,colReflect,0.2);
        }
        else if (mat==3.0) // pyr1
        {
            NoL+=pow(NoL,32.0);
            col+=vec3(1.0,0.0,0.0)*vec3(NoL);
        }
        else if (mat==4.0) // pyr2
        {
            NoL+=pow(NoL,32.0);
            col+=vec3(1.0,1.0,0.0)*vec3(NoL);
        }
        else if (mat==5.0) // magic mirror
        {
            vec3 N=vec3(0.0,0.0,-1.0);
            vec3 colReflect=bounceRender(pHit,N,uv);
            col=colReflect*0.5;
        }
        else if (mat==8.0) // mysterious blob
        {
            vec3 centerVec=vec3(5.0,0.0,1.0+iTime);
            vec3 edgeVec=pHit-centerVec;

            edgeVec=normalize(edgeVec);
            float edge=1.0-dot(edgeVec,vec3(0.,0.,-1.0));
            edge=pow(edge,3.0);

            vec3 color1 = vec3(0.0,0.0,0.0);
            vec3 color2 = vec3(0.726,0.726,0.726);
            col = mix(color1,color2,edge);
           	col+=edge*pow(distance(pHit,centerVec)*.4,2.0)*vec3(0.38,0.38,0.64);
            //col=mix(col,color3,min(0.,edge-0.6)*2.0);
        }

    	float modTime=mod(iTime,patternLength*numPatterns);    
        if (modTime>=patternLength*3.0)        
        {
            if (mat!=8.0)
            {
                vec3 nrd=normalize(rayDir);
                float fft = texture( iChannel0, vec2(0.0,0.0) ).x;
                float myTime=iTime/8.0;
                vec2 b=abs(fract(vec2(1.)*nrd.z+myTime)-.5)*abs(fract(vec2(1.)*nrd.z+myTime)-.5);
                col+=vec3(
                    (.01*pow(b.x+.8,20.)+b.y/dot(nrd.xy,nrd.xy)*exp(.24-1./4.))*fft,
                    (.01*pow(b.x+.2,20.)+b.y/dot(nrd.xy,nrd.xy)*exp(.1-1./4.))*fft,
                    (.01*pow(b.x+.1,20.)+b.y/dot(nrd.xy,nrd.xy)*exp(.32-1./2.))*fft)/3.0;
            }
        }

        col/=distance(pHit-rayOrigin,vec3(0.))*.6;
    }

    col=pow(col,vec3(0.58));
    return vec4(col,1.0);
}

vec3 getCameraRayDir(vec2 uv, vec3 camPos, vec3 camTarget)
{
    vec3 camForward = normalize(camTarget - camPos);
    vec3 upz=vec3(0.,1.,0.);
    vec3 camRight = normalize(cross(upz, camForward));
    vec3 camUp = normalize(cross(camForward, camRight));

    float fPersp = 2.0;
    vec3 vDir = normalize(uv.x * camRight + uv.y * camUp + camForward * fPersp);

    return vDir;
}

vec2 normalizeScreenCoords(vec2 screenCoord)
{
    vec2 result = 2.0 * (screenCoord/iResolution.xy - 0.5);
    result.x *= iResolution.x/iResolution.y;
    return result;
}

//

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float myTime=iTime/8.0;
	vec2 uv = normalizeScreenCoords(fragCoord);

    vec3 camPos,camTarget;

    float modTime=mod(iTime,patternLength*numPatterns);

    if (modTime<patternLength) // magic mirror
    {
        modTime/=8.0;
        camPos = vec3(0.0,0.,-3.0+7.5-modTime);
        camTarget = vec3(.0, 0.,0.0+7.5-modTime);
    }
    else if ((modTime>=patternLength)&&(modTime<(patternLength*2.0))) // roaming camera
    {
        if (iTime>(patternLength*4.0))
        {
            camPos = vec3(cos(myTime)*4.0,0.,-3.0+iTime);
            camTarget = vec3(.0, 0.,0.0+iTime);
        }
        else
        {
            camPos = vec3(sin(myTime)*4.0,0.,-3.0+iTime);
            camTarget = vec3(.0, 0.,0.0+iTime);
        }
    }
    else if ((modTime>=patternLength*2.0)&&(modTime<(patternLength*3.0))) // fisheye
    {
    	uv*=(asin(length(uv)*0.75));
        camPos = vec3(sin(myTime)*5.0,0.6*cos(myTime),-3.0+iTime);
        camTarget = vec3(.0, 0.,0.0+iTime);
    }
    else // glowsphere
    {
        camPos = vec3(5.0,0.0,-3.0+iTime);
        camTarget = vec3(5.0, 0.,0.0+iTime);
    }




    vec3 rayDir = getCameraRayDir(uv, camPos, camTarget);   

    float fft = texture( iChannel0, vec2(0.0,0.0) ).x;

    vec4 finalCol = vec4(render(camPos, rayDir,uv).xyz*(1.2+fft*2.0),1.0);

    vec4 distCol=finalCol;
    float pixx=mod(abs(uv.x*64.0),1.0);
    if (pixx<0.33) distCol.x/=2.0;
    if ((pixx>=0.33)&&(pixx<0.66)) distCol.y/=2.0;
    if (pixx>0.66) distCol.z/=2.0;

    if ((modTime>=patternLength*2.0)&&(modTime<(patternLength*3.0)))
    {
        float mult=4.0;
        distCol+=sin(uv.x*mult)*cos(uv.y*mult);
    }

   	finalCol=mix(distCol,finalCol,0.5);

    if (iTime>(patternLength*4.0))
    {
        vec3 value = finalCol.rgb;
        vec3 oldcolor = value + (value * texture(iChannel1, (mod(fragCoord, 8.0) / 8.0)).rrr);
        finalCol = vec4(floor(oldcolor),1.0);    
    }

    fragColor=finalCol;
}

