    //z-curve indecies
    int zcurve0 = xy2z(val0.xy);
    int zcurve1 = xy2z(val1.xy);

    vec2 cmin = (zcurve0<zcurve1) ? vec2(p0_id, zcurve0): vec2(p1_id, zcurve1);
    vec2 cmax = (zcurve0<zcurve1) ? vec2(p1_id, zcurve1): vec2(p0_id, zcurve0);

    return (zcurve0==zcurve1)?vec2(p0_id, zcurve0):((pid > id) ? cmin : cmax); //swap sorted
}

