vec2 qgHit, qBlk;
float dstFar, boxSize, boxSizeS, blkSize, bGrid, todCur;
int idObj, idBlk;
const float pi = 3.14159;

float BlkHit (vec3 ro, vec3 rd)
{
  mat3 m;
  vec3 rm, rdm, v, tm, tp, u;
  float dMin, dn, df;
  dMin = dstFar;
  for (int n = 0; n < nBlk; n ++) {
    rm = GetRB (n);
    m = QtToRMat (GetQB (n));
    rdm = rd * m;
    v = ((ro - rm) * m) / rdm;
    tp = blkSize / abs (rdm) - v;
    tm = - tp - 2. * v;
    dn = Maxv3 (tm);
    df = Minv3 (tp);
    if (df > 0. && dn < min (df, dMin)) {
      dMin = dn;
      fcBlk = - sign (rdm) * step (tm.zxy, tm) * step (tm.yzx, tm);
      idBlk = n;
      u = (v + dn) * rdm;
    }
  }
  if (dMin < dstFar) {
    qBlk = vec2 (dot (u.zxy, fcBlk), dot (u.yzx, fcBlk));
    vnBlk = QtToRMat (GetQB (idBlk)) * fcBlk;
  }
  return dMin;
}

float BlkHitSh (vec3 ro, vec3 rd, float rng)
{
  mat3 m;
  vec3 rm, rdm, v, tm, tp;
  float dMin, dn, df;
  dMin = dstFar;
  for (int n = 0; n < nBlk; n ++) {
    rm = GetRB (n);
    m = QtToRMat (GetQB (n));
    rdm = rd * m;
    v = ((ro - rm) * m) / rdm;
    tp = blkSize / abs (rdm) - v;
    tm = - tp - 2. * v;
    dn = Maxv3 (tm);
    df = Minv3 (tp);
    if (df > 0. && dn < min (df, dMin)) dMin = dn;
  }
  return smoothstep (0.3 * rng, rng, dMin);
}

#define DMIN(id) if (d < dMin) { dMin = d;  idObj = id; }

float ObjDf (vec3 p)
{
  vec4 sLen, rsLen;
  vec3 eWid;
  float dMin, d;
  dMin = dstFar;
  sLen = vec4 (vec3 (boxSizeS), 0.);
  rsLen = vec4 (sLen.xyz * rdSign, 0.);
  eWid = vec3 (0.1);
  d = min (min (
     PrBoxDf (p - rsLen.xww, eWid + sLen.wyz),
     PrBoxDf (p - rsLen.wyw, eWid + sLen.xwz)),
     PrBoxDf (p - rsLen.wwz, eWid + sLen.xyw));
