  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = 0; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float SheetHit (vec3 ro, vec3 rd)
{
  vec3 r, q;
  vec2 g;
  float d, dMin, sz, szMax, szMin;
  bvec2 ilt;
  bool bkTrak;
  const int nsMax = 3000;
  dMin = dstFar;
  szMax = bGrid;
  szMin = 1./16.;
  g = vec2 (0.);
  bkTrak = false;
  sz = szMax;
  for (int ns = 0; ns < nsMax; ns ++) {
    if (sz == szMin) {
      r = GetR (g) - ro;
      q = cross (r, rd);
      if (dot (q, q) < sz * sz) {
        d = dot (r, rd);
        if (d < dMin) {
          dMin = d;
          qgHit = g;
        }
      }
      bkTrak = true;
    }
    q = cross (GetR (g + 0.5 * sz) - ro, rd);
    bkTrak = bkTrak || (dot (q, q) > sz * sz);
    if (bkTrak) {
      bkTrak = false;
      ilt = lessThan (fract (g / (2. * sz)), vec2 (0.5));
      g.x += ilt.x ? sz : - sz;
      if (! ilt.x) {
        g.y += ilt.y ? sz : - sz;
        if (! ilt.y) {
          sz *= 2.;
          if (sz == szMax) break;
          bkTrak = true;
        }
      }
    } else if (sz > szMin) sz *= 0.5;
  }
  return dMin;
}

vec3 SheetNf () 
{
  vec2 e;
  e = vec2 (1., 0.);
  return normalize (cross (GetRC (qgHit + e.yx) - GetRC (qgHit - e.yx),
     GetRC (qgHit + e.xy) - GetRC (qgHit - e.xy)));
}

vec3 SheetCol (float s)
{
  vec3 col;
  vec2 b, g, c;
  float rad, a, f, gRot;
  col = vec3 (0.9, 0.9, 1.);
  c = vec2 (1., 0.4);
  rad = 0.5 * float (nBallE - 1);
  b = (qgHit / rad - 1.);
  b = Rot2D (b, pi);
  if (s > 0.) b.y *= -1.;
  a = atan (b.y, - b.x) / pi;
  if (abs (mod (6. * (a + 1.) + 0.5, 1.) - 0.5) < 0.03 &&
     abs (length (b) - 0.95) < 0.05 || abs (6. * a) < 0.03 &&
     abs (length (b) - 0.9) < 0.1) col = c.yyx;
  col = mix (c.yyx, col, 
     0.8 + 0.2 * SmoothBump (0.15, 0.85, 0.02, mod (rad * length (b), 1.)));
  col = mix (col, c.yyx, smoothstep (0.96, 0.97, length (b)));
  gRot = todCur * 2. * pi / (12. * 3600.);
  g = Rot2D (b, gRot - pi);
  g.x -= 0.25;
  f = length (max (abs (g) - vec2 (0.3, 0.027 * (0.6 - 0.4 * g.x / 0.3)), 0.));
  col = mix (col, c.xxy * (1. - 0.4 * step (0.01, f)), step (f, 0.02));
  gRot *= 12.;
  g = Rot2D (b, gRot - pi);
  g.x -= 0.35;
  f = length (max (abs (g) - vec2 (0.4, 0.013 * (0.6 - 0.4 * g.x / 0.4)), 0.));
  col = mix (col, c.yxy * (1. - 0.4 * step (0.01, f)), step (f, 0.02));
  gRot *= 60.;
  g = Rot2D (b, gRot - pi);
  g.x -= 0.4;
  f = length (max (abs (g) - vec2 (0.46, 0.007 * (0.6 - 0.4 * g.x / 0.46)), 0.));
  col = mix (col, c.xyy * (1. - 0.4 * step (0.01, f)), step (f, 0.02));
  col = mix (vec3 (0.1, 0.1, 0.2), col, smoothstep (0.007, 0.013, length (b)));
  return col;
}

float BoxHit (vec3 ro, vec3 rd, vec3 sz)
{
  vec3 v, tm, tp;
  float dMin, dn, df;
  dMin = dstFar;
  v = ro / rd;
  tp = sz / abs (rd) - v;
  tm = - tp - 2. * v;
  dn = Maxv3 (tm);
  df = Minv3 (tp);
  if (df > 0. && dn < df) {
    dMin = dn;
    vnBox = - sign (rd) * step (tm.zxy, tm) * step (tm.yzx, tm);
  }
  return dMin;
}

float BoxHitSh (vec3 ro, vec3 rd, vec3 sz, float rng)
{
  vec3 v, tm, tp;
  float dn, df;
  v = ro / rd;
  tp = sz / abs (rd) - v;
  tm = - tp - 2. * v;
  dn = Maxv3 (tm);
  df = Minv3 (tp);
  return smoothstep (0.3 * rng, rng, (df > 0. && dn < df) ? dn : dstFar);
}

float BoxHitSil (vec3 ro, vec3 rd, vec3 sz)
{
  vec3 v, tm, tp;
  float dn, df;
  v = ro / rd;
  tp = sz / abs (rd) - v;
  tm = - tp - 2. * v;
  dn = Maxv3 (tm);
  df = Minv3 (tp);
  return (df > 0. && dn < df) ? dn : dstFar;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 col, vn, w;
  float dstBlk, dstBox, dstSheet, s, sh, nDotL;
  bool isWall, isBlk;
  isWall = false;
  isBlk = false;
  dstBlk = BlkHit (ro, rd);
  dstSheet = SheetHit (ro, rd);
  rdSign = sign (rd);
  dstBox = ObjRay (ro, rd);
  if (min (min (dstBlk, dstBox), dstSheet) < dstFar) {
    if (dstSheet < min (dstBlk, dstBox)) {
      ro += dstSheet * rd;
      vn = SheetNf ();
      s = sign (dot (rd, vn));
      if (s > 0.) vn = - vn;
      col4 = vec4 (SheetCol (s), 0.1);
    } else if (dstBlk < dstBox) {
      ro += dstBlk * rd;
      vn = vnBlk;
      col4 = (idBlk < nBlk / 2) ? vec4 (1., 0.7, 0.3, 0.2) : vec4 (0.7, 1., 0.3, 0.2);
      s = -1.;
      if (fcBlk.x == -1. || fcBlk.y == 1.) qBlk = Rot2D (qBlk, 0.25 * pi);
      if (abs (fcBlk.x) == 1.) s = (fcBlk.x > 0.) ? length (qBlk) :
         length (abs (qBlk) - vec2 (0.35 * blkSize, 0.));
      else if (abs (fcBlk.y) == 1.) s = (fcBlk.y > 0.) ?
         length (vec2 (mod (qBlk.x + 0.4 * blkSize, 0.8 * blkSize) - 0.4 * blkSize, qBlk.y)) :
         length (abs (qBlk) - 0.4 * blkSize);
      else if (abs (fcBlk.z) == 1.) s = (fcBlk.z > 0.) ?
         min (length (abs (qBlk) - 0.4 * blkSize), length (qBlk)) :
         length (vec2 (mod (qBlk.x + 0.3 * blkSize, 0.6 * blkSize) - 0.3 * blkSize,
         abs (qBlk.y) - 0.4 * blkSize));
      s -= 0.2 * blkSize;
      if (s < 0.) col4 = vec4 (vec3 (1.2) * (1. - 0.7 * smoothstep (-0.1, 0., s)), 0.5);
      isBlk = true;
    } else if (dstBox < dstFar) {
      ro += dstBox * rd;
      vn = ObjNf (ro);
      if (idObj == 1) {
        w = smoothstep (0., 0.1, abs (fract (5. * ro / boxSize + 0.5) - 0.5));
        col4 = vec4 (vec3 (mix (vec3 (0.6, 0.55, 0.55), vec3 (0.5, 0.5, 0.55), 
           dot (abs (vn) * w.yzx * w.zxy, vec3 (1.)))), 0.1);
      } else if (idObj == 2) col4 = vec4 (vec3 (0.6), 0.1);
      isWall = true;
    }
    sh = (isWall || isBlk && (idBlk < nBlk / 2 && ltDir.z > 0. || 
       idBlk >= nBlk / 2 && ltDir.z < 0.)) ?
       BoxHitSh (ro + 0.01 * ltDir, ltDir, vec3 (boxSizeS, boxSizeS, 0.1), 3. * boxSize) : 1.;
    sh = 0.6 + 0.4 * min (sh, BlkHitSh (ro + 0.01 * ltDir, ltDir, boxSize));
    nDotL = max (dot (vn, ltDir), 0.);
    if (! isWall) nDotL *= nDotL;
    col = col4.rgb * (0.2 + 0.2 * max (- dot (vn, ltDir), 0.) + 0.8 * sh * nDotL) +
       col4.a * step (0.95, sh) * pow (max (0., dot (ltDir, reflect (rd, vn))), 32.);
    if (BoxHit (ro, rd, vec3 (boxSizeS)) < dstFar) col = mix (col,
       vec3 (0.7, 0.7, 0.75) * (0.2 + 0.8 * max (dot (vnBox, ltDir), 0.)) +
       0.1 * pow (max (0., dot (ltDir, reflect (rd, vnBox))), 64.),
       pow (1. - abs (dot (rd, vnBox)), 4.));
  } else col = vec3 (0.05);
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 qtVu, stDat, mPtr;
  vec3 col, rd, ro;
  vec2 canvas, uv;
  float vuEl, zmFac, asp, sr;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  todCur = iDate.w;
  bGrid = float (nBallE - 1);
  stDat = Loadv4 (vec2 (4 * nBlk + 0, nBallE));
  boxSize = stDat.y;
  boxSizeS = boxSize - 1.;
  dstFar = 11. * boxSize;
  blkSize = 1.3;
  qtVu = Loadv4 (vec2 (4 * nBlk + 1, nBallE));
  vuMat = QtToRMat (qtVu);
  vuEl = 0.1 * pi;
  ro = vec3 (0., 0., -6. * boxSize);
  ro.yz = Rot2D (ro.yz, vuEl);
  ro = ro * vuMat;
  ltDir = normalize (vec3 (-1., 1., -1.)) * vuMat;
  zmFac = 4.;
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  col = vec3 (0.);
  for (float a = 0.; a < naa; a ++) {
    rd = normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), zmFac));
    rd.yz = Rot2D (rd.yz, vuEl);
    rd = rd * vuMat;
    if (BoxHitSil (ro, rd, vec3 (boxSizeS)) < dstFar) col += (1. / naa) * ShowScene (ro, rd);
  }
  if (length (col) == 0.) col = vec3 (0.1, 0.1, 0.) * (1. - 0.8 * smoothstep (1.1, 1.3, length (uv)));
  mPtr = Loadv4 (vec2 (4 * nBlk + 2, nBallE));
  asp = canvas.x / canvas.y;
  if (mPtr.z > 0. && min (uv.x - asp, uv.y - 1.) > -0.1) col = mix (col, vec3 (1., 1., 0.), 0.3);
  fragColor = vec4 (col, 1.);
}

float PrBoxDf (vec3 p, vec3 b)
{
  vec3 d;
  d = abs (p) - b;
  return min (max (d.x, max (d.y, d.z)), 0.) + length (max (d, 0.));
}

mat3 QtToRMat (vec4 q) 
{
  mat3 m;
  float a1, a2, s;
  s = q.w * q.w - 0.5;
  m[0][0] = q.x * q.x + s;  m[1][1] = q.y * q.y + s;  m[2][2] = q.z * q.z + s;
  a1 = q.x * q.y;  a2 = q.z * q.w;  m[0][1] = a1 + a2;  m[1][0] = a1 - a2;
  a1 = q.x * q.z;  a2 = q.y * q.w;  m[2][0] = a1 + a2;  m[0][2] = a1 - a2;
  a1 = q.y * q.z;  a2 = q.x * q.w;  m[1][2] = a1 + a2;  m[2][1] = a1 - a2;
  return 2. * m;
}

float Minv3 (vec3 p)
{
  return min (p.x, min (p.y, p.z));
}

float Maxv3 (vec3 p)
{
  return max (p.x, max (p.y, p.z));
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec3 GetR (vec2 v)
{
  return Loadv4 (v).xyz;
}

vec3 GetRC (vec2 v)
{
  return GetR (clamp (v, 0., bGrid - 1.));
}

vec3 GetRB (int n)
{
  return Loadv4 (vec2 (4 * n + 0, nBallE)).xyz;
}

vec4 GetQB (int n)
{
  return Loadv4 (vec2 (4 * n + 2, nBallE));
}

#define txBuf iChannel0
#define txSize iChannelResolution[0].xy

vec4 Loadv4 (vec2 vId)
{
  return texture (txBuf, (vId + 0.5) / txSize);
}

buffer_a_code:
// "Floppy Clock With Dice" by dr2 - 2020
// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

vec4 QtMul (vec4 q1, vec4 q2);
vec4 RMatToQt (mat3 m);
mat3 QtToRMat (vec4 q);
vec4 EulToQt (vec3 e);
vec3 QtToEul (vec4 q);
mat3 LpStepMat (vec3 a);
float Hashff (float p);
vec3 GetR (vec2 v);
vec3 GetV (vec2 v);
vec3 GetRB (int n);
vec3 GetVB (int n);
vec4 GetQB (int n);
vec3 GetWB (int n);
vec4 Loadv4 (vec2 vId);
void Savev4 (vec2 vId, vec4 val, inout vec4 fCol, vec2 fCoord);

const int nBallE = 33, nBlk = 8;
const vec3 blkSph = vec3 (3.), blkGap = vec3 (0.9);
const int nSiteBlk = 27;

vec3 gravVec;
ivec2 idNeb[4], idNebD[4];
float boxSize, spLen, bMass, farSep, fOvlap, fricN, fricT, fricS, fvDamp;
const float pi = 3.14159;

void IdNebs ()
{
  idNeb[0] = ivec2 (1, 0);
  idNeb[1] = - idNeb[0];
  idNeb[2] = ivec2 (0, 1);
  idNeb[3] = - idNeb[2];
  idNebD[0] = ivec2 (1, 1);
  idNebD[1] = - idNebD[0];
  idNebD[2] = ivec2 (1, -1);
  idNebD[3] = - idNebD[2];
}

#define InLatt(t) (t >= 0 && t < nBallE)

void ShSpringForce (ivec2 iv, vec3 r, vec3 v, inout vec3 a)
{
  vec3 dr;
  ivec2 ivn;
  float spLenD, fSpring, fDamp;
  fSpring = 200.;
  fDamp = 0.05;
  for (int n = 0; n < 4; n ++) {
    ivn = iv + idNeb[n];
    if (InLatt (ivn.x) && InLatt (ivn.y)) {
      dr = r - GetR (vec2 (ivn));
      a += fSpring * (spLen / length (dr) - 1.) * dr - fDamp * (v - GetV (vec2 (ivn)));
    }
  }
  spLenD = spLen * sqrt (2.);
  for (int n = 0; n < 4; n ++) {
    ivn = iv + idNebD[n];
    if (InLatt (ivn.x) && InLatt (ivn.y)) {
      dr = r - GetR (vec2 (ivn));
      a += 5. * fSpring * (spLenD / length (dr) - 1.) * dr - fDamp * (v - GetV (vec2 (ivn)));
    }
  }
}

#define InLatt2(t, k) (k == 0 && t > 1 || k == 2 && t < nBallE - 2 || k == 1 && (t > 0 && t < nBallE - 1))

void ShBendForce (ivec2 iv, vec3 r, inout vec3 a)
{
  vec3 dr1, dr2, rt;
  ivec2 ivd;
  float s, c11, c22, c12, cd, fBend;
  fBend = 500.;
  for (int nd = 0; nd < 2; nd ++) {
    ivd = (nd == 0) ? ivec2 (1, 0) : ivec2 (0, 1);
    for (int k = 0; k < 3; k ++) {
      if (nd == 0 && InLatt2 (iv.x, k) || nd == 1 && InLatt2 (iv.y, k)) {
        if (k == 0) {
          rt = GetR (vec2 (iv - ivd));
          dr1 = rt - GetR (vec2 (iv - 2 * ivd));
          dr2 = r - rt;
          s = -1.;
        } else if (k == 2) {
          rt = GetR (vec2 (iv + ivd));
          dr1 = rt - r;
          dr2 = GetR (vec2 (iv + 2 * ivd)) - rt;
          s = -1.;
        } else {
          dr1 = r - GetR (vec2 (iv - ivd));
          dr2 = GetR (vec2 (iv + ivd)) - r;
          s = 1.;
        }
        c11 = 1. / dot (dr1, dr1);
        c12 = dot (dr1, dr2);
        c22 = 1. / dot (dr2, dr2);
        cd = sqrt (c11 * c22);
        s *= fBend * cd * (c12 * cd - 1.);
        if (k <= 1) a += s * (dr1 - c12 * c22 * dr2);
        if (k >= 1) a += s * (c12 * c11 * dr1 - dr2);
      }
    }
  }
}

vec4 RSite (int sId)
{
  vec3 s;
  s = floor (mod (vec3 (sId), blkSph.x * vec3 (1., blkSph.y, blkSph.y * blkSph.z)) /
     vec3 (1., blkSph.x, blkSph.x * blkSph.y)) - 0.5 * (blkSph - 1.);
  return vec4 (blkGap * s, dot (step (0.45 * (blkSph - 1.), abs (s)), vec3 (1.)));
}

void ShBForce (vec3 r, inout vec3 a)
{
  mat3 mRot;
  vec4 rts;
  vec3 rm, dr;
  float rSep;
  for (int n = 0; n < nBlk; n ++) {
    rm = GetRB (n);
    mRot = QtToRMat (GetQB (n));
    for (int j = 0; j < nSiteBlk; j ++) {
      rts = RSite (j);
      if (rts.w >= 2.) {
        dr = r - (rm + mRot * rts.xyz);
        rSep = length (dr);
        if (rSep < 1.) a += fOvlap * (1. / rSep - 1.) * dr;
      }
    }
  }
}

vec3 FcFun (vec3 dr, vec3 dv)
{
  vec3 f;
  float rSep, vRel, fo, drv;
  f = vec3 (0.);
  rSep = length (dr);
  if (rSep < 1.) {
    fo = fOvlap * (1. / rSep - 1.);
    drv = dot (dr, dv) / (rSep * rSep);
    dv -= drv * dr;
    vRel = length (dv);
    fo = max (fo - fricN * drv, 0.);
    f = fo * dr;
    if (vRel > 0.001) f -= min (fricT, fricS * abs (fo) * rSep / vRel) * dv;
  }
  return f;
}

void BBPairForce (vec3 rm, vec3 vm, mat3 mRot, vec3 wm, inout vec3 am, inout vec3 wam)
{
  mat3 mRotN;
  vec4 rts, rtsN;
  vec3 rmN, vmN, wmN, dr, dv, f;
  float bSep;
  for (int n = 0; n < nBlk; n ++) {
    rmN = GetRB (n);
    bSep = length (rm - rmN);
    if (bSep > 0.01 && bSep < farSep) {
      vmN = GetVB (n);
      mRotN = QtToRMat (GetQB (n));
      wmN = GetWB (n);
      for (int j = 0; j < nSiteBlk; j ++) {
        rts = RSite (j);
        if (rts.w >= 1.) {
          rts.xyz = mRot * rts.xyz;
          dv = vm + cross (wm, rts.xyz) - vmN;
          f = vec3 (0.);
          for (int jN = 0; jN < nSiteBlk; jN ++) {
            rtsN = RSite (jN);
            if (max (rts.w, rtsN.w) >= 2.) {
              rtsN.xyz = mRotN * rtsN.xyz;
              dr = rm + rts.xyz - (rmN + rtsN.xyz);
              f += FcFun (dr, dv - cross (wmN, rtsN.xyz));
            }
          }
        }
        am += f;
        wam += cross (rts.xyz, f);
      }
    }
  }
}

void BWallForce (vec3 rm, vec3 vm, mat3 mRot, vec3 wm, inout vec3 am, inout vec3 wam)
{
  vec4 rts, drw;
  vec3 r, dr, dv, f;
  for (int j = 0; j < nSiteBlk; j ++) {
    rts = RSite (j);
    if (rts.w >= 2.) {
      rts.xyz = mRot * rts.xyz;
      r = rm + rts.xyz;
      dv = vm + cross (wm, rts.xyz);
      f = vec3 (0.);
      drw = vec4 ((boxSize - 1. - abs (r)) * (1. - 2. * step (0., r)), 0.);
      for (int nf = 0; nf < 3; nf ++) {
        dr = (nf == 0) ? drw.xww : ((nf == 1) ? drw.wyw : drw.wwz);
        f += FcFun (dr, dv);
      }
      am += f;
      wam += cross (rts.xyz, f);
    }
  }
}

void BShForce (vec3 rm, vec3 vm, mat3 mRot, vec3 wm, inout vec3 am, inout vec3 wam)
{
  vec4 rts;
  vec3 dr, dv, f;
  vec2 g;
  for (int j = 0; j < nSiteBlk; j ++) {
    rts = RSite (j);
    if (rts.w >= 2.) {
      rts.xyz = mRot * rts.xyz;
      f = vec3 (0.);
      g = vec2 (0.);
      for (int n = 0; n < nBallE * nBallE; n ++) {
        dr = rm + rts.xyz - GetR (g);
        dv = vm + cross (wm, rts.xyz) - GetV (g);
        f += FcFun (dr, dv);
        if (++ g.x == float (nBallE)) {
          g.x = 0.;
          ++ g.y;
        }  
      }
      am += f;
      wam += cross (rts.xyz, f);
    }
  }
}

void Step (ivec2 iv, out vec3 rm, out vec3 vm)
{
  vec3 am;
  float dt;
  IdNebs ();
  rm = GetR (vec2 (iv));
  vm = GetV (vec2 (iv));
  am = vec3 (0.);
  if (iv.x > 0 && iv.x < nBallE - 1 && iv.y > 0 && iv.y < nBallE - 1) {
    ShSpringForce (iv, rm, vm, am);
    ShBendForce (iv, rm, am);
    ShBForce (rm, am);
    am -= fvDamp * vm;
    dt = 0.02;
    vm += dt * am;
    rm += dt * vm;
  }
}

void StepB (int mId, out vec3 rm, out vec3 vm, out vec4 qm, out vec3 wm)
{
  mat3 mRot;
  vec3 am, wam, dSp;
  float dt;
  rm = GetRB (mId);
  vm = GetVB (mId);
  qm = GetQB (mId);
  wm = GetWB (mId);
  mRot = QtToRMat (qm);
  am = vec3 (0.);
  wam = vec3 (0.);
  BBPairForce (rm, vm, mRot, wm, am, wam);
  BWallForce (rm, vm, mRot, wm, am, wam);
  BShForce (rm, vm, mRot, wm, am, wam);
  am -= fvDamp * vm;
  am /= bMass;
  am -= 2. * gravVec;
  dt = 0.02;
  vm += dt * am;
  rm += dt * vm;
  dSp = blkGap * blkSph;
  wam = mRot * (wam * mRot / (0.25 * (vec3 (dot (dSp, dSp)) - dSp * dSp) + 1.));
  wm += dt * wam / bMass;
  qm = normalize (QtMul (RMatToQt (LpStepMat (0.5 * dt * wm)), qm));
}

void OrientVu (inout vec4 qtVu, vec4 mPtr, inout vec4 mPtrP, bool init)
{
  vec3 vq1, vq2;
  vec2 dm;
  float mFac;
  if (! init) {
    qtVu = EulToQt (vec3 (0.1, 0.4, 0.3) * pi);
    mPtrP = vec4 (99., 0., -1., 0.);
  } else {
    if (mPtr.z > 0.) {
      if (mPtrP.x == 99.) mPtrP = mPtr;
      mFac = 1.5;
      dm = - mFac * mPtrP.xy;
      vq1 = vec3 (dm, sqrt (max (1. - dot (dm, dm), 0.)));
      dm = - mFac * mPtr.xy;
      vq2 = vec3 (dm, sqrt (max (1. - dot (dm, dm), 0.)));
      qtVu = normalize (QtMul (vec4 (cross (vq1, vq2), dot (vq1, vq2)), qtVu));
      mPtrP = mPtr;
    } else mPtrP = vec4 (99., 0., -1., 0.);
  }
}

void Init (ivec2 iv, out vec3 r, out vec3 v)
{
  for (int iy = 0; iy < nBallE; iy ++) {
    for (int ix = 0; ix < nBallE; ix ++) {
      if (iv.x == ix && iv.y == iy) {
        r = 0.97 * spLen * (vec3 (ix, iy, 0.) - vec3 (vec2 (0.5 * float (nBallE - 1)), 0.));
      }
    }
  }
  v = vec3 (0.);
}

void InitB (int mId, out vec3 r, out vec3 v, out vec4 qm, out vec3 wm)
{
  float mIdf;
  mIdf = float (mId);
  r = 0.4 * boxSize * (2. * vec3 (mod (mIdf, 2.), floor (mod (mIdf, 4.) / 2.),
     floor (mIdf / 4.)) - 1.);
  v = vec3 (0.);
  qm = EulToQt (normalize (vec3 (Hashff (mIdf), Hashff (mIdf + 0.3), Hashff (mIdf + 0.6))));
  wm = vec3 (0.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  vec4 mPtr, mPtrP, stDat, qtVu, qm;
  vec3 r, v, wm;
  vec2 canvas;
  ivec2 pxIv, iv;
  float tCur, nStep, asp;
  int mId, pxId, kp;
  bool doInit;
  pxIv = ivec2 (fragCoord);
  pxId = pxIv.x + 2 * nBallE * pxIv.y;
  if (pxIv.x >= 2 * nBallE || pxId > 2 * nBallE * nBallE + 4 * nBlk + 2) discard;
  canvas = iResolution.xy;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  asp = canvas.x / canvas.y;
  qtVu = Loadv4 (vec2 (4 * nBlk + 1, nBallE));
  mPtrP = Loadv4 (vec2 (4 * nBlk + 2, nBallE));
  iv = pxIv;
  if (iv.x >= nBallE) iv.x -= nBallE;
  mId = iv.y * nBallE + iv.x;
  if (iFrame <= 5) {
    doInit = true;
    spLen = 1.1;
    boxSize = 0.52 * spLen * float (nBallE - 1);
    nStep = 0.;
    OrientVu (qtVu, mPtr, mPtrP, false);
  } else {
    doInit = false;
    stDat = Loadv4 (vec2 (4 * nBlk + 0, nBallE));
    spLen = stDat.x;
    boxSize = stDat.y;
    nStep = stDat.w;
    ++ nStep;
    if (mPtrP.z < 0.) qtVu = EulToQt (QtToEul (qtVu) + 0.003 * pi * vec3 (0.41, 0., 0.27));
    OrientVu (qtVu, mPtr, mPtrP, true);
    gravVec = vec3 (0., 1., 0.) * QtToRMat (qtVu);
    if (nStep > 100. && mPtr.z > 0. && mPtr.x > 0.5 - 0.05 / asp &&
       mPtr.y > 0.5 - 0.05) doInit = true;
  }
  if (doInit) nStep = 0.;
  bMass = 8.;
  farSep = length (blkGap * (blkSph - 1.)) + 1.;
  fOvlap = 1000.;
  fricN = 1.;
  fricT = 1.;
  fricS = 2.;
  fvDamp = 0.03;
  if (mId < nBallE * nBallE) {
    if (doInit) Init (iv, r, v);
    else Step (iv, r, v);
    stDat = vec4 (((pxIv.x < nBallE) ? r : v), 0.);
  } else if (pxId >= 2 * nBallE * nBallE && pxId < 2 * nBallE * nBallE + 4 * nBlk) {
    mId = (pxId - 2 * nBallE * nBallE) / 4;
    if (doInit) InitB (mId, r, v, qm, wm);
    else StepB (mId, r, v, qm, wm);
    kp = pxId - 2 * nBallE * nBallE - 4 * mId;
    if      (kp == 0) stDat = vec4 (r, 0.);
    else if (kp == 1) stDat = vec4 (v, 0.);
    else if (kp == 2) stDat = qm;
    else if (kp == 3) stDat = vec4 (wm, 0.);
  } else {
    kp = pxId - 2 * nBallE * nBallE - 4 * nBlk;
    if      (kp == 0) stDat = stDat = vec4 (spLen, boxSize, tCur, nStep);
    else if (kp == 1) stDat = qtVu;
    else if (kp == 2) stDat = mPtrP;
  }
  Savev4 (vec2 (pxIv), stDat, fragColor, fragCoord);
}

vec4 QtMul (vec4 q1, vec4 q2)
{
  return vec4 (dot (vec4 (q1.w, - q1.z, q1.yx), q2), dot (vec4 (q1.zw, - q1.x, q1.y), q2),
     dot (vec4 (- q1.y, q1.xwz), q2), dot (vec4 (- q1.xyz, q1.w), q2));
}

mat3 QtToRMat (vec4 q) 
{
  mat3 m;
  float a1, a2, s;
  q = normalize (q);
  s = q.w * q.w - 0.5;
  m[0][0] = q.x * q.x + s;  m[1][1] = q.y * q.y + s;  m[2][2] = q.z * q.z + s;
  a1 = q.x * q.y;  a2 = q.z * q.w;  m[0][1] = a1 + a2;  m[1][0] = a1 - a2;
  a1 = q.x * q.z;  a2 = q.y * q.w;  m[2][0] = a1 + a2;  m[0][2] = a1 - a2;
  a1 = q.y * q.z;  a2 = q.x * q.w;  m[1][2] = a1 + a2;  m[2][1] = a1 - a2;
  return 2. * m;
}

vec4 RMatToQt (mat3 m)
{
  vec4 q;
  const float tol = 1e-6;
  q.w = 0.5 * sqrt (max (1. + m[0][0] + m[1][1] + m[2][2], 0.));
  if (abs (q.w) > tol) q.xyz =
     vec3 (m[1][2] - m[2][1], m[2][0] - m[0][2], m[0][1] - m[1][0]) / (4. * q.w);
  else {
    q.x = sqrt (max (0.5 * (1. + m[0][0]), 0.));
    if (abs (q.x) > tol) q.yz = vec2 (m[0][1], m[0][2]) / q.x;
    else {
      q.y = sqrt (max (0.5 * (1. + m[1][1]), 0.));
      if (abs (q.y) > tol) q.z = m[1][2] / q.y;
      else q.z = 1.;
    }
  }
  return normalize (q);
}

vec4 EulToQt (vec3 e)
{
  float a1, a2, a3, c1, s1;
  a1 = 0.5 * e.y;  a2 = 0.5 * (e.x - e.z);  a3 = 0.5 * (e.x + e.z);
  s1 = sin (a1);  c1 = cos (a1);
  return normalize (vec4 (s1 * cos (a2), s1 * sin (a2), c1 * sin (a3),
     c1 * cos (a3)));
}

vec3 QtToEul (vec4 q)
{
  vec3 e;
  float s, t;
  t = dot (q.xy, q.xy);
  s = 2. * sqrt (max (t * (1. - t), 0.));
  e.y = atan (s, 1. - 2. * t);
  if (s != 0.) e.xz = 
     vec2 (atan (q.x * q.z + q.y * q.w, q.x * q.w - q.y * q.z),
           atan (q.x * q.z - q.y * q.w, q.x * q.w + q.y * q.z));
  else e.xz = vec2 (2. * atan (q.z, q.w), 0.);
  return e;
}

mat3 LpStepMat (vec3 a)
{
  mat3 m1, m2;
  vec3 t, c, s;
  float b1, b2;
  t = 0.25 * a * a;
  c = (1. - t) / (1. + t);
  s = a / (1. + t);
  m1[0][0] = c.y * c.z;  m2[0][0] = c.y * c.z;
  b1 = s.x * s.y * c.z;  b2 = c.x * s.z;
  m1[0][1] = b1 + b2;  m2[1][0] = b1 - b2;
  b1 = c.x * s.y * c.z;  b2 = s.x * s.z;
  m1[0][2] = - b1 + b2;  m2[2][0] = b1 + b2;
  b1 = c.y * s.z;
  m1[1][0] = - b1;  m2[0][1] = b1;  
  b1 = s.x * s.y * s.z;  b2 = c.x * c.z;
  m1[1][1] = - b1 + b2;  m2[1][1] = b1 + b2; 
  b1 = c.x * s.y * s.z;  b2 = s.x * c.z;
  m1[1][2] = b1 + b2;  m2[2][1] = b1 - b2;
  m1[2][0] = s.y;  m2[0][2] = - s.y;
  b1 = s.x * c.y;
  m1[2][1] = - b1;  m2[1][2] = b1;
  b1 = c.x * c.y;
  m1[2][2] = b1;  m2[2][2] = b1;
  return m1 * m2;
}

const float cHashM = 43758.54;

float Hashff (float p)
{
  return fract (sin (p) * cHashM);
}

vec3 GetR (vec2 v)
{
  return Loadv4 (v).xyz;
}

vec3 GetV (vec2 v)
{
  v.x += float (nBallE);
  return Loadv4 (v).xyz;
}

vec3 GetRB (int n)
{
  return Loadv4 (vec2 (4 * n + 0, nBallE)).xyz;
}

vec3 GetVB (int n)
{
  return Loadv4 (vec2 (4 * n + 1, nBallE)).xyz;
}

vec4 GetQB (int n)
{
  return Loadv4 (vec2 (4 * n + 2, nBallE));
}

vec3 GetWB (int n)
{
  return Loadv4 (vec2 (4 * n + 3, nBallE)).xyz;
}

#define txBuf iChannel0
#define txSize iChannelResolution[0].xy

vec4 Loadv4 (vec2 vId)
{
  return texture (txBuf, (vId + 0.5) / txSize);
}

void Savev4 (vec2 vId, vec4 val, inout vec4 fCol, vec2 fCoord)
{
  vec2 d;
  d = abs (fCoord - vId - 0.5);
  if (max (d.x, d.y) < 0.5) fCol = val;
}

buffer_b_code:
// "Floppy Clock With Dice" by dr2 - 2020
// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

vec4 QtMul (vec4 q1, vec4 q2);
vec4 RMatToQt (mat3 m);
mat3 QtToRMat (vec4 q);
vec4 EulToQt (vec3 e);
vec3 QtToEul (vec4 q);
mat3 LpStepMat (vec3 a);
float Hashff (float p);
vec3 GetR (vec2 v);
vec3 GetV (vec2 v);
vec3 GetRB (int n);
vec3 GetVB (int n);
vec4 GetQB (int n);
vec3 GetWB (int n);
vec4 Loadv4 (vec2 vId);
void Savev4 (vec2 vId, vec4 val, inout vec4 fCol, vec2 fCoord);

const int nBallE = 33, nBlk = 8;
const vec3 blkSph = vec3 (3.), blkGap = vec3 (0.9);
const int nSiteBlk = 27;

vec3 gravVec;
ivec2 idNeb[4], idNebD[4];
float boxSize, spLen, bMass, farSep, fOvlap, fricN, fricT, fricS, fvDamp;
const float pi = 3.14159;

void IdNebs ()
{
  idNeb[0] = ivec2 (1, 0);
  idNeb[1] = - idNeb[0];
  idNeb[2] = ivec2 (0, 1);
  idNeb[3] = - idNeb[2];
  idNebD[0] = ivec2 (1, 1);
  idNebD[1] = - idNebD[0];
  idNebD[2] = ivec2 (1, -1);
  idNebD[3] = - idNebD[2];
}

#define InLatt(t) (t >= 0 && t < nBallE)

void ShSpringForce (ivec2 iv, vec3 r, vec3 v, inout vec3 a)
{
  vec3 dr;
  ivec2 ivn;
  float spLenD, fSpring, fDamp;
  fSpring = 200.;
  fDamp = 0.05;
  for (int n = 0; n < 4; n ++) {
    ivn = iv + idNeb[n];
    if (InLatt (ivn.x) && InLatt (ivn.y)) {
      dr = r - GetR (vec2 (ivn));
      a += fSpring * (spLen / length (dr) - 1.) * dr - fDamp * (v - GetV (vec2 (ivn)));
    }
  }
  spLenD = spLen * sqrt (2.);
  for (int n = 0; n < 4; n ++) {
    ivn = iv + idNebD[n];
    if (InLatt (ivn.x) && InLatt (ivn.y)) {
      dr = r - GetR (vec2 (ivn));
      a += 5. * fSpring * (spLenD / length (dr) - 1.) * dr - fDamp * (v - GetV (vec2 (ivn)));
    }
  }
}

#define InLatt2(t, k) (k == 0 && t > 1 || k == 2 && t < nBallE - 2 || k == 1 && (t > 0 && t < nBallE - 1))

void ShBendForce (ivec2 iv, vec3 r, inout vec3 a)
{
  vec3 dr1, dr2, rt;
  ivec2 ivd;
  float s, c11, c22, c12, cd, fBend;
  fBend = 500.;
  for (int nd = 0; nd < 2; nd ++) {
    ivd = (nd == 0) ? ivec2 (1, 0) : ivec2 (0, 1);
    for (int k = 0; k < 3; k ++) {
      if (nd == 0 && InLatt2 (iv.x, k) || nd == 1 && InLatt2 (iv.y, k)) {
        if (k == 0) {
          rt = GetR (vec2 (iv - ivd));
          dr1 = rt - GetR (vec2 (iv - 2 * ivd));
          dr2 = r - rt;
          s = -1.;
        } else if (k == 2) {
          rt = GetR (vec2 (iv + ivd));
          dr1 = rt - r;
          dr2 = GetR (vec2 (iv + 2 * ivd)) - rt;
          s = -1.;
        } else {
          dr1 = r - GetR (vec2 (iv - ivd));
          dr2 = GetR (vec2 (iv + ivd)) - r;
          s = 1.;
        }
        c11 = 1. / dot (dr1, dr1);
        c12 = dot (dr1, dr2);
        c22 = 1. / dot (dr2, dr2);
        cd = sqrt (c11 * c22);
        s *= fBend * cd * (c12 * cd - 1.);
        if (k <= 1) a += s * (dr1 - c12 * c22 * dr2);
        if (k >= 1) a += s * (c12 * c11 * dr1 - dr2);
      }
    }
  }
}

vec4 RSite (int sId)
{
  vec3 s;
  s = floor (mod (vec3 (sId), blkSph.x * vec3 (1., blkSph.y, blkSph.y * blkSph.z)) /
     vec3 (1., blkSph.x, blkSph.x * blkSph.y)) - 0.5 * (blkSph - 1.);
  return vec4 (blkGap * s, dot (step (0.45 * (blkSph - 1.), abs (s)), vec3 (1.)));
}

void ShBForce (vec3 r, inout vec3 a)
{
  mat3 mRot;
  vec4 rts;
  vec3 rm, dr;
  float rSep;
  for (int n = 0; n < nBlk; n ++) {
    rm = GetRB (n);
    mRot = QtToRMat (GetQB (n));
    for (int j = 0; j < nSiteBlk; j ++) {
      rts = RSite (j);
      if (rts.w >= 2.) {
        dr = r - (rm + mRot * rts.xyz);
        rSep = length (dr);
        if (rSep < 1.) a += fOvlap * (1. / rSep - 1.) * dr;
      }
    }
  }
}

vec3 FcFun (vec3 dr, vec3 dv)
{
  vec3 f;
  float rSep, vRel, fo, drv;
  f = vec3 (0.);
  rSep = length (dr);
  if (rSep < 1.) {
    fo = fOvlap * (1. / rSep - 1.);
    drv = dot (dr, dv) / (rSep * rSep);
    dv -= drv * dr;
    vRel = length (dv);
    fo = max (fo - fricN * drv, 0.);
    f = fo * dr;
    if (vRel > 0.001) f -= min (fricT, fricS * abs (fo) * rSep / vRel) * dv;
  }
  return f;
}

void BBPairForce (vec3 rm, vec3 vm, mat3 mRot, vec3 wm, inout vec3 am, inout vec3 wam)
{
  mat3 mRotN;
  vec4 rts, rtsN;
  vec3 rmN, vmN, wmN, dr, dv, f;
  float bSep;
  for (int n = 0; n < nBlk; n ++) {
    rmN = GetRB (n);
    bSep = length (rm - rmN);
    if (bSep > 0.01 && bSep < farSep) {
      vmN = GetVB (n);
      mRotN = QtToRMat (GetQB (n));
      wmN = GetWB (n);
      for (int j = 0; j < nSiteBlk; j ++) {
        rts = RSite (j);
        if (rts.w >= 1.) {
          rts.xyz = mRot * rts.xyz;
          dv = vm + cross (wm, rts.xyz) - vmN;
          f = vec3 (0.);
          for (int jN = 0; jN < nSiteBlk; jN ++) {
            rtsN = RSite (jN);
            if (max (rts.w, rtsN.w) >= 2.) {
              rtsN.xyz = mRotN * rtsN.xyz;
              dr = rm + rts.xyz - (rmN + rtsN.xyz);
              f += FcFun (dr, dv - cross (wmN, rtsN.xyz));
            }
          }
        }
        am += f;
        wam += cross (rts.xyz, f);
      }
    }
  }
}

void BWallForce (vec3 rm, vec3 vm, mat3 mRot, vec3 wm, inout vec3 am, inout vec3 wam)
{
  vec4 rts, drw;
  vec3 r, dr, dv, f;
  for (int j = 0; j < nSiteBlk; j ++) {
    rts = RSite (j);
    if (rts.w >= 2.) {
      rts.xyz = mRot * rts.xyz;
      r = rm + rts.xyz;
      dv = vm + cross (wm, rts.xyz);
      f = vec3 (0.);
      drw = vec4 ((boxSize - 1. - abs (r)) * (1. - 2. * step (0., r)), 0.);
      for (int nf = 0; nf < 3; nf ++) {
        dr = (nf == 0) ? drw.xww : ((nf == 1) ? drw.wyw : drw.wwz);
        f += FcFun (dr, dv);
      }
      am += f;
      wam += cross (rts.xyz, f);
    }
  }
}

void BShForce (vec3 rm, vec3 vm, mat3 mRot, vec3 wm, inout vec3 am, inout vec3 wam)
{
  vec4 rts;
  vec3 dr, dv, f;
  vec2 g;
  for (int j = 0; j < nSiteBlk; j ++) {
    rts = RSite (j);
    if (rts.w >= 2.) {
      rts.xyz = mRot * rts.xyz;
      f = vec3 (0.);
      g = vec2 (0.);
      for (int n = 0; n < nBallE * nBallE; n ++) {
        dr = rm + rts.xyz - GetR (g);
        dv = vm + cross (wm, rts.xyz) - GetV (g);
        f += FcFun (dr, dv);
        if (++ g.x == float (nBallE)) {
          g.x = 0.;
          ++ g.y;
        }  
      }
      am += f;
      wam += cross (rts.xyz, f);
    }
  }
}

void Step (ivec2 iv, out vec3 rm, out vec3 vm)
{
  vec3 am;
  float dt;
  IdNebs ();
  rm = GetR (vec2 (iv));
  vm = GetV (vec2 (iv));
  am = vec3 (0.);
  if (iv.x > 0 && iv.x < nBallE - 1 && iv.y > 0 && iv.y < nBallE - 1) {
    ShSpringForce (iv, rm, vm, am);
    ShBendForce (iv, rm, am);
    ShBForce (rm, am);
    am -= fvDamp * vm;
    dt = 0.02;
    vm += dt * am;
    rm += dt * vm;
  }
}

void StepB (int mId, out vec3 rm, out vec3 vm, out vec4 qm, out vec3 wm)
{
  mat3 mRot;
  vec3 am, wam, dSp;
  float dt;
  rm = GetRB (mId);
  vm = GetVB (mId);
  qm = GetQB (mId);
  wm = GetWB (mId);
  mRot = QtToRMat (qm);
  am = vec3 (0.);
  wam = vec3 (0.);
  BBPairForce (rm, vm, mRot, wm, am, wam);
  BWallForce (rm, vm, mRot, wm, am, wam);
  BShForce (rm, vm, mRot, wm, am, wam);
  am -= fvDamp * vm;
  am /= bMass;
  am -= 2. * gravVec;
  dt = 0.02;
  vm += dt * am;
  rm += dt * vm;
  dSp = blkGap * blkSph;
  wam = mRot * (wam * mRot / (0.25 * (vec3 (dot (dSp, dSp)) - dSp * dSp) + 1.));
  wm += dt * wam / bMass;
  qm = normalize (QtMul (RMatToQt (LpStepMat (0.5 * dt * wm)), qm));
}

void OrientVu (inout vec4 qtVu, vec4 mPtr, inout vec4 mPtrP, bool init)
{
  vec3 vq1, vq2;
  vec2 dm;
  float mFac;
  if (! init) {
    qtVu = EulToQt (vec3 (0.1, 0.4, 0.3) * pi);
    mPtrP = vec4 (99., 0., -1., 0.);
  } else {
    if (mPtr.z > 0.) {
      if (mPtrP.x == 99.) mPtrP = mPtr;
      mFac = 1.5;
      dm = - mFac * mPtrP.xy;
      vq1 = vec3 (dm, sqrt (max (1. - dot (dm, dm), 0.)));
      dm = - mFac * mPtr.xy;
      vq2 = vec3 (dm, sqrt (max (1. - dot (dm, dm), 0.)));
      qtVu = normalize (QtMul (vec4 (cross (vq1, vq2), dot (vq1, vq2)), qtVu));
      mPtrP = mPtr;
    } else mPtrP = vec4 (99., 0., -1., 0.);
  }
}

void Init (ivec2 iv, out vec3 r, out vec3 v)
{
  for (int iy = 0; iy < nBallE; iy ++) {
    for (int ix = 0; ix < nBallE; ix ++) {
      if (iv.x == ix && iv.y == iy) {
        r = 0.97 * spLen * (vec3 (ix, iy, 0.) - vec3 (vec2 (0.5 * float (nBallE - 1)), 0.));
      }
    }
  }
  v = vec3 (0.);
}

void InitB (int mId, out vec3 r, out vec3 v, out vec4 qm, out vec3 wm)
{
  float mIdf;
  mIdf = float (mId);
  r = 0.4 * boxSize * (2. * vec3 (mod (mIdf, 2.), floor (mod (mIdf, 4.) / 2.),
     floor (mIdf / 4.)) - 1.);
  v = vec3 (0.);
  qm = EulToQt (normalize (vec3 (Hashff (mIdf), Hashff (mIdf + 0.3), Hashff (mIdf + 0.6))));
  wm = vec3 (0.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  vec4 mPtr, mPtrP, stDat, qtVu, qm;
  vec3 r, v, wm;
  vec2 canvas;
  ivec2 pxIv, iv;
  float tCur, nStep, asp;
  int mId, pxId, kp;
  bool doInit;
  pxIv = ivec2 (fragCoord);
  pxId = pxIv.x + 2 * nBallE * pxIv.y;
  if (pxIv.x >= 2 * nBallE || pxId > 2 * nBallE * nBallE + 4 * nBlk + 2) discard;
  canvas = iResolution.xy;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  asp = canvas.x / canvas.y;
  qtVu = Loadv4 (vec2 (4 * nBlk + 1, nBallE));
  mPtrP = Loadv4 (vec2 (4 * nBlk + 2, nBallE));
  iv = pxIv;
  if (iv.x >= nBallE) iv.x -= nBallE;
  mId = iv.y * nBallE + iv.x;
  if (iFrame <= 5) {
    doInit = true;
    spLen = 1.1;
    boxSize = 0.52 * spLen * float (nBallE - 1);
    nStep = 0.;
    OrientVu (qtVu, mPtr, mPtrP, false);
  } else {
    doInit = false;
    stDat = Loadv4 (vec2 (4 * nBlk + 0, nBallE));
    spLen = stDat.x;
    boxSize = stDat.y;
    nStep = stDat.w;
    ++ nStep;
    if (mPtrP.z < 0.) qtVu = EulToQt (QtToEul (qtVu) + 0.003 * pi * vec3 (0.41, 0., 0.27));
    OrientVu (qtVu, mPtr, mPtrP, true);
    gravVec = vec3 (0., 1., 0.) * QtToRMat (qtVu);
    if (nStep > 100. && mPtr.z > 0. && mPtr.x > 0.5 - 0.05 / asp &&
       mPtr.y > 0.5 - 0.05) doInit = true;
  }
  if (doInit) nStep = 0.;
  bMass = 8.;
  farSep = length (blkGap * (blkSph - 1.)) + 1.;
  fOvlap = 1000.;
  fricN = 1.;
  fricT = 1.;
  fricS = 2.;
  fvDamp = 0.03;
  if (mId < nBallE * nBallE) {
    if (doInit) Init (iv, r, v);
    else Step (iv, r, v);
    stDat = vec4 (((pxIv.x < nBallE) ? r : v), 0.);
  } else if (pxId >= 2 * nBallE * nBallE && pxId < 2 * nBallE * nBallE + 4 * nBlk) {
    mId = (pxId - 2 * nBallE * nBallE) / 4;
    if (doInit) InitB (mId, r, v, qm, wm);
    else StepB (mId, r, v, qm, wm);
    kp = pxId - 2 * nBallE * nBallE - 4 * mId;
    if      (kp == 0) stDat = vec4 (r, 0.);
    else if (kp == 1) stDat = vec4 (v, 0.);
    else if (kp == 2) stDat = qm;
    else if (kp == 3) stDat = vec4 (wm, 0.);
  } else {
    kp = pxId - 2 * nBallE * nBallE - 4 * nBlk;
    if      (kp == 0) stDat = stDat = vec4 (spLen, boxSize, tCur, nStep);
    else if (kp == 1) stDat = qtVu;
    else if (kp == 2) stDat = mPtrP;
  }
  Savev4 (vec2 (pxIv), stDat, fragColor, fragCoord);
}

vec4 QtMul (vec4 q1, vec4 q2)
{
  return vec4 (dot (vec4 (q1.w, - q1.z, q1.yx), q2), dot (vec4 (q1.zw, - q1.x, q1.y), q2),
     dot (vec4 (- q1.y, q1.xwz), q2), dot (vec4 (- q1.xyz, q1.w), q2));
}

mat3 QtToRMat (vec4 q) 
{
  mat3 m;
  float a1, a2, s;
  q = normalize (q);
  s = q.w * q.w - 0.5;
  m[0][0] = q.x * q.x + s;  m[1][1] = q.y * q.y + s;  m[2][2] = q.z * q.z + s;
  a1 = q.x * q.y;  a2 = q.z * q.w;  m[0][1] = a1 + a2;  m[1][0] = a1 - a2;
  a1 = q.x * q.z;  a2 = q.y * q.w;  m[2][0] = a1 + a2;  m[0][2] = a1 - a2;
  a1 = q.y * q.z;  a2 = q.x * q.w;  m[1][2] = a1 + a2;  m[2][1] = a1 - a2;
  return 2. * m;
}

vec4 RMatToQt (mat3 m)
{
  vec4 q;
  const float tol = 1e-6;
  q.w = 0.5 * sqrt (max (1. + m[0][0] + m[1][1] + m[2][2], 0.));
  if (abs (q.w) > tol) q.xyz =
     vec3 (m[1][2] - m[2][1], m[2][0] - m[0][2], m[0][1] - m[1][0]) / (4. * q.w);
  else {
    q.x = sqrt (max (0.5 * (1. + m[0][0]), 0.));
    if (abs (q.x) > tol) q.yz = vec2 (m[0][1], m[0][2]) / q.x;
    else {
      q.y = sqrt (max (0.5 * (1. + m[1][1]), 0.));
      if (abs (q.y) > tol) q.z = m[1][2] / q.y;
      else q.z = 1.;
    }
  }
  return normalize (q);
}

vec4 EulToQt (vec3 e)
{
  float a1, a2, a3, c1, s1;
  a1 = 0.5 * e.y;  a2 = 0.5 * (e.x - e.z);  a3 = 0.5 * (e.x + e.z);
  s1 = sin (a1);  c1 = cos (a1);
  return normalize (vec4 (s1 * cos (a2), s1 * sin (a2), c1 * sin (a3),
     c1 * cos (a3)));
}

vec3 QtToEul (vec4 q)
{
  vec3 e;
  float s, t;
  t = dot (q.xy, q.xy);
  s = 2. * sqrt (max (t * (1. - t), 0.));
  e.y = atan (s, 1. - 2. * t);
  if (s != 0.) e.xz = 
     vec2 (atan (q.x * q.z + q.y * q.w, q.x * q.w - q.y * q.z),
           atan (q.x * q.z - q.y * q.w, q.x * q.w + q.y * q.z));
  else e.xz = vec2 (2. * atan (q.z, q.w), 0.);
  return e;
}

mat3 LpStepMat (vec3 a)
{
  mat3 m1, m2;
  vec3 t, c, s;
  float b1, b2;
  t = 0.25 * a * a;
  c = (1. - t) / (1. + t);
  s = a / (1. + t);
  m1[0][0] = c.y * c.z;  m2[0][0] = c.y * c.z;
  b1 = s.x * s.y * c.z;  b2 = c.x * s.z;
  m1[0][1] = b1 + b2;  m2[1][0] = b1 - b2;
  b1 = c.x * s.y * c.z;  b2 = s.x * s.z;
  m1[0][2] = - b1 + b2;  m2[2][0] = b1 + b2;
  b1 = c.y * s.z;
  m1[1][0] = - b1;  m2[0][1] = b1;  
  b1 = s.x * s.y * s.z;  b2 = c.x * c.z;
  m1[1][1] = - b1 + b2;  m2[1][1] = b1 + b2; 
  b1 = c.x * s.y * s.z;  b2 = s.x * c.z;
  m1[1][2] = b1 + b2;  m2[2][1] = b1 - b2;
  m1[2][0] = s.y;  m2[0][2] = - s.y;
  b1 = s.x * c.y;
  m1[2][1] = - b1;  m2[1][2] = b1;
  b1 = c.x * c.y;
  m1[2][2] = b1;  m2[2][2] = b1;
  return m1 * m2;
}

const float cHashM = 43758.54;

float Hashff (float p)
{
  return fract (sin (p) * cHashM);
}

vec3 GetR (vec2 v)
{
  return Loadv4 (v).xyz;
}

vec3 GetV (vec2 v)
{
  v.x += float (nBallE);
  return Loadv4 (v).xyz;
}

