// Original code created by Trashe725

#define AA 4
#define pi 3.14159265358979323846
#define maxIter 100
#define maxThres 100.

// from julesb
// github: https://github.com/julesb/glsl-util/blob/master/complexvisual.glsl

float sinnh(float a) { return float(0.5*( exp(a) - exp(-a))); }
float cossh(float a) { return float(0.5*( exp(a) + exp(-a))); }
float tannh(float a) { return float(sinnh(a)/cossh(a)); }
vec2 cx_add(vec2 a, float b) { return vec2(a.x+b, a.y); }
vec2 cx_mul(vec2 a, vec2 b) { return vec2(a.x*b.x-a.y*b.y, a.x*b.y+a.y*b.x); }
vec2 cx_div(vec2 a, vec2 b) { return vec2((a.x*b.x+a.y*b.y)/(b.x*b.x+b.y*b.y), (a.y*b.x-a.x*b.y)/(b.x*b.x+b.y*b.y)); }
vec2 cx_cos(vec2 a) { return vec2(cos(a.x) * cossh(a.y), -sin(a.x) * sinnh(a.y)); }
vec2 cx_sin(vec2 a) { return vec2(sin(a.x) * cossh(a.y), cos(a.x) * sinnh(a.y)); }
vec2 cx_tanh(vec2 a) { return vec2(cx_div(cx_sin(a),cx_cos(a))); }

vec3 render(vec2 fragCoord, float time)
{
    //zoom
    float sc = 0.12;
    // Center of the image
    vec2 ce = vec2(1.99, 0.0);
    float zoom = time;
    sc = sc * pow(0.9, zoom);

    vec2 fc = (-iResolution.xy + 2.0 *fragCoord) / iResolution.x;
    vec2 z = ce + sc * fc;
    vec2 dz = vec2(1.0, 0.0);

    vec2 lz = z;
    vec2 ldz = dz;

    //iter
    int iter = 0;
    while(iter < maxIter && length(z) < maxThres){
        lz = z;
        ldz = dz;
        vec2 piz = pi*z;
        dz = cx_add( pi*cx_mul(cx_sin(piz), cx_add(z*4., 2.))-5.*cx_cos(piz), 7.)/4.;
        z = ( cx_add(z*7., 2.) - cx_mul(cx_add(z*5., 2.), cx_cos(piz)) )/4.;
        ++iter;
    }

    //color
    float de = length(lz)*log(length(lz))/length(ldz);
    if (iter < maxIter && length(ldz) < maxThres){

        return vec3(1.0, 1.0, 1.0);
    } else {
    	return vec3(0.0, sin(clamp( length(dz), 0.0, 1.0)), tannh(clamp(length(ldz), 0.0, 1.0)));
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col = vec3(0.0);
    float time = -sin(iTime/10.)*9.1 + pi/2.;
#ifdef AA
    for(int m=0;m<AA;++m){
        for(int n=0;n<AA;++n){
        	vec2 px = fragCoord + vec2(float(m), float(n))/float(AA);
            col += render(px, time);
        }
    }

    col /= float(AA*AA);
#else
    col = render(fragCoord, time);
#endif
    fragColor = vec4(col, 1.0);
}

#define S(v)   smoothstep(1.5,0.,(v)/fwidth(v))  // antialiased draw // 15./R.y
#define L(x,y) length(vec2(x,y))

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = 5.* ( 2.*u - R ) / R.y, V=U;
    U.x = mod(U.x-3.*iTime, 4.)-2.;      // horizontal tranlation

    float t = 2.*iTime, f,F,c,
#define setT(t) f = fract(t), F = min(f,1.-f), c = F - A
          a = atan(U.y,U.x), A = abs(a)/1.6, n = 6.,
       // B = (2.-A) / (2.-F),           // normalized angle along deformed perimeter
          l = length(U);
    O-=O;
 // t += .06*V.x;                        // make them out of phase 

    // --- body
    setT(t);
    O.g =   S ( l-1.) * S ( c*l );       // disk * pie

    // --- eye
    a = a/1.6 - 1.5/(2.-F);              // local eye coordinates
    O += S( L(a,l-.5) -.25 );            //   white part
    O -= S( L(a,l-.5) -.12 ) * 2.;       //   black part
 // O += S( abs(a/1.6 - 1.5/(2.-F) )-.2 ) * S( abs(l-.5) - .25 ); // square

    // --- hairs
    setT( t - .3*(l-1.) );               // deform: small time delay
    A = fract( n* (2.-A) / (2.-F) ) -.5; // local hair ordinate
    O.g += (   S( L(A,2.*(l-1.2)) -.2 )  //   disk + bar
             + S( abs(A)-.1 ) * S ( abs(l-1.1)-.1 ) ) * S ( c*l );
 // O.g +=     S( A ) * S ( abs(l-1.2)-.2 )  * S ( c*l ) ;  // squares
}

#define R iResolution.xy

#define S(d,r) smoothstep( .7, -.7, (d-r)*R.y/2.)  // antialiased draw

float dots(vec2 p, vec2 a,vec2 b, float w) {      // SDF to dots
    p -= a, b -= a;
    float h = dot(p, b) / dot(b, b),    
          c = clamp(h, 0., 1.-w/2.),
          n = length(b)/w,          // number of dots separate by w
       // n = 10.,                  //  for always 10. dots
          H = round(c*n)/n;
    return length(p - b * H);
 // return h==clamp(h, 0., 1.) ? length(p - b * h) : 1e5; // line without round ends

}

void mainImage( out vec4 O, vec2 u )
{
    float scale = 2./R.y,        t = iTime, d = 1e3,
          WIDTH = 10.* scale;
    vec2 U = scale* ( u - .5*R ),
         M = scale* ( iMouse.xy - .5*R );
    if (length(iMouse.xy) < 10. ) M = vec2(.6*cos(t)+.3*sin(2.3*t),.6*sin(.9*t)-.3*cos(1.8*t)); // demo mode 

    // SDF of multiple dotty lines
    d = min(d, dots (U, vec2(0), M , WIDTH ) );
    d = min(d, dots (U, M, vec2(M.y,M.x) , WIDTH ) );
    d = min(d, dots (U, vec2(M.y,M.x), vec2(0) , WIDTH ) );
    O = vec4( S( d, .8*WIDTH/2.) ); // draw dots
}

//Random Trig by eiffie - playing with waveforms
// The sine wave when matched with its "co"-sine 1/4 of a cycle ahead
// create the unit circle. All waveforms create a shape this way and
// form the basis for their own user defined trigonometry.
// What waveform creates a box, a triangle, a cat?
// Usage:
// Draw the "sine" wave on the black surface starting from the left side
// on the green line and ending on the right back to zero (the green line).
// The resulting shape is drawn on the white surface.

// While mostly a time waster there are some simple waveforms that are
// useful in cg. This exercize gives you an intuition where to start looking.
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 uv=fragCoord/iResolution.xy;
  fragColor=texture(iChannel0,uv);            //base line for drawing
  if(uv.x<0.5){fragColor.g+=0.5*smoothstep(1.0/iResolution.y,0.,min(abs(uv.y-0.5),mod(uv.x,.125)));return;}
  vec4 lastms=texture(iChannel0,vec2(0.5,1.5)/iResolution.xy);
  if(lastms.x>iResolution.x/2.-5.){//good enough
    float a=fract(iTime*0.1);//rotation angle
    float sa=texture(iChannel0,vec2(a*0.5,0.0)).r;//get trig from lookup table
    float ca=texture(iChannel0,vec2(fract(a+0.25)*0.5,0.0)).r;
    ca=(ca*2.)-1.0;sa=(sa*2.)-1.0;//fix for 0-1 to -1,1
    uv.x=(uv.x-0.5)*2.0;//fix for left side
    uv=(uv-vec2(0.5))*2.0;//and scale
    float d=length(uv-vec2(ca,sa));//distance to this pixel
    fragColor.rgb=mix(vec3(1.0,0.0,0.0),fragColor.rgb,smoothstep(d,8.0/iResolution.y,0.0));
  }
}

//I'm Developing a Pyramid Complex - by eiffie
float nois(vec2 v){// based on 2d noise by Trisomie21
  vec4 h=fract(sin(vec4(floor(v.x)+floor(v.y)*100.)+vec4(0.,1.,100.,101.))*100.);
  v=smoothstep(0.,1.,fract(v));return mix(mix(h.x,h.y,v.x),mix(h.z,h.w,v.x),v.y);
}
float bxLength(vec2 p){p=abs(p);return max(p.x,p.y);}
float wav(float a){return abs(fract(a)-0.5);}
float brick(vec2 p){
  p.y+=0.5;//if(mod(p.y+0.5,2.0)<1.)p.x+=1.0;//i've turned all the pyrs to 45 deg
  return min(wav(p.x*0.45)*wav(p.y),0.07);   //so no need to offset brick pattern
}
vec3 mcol=vec3(0.0);float detail=0.0;
float DE(vec3 p0){
  if(detail>0.0){
    p0.y+=0.00025*nois(p0.xz*1000.0)*detail;
    p0.y-=abs(sin(p0.y*1000.0))*0.001*detail; 
  }
  vec2 p=p0.xz;
  float d=bxLength(p)-0.25+p0.y,ts=1.0,s=1.936,id=1.,sid=1.;
  for(int i=0;i<6;i++){
    p=(abs(vec2(p.x-p.y,p.y+p.x)*0.707)-0.6)*s;ts*=s;
    float d2=max(((bxLength(p)-0.25)/ts+p0.y)*.5,p0.y-0.235/ts);
    if(d2<d){sid=id;d=d2;}id=-id;
  }
  float y=p0.y;
  if(detail>0.0){
    y+=0.0025*sin(p0.x*100.0+sin(p0.z*50.0))*detail;
    y+=0.00025*sin(y*100.0+sin(p0.x*400.0+sin(p0.z*200.0)))*detail;
    if(sid<0.)p0.x+=p0.z;//turn axis aligned pyrs 45 deg
    d-=brick(p0.xy*318.3)*0.005*detail;
  }
  mcol=vec3(0.6,(d<y?0.5:0.2),0.);
  return min(d,y)*0.5;
}
vec3 sky(vec3 rd, vec3 L){//modified bananaft's & public_int_i's code
  float d=0.4*dot(rd,L)+0.6;
  if(rd.y<0.0)return vec3(0.28,0.17,0.07)*dot(vec3(rd.x,-rd.y,rd.z),L);
  float y=max(0.,L.y),sun=max(1.-(1.+10.*y+rd.y)*length(rd-L),0.)
    +.3*pow(1.-rd.y,12.)*(1.6-y);
  return d*mix(vec3(0.3984,0.5117,0.7305),vec3(0.7031,0.4687,0.1055),sun)
    *((.5+pow(y,.4))*(1.5-abs(L.y))+pow(sun,5.2)*y*(5.+15.0*y));
}
float rnd;
void randomize(in vec2 p){rnd=fract(float(iFrame)+sin(dot(p,vec2(13.3145,117.7391)))*42317.7654321);}

vec3 scene(vec3 ro, vec3 rd){
  vec3 L=normalize(vec3(0.4,0.025,0.5));
  vec3 bcol=sky(rd,L);
  vec4 col=vec4(0.0);//color accumulator
  float t=DE(ro)*rnd,d,od=1.0,px=1.0/iResolution.x;ro+=t*rd;
  for(int i=0;i<256;i++){
    detail=max(1.0-t*2.0,0.0);
    d=DE(ro);
    float soc=px*t*max(20.-750.*t,1.0);
    if(d<soc){
      vec3 scol=mcol;
      float d3=DE(ro+d*16.0*L),d2=DE(ro+d*2.0*L);
      float vis=clamp(1.0-d/od,0.0,1.0);
      float dif=clamp(0.3*d2/d,0.0,1.0);
      float ld=dot(rd,L);
      float spec=ld<0.0?-ld*vis:ld*(1.0-vis);
      float shad=clamp(0.03125*d3/d,0.0,1.0);
      if(detail>0.0)
        scol+=(vec3(nois(ro.xz*2500.0))+vec3(1.0,0.5,0.0)*nois((ro.zx+(mcol.g==0.5?2.0:.2)*ro.yy)*7000.0))*detail;
      scol=(scol*dif+vec3(vis)+vec3(1.0,0.9,0.6)*spec)*shad*5.0;
      float alpha=(1.0-col.w)*clamp(1.0-d/soc,0.0,1.0);if(alpha>0.89)alpha=1.0;
      col+=vec4(clamp(scol,0.0,1.0),1.0)*alpha;
      if(col.w>0.9)break;
    }
    t+=d;od=d;ro+=rd*d;
    if(t>3.0)break;
  }
  col.rgb+=bcol*(1.0-clamp(col.w,0.0,1.0));
