float randi(float n){return mod(floor(iTime*10.0),n);}
float spow(float a, float p){return sign(a)*pow(abs(a),p);}
vec3 mcol=vec3(0.0),glw=vec3(0.0);
float MD=11.,LGT=2.;
float DE(vec3 p0){
  vec4 p=vec4(p0,1.0);
  float d=10.0,id=0.;
  p=abs(p);
  if(p.x>p.y)p.xy=p.yx;
  for(int n=0;n<5;n++){
    id+=(sign(p.x)+sign(p.z))*p.w;
    p.xz=abs(p.xz)-0.72-0.25*spow(sin(iTime),40.0);
    float ds=length(p.xyz)-1.0;
    if(ds<-0.){     
      if(mod(id,MD)==LGT){glw+=vec3(cos(id),sin(id),sin(id+1.))*0.05;d=min(d,ds+1.0)/p.w;}
    }
    d=min(d,max(ds,-min(length(p.xy)-.68,length(p.zy)-.68))/p.w);
    p.y-=1.0;
    p*=2.0;
    if(n==2 && mcol.x>0.0)mcol+=vec3(0.5)+p.xyz/p.w;
  }
  return d;
}
vec3 normal(vec3 p, float d){//from dr2
  vec2 e=vec2(d,-d);vec4 v=vec4(DE(p+e.xxx),DE(p+e.xyy),DE(p+e.yxy),DE(p+e.yyx));
  return normalize(2.*v.yzw+vec3(v.x-v.y-v.z-v.w));
}
vec3 sky(vec3 rd, vec3 L){
  float d=0.1*dot(rd,L)+0.1;
  return vec3(d);
}
float rnd;
void randomize(in vec2 p){rnd=fract(float(iTime)+sin(dot(p,vec2(13.3145,117.7391)))*42317.7654321);}

float ShadAO(in vec3 ro, in vec3 rd){
 float t=0.01*rnd,s=1.0,d,mn=0.01;
 for(int i=0;i<12;i++){
  d=max(DE(ro+rd*t)*1.5,mn);
  s=min(s,d/t+t*0.5);
  t+=d;
 }
 return s;
}
vec3 scene(vec3 ro, vec3 rd){
  float t=DE(ro)*rnd,d,px=1.0/iResolution.x;
  for(int i=0;i<99;i++){
    t+=d=DE(ro+rd*t);
    if(t>10.0 || d<px*t)break;
  }
  vec3 L=normalize(vec3(0.4,0.025,0.5));
  vec3 col=sky(rd,L),sgl=glw;
  if(d<px*t*5.0){
    mcol=vec3(0.001);
    vec3 so=ro+rd*t;
    vec3 N=normal(so,d);
    vec3 scol=mcol*0.25;
    float dif=0.5+0.5*dot(N,L);
    float vis=clamp(dot(N,-rd),0.05,1.0);
    float fr=pow(1.-vis,5.0);
    float shad=ShadAO(so,L);
    col=(scol*dif+fr*sky(reflect(rd,N),L))*shad;
  }
  col+=sgl;
  return col;
}
