#define pi acos(-1.)

float DistLine(vec3 ro, vec3 rd, vec3 p) {
	return length(cross(p-ro, rd))/length(rd);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;
    uv.x *= iResolution.x/iResolution.y;

    vec3 ro = vec3(0., .5, -2.); //vec3(0., (2.*iMouse.y-iResolution.y)/iResolution.y, -2.);
    vec3 rd = vec3(uv.x, uv.y, 0.)-ro;

    float t = iTime*2.;
    vec3 p =.5*vec3(sin(t), 0., .5+cos(t));
    vec3 q =.5*vec3(sin(t+pi), 0., .5+cos(t+pi));
    float d = DistLine(ro, rd, p);
    float e = DistLine(ro, rd, q);

    d = pow(.05/(d+0.), 1.);
    e = pow(.05/(e+0.), 1.);

    vec4 new = vec4(d*vec3(.8,.3,1.)+e*vec3(.3,.8,1.),1.);
    vec4 old = texture(iChannel0, fragCoord.xy / iResolution.xy);

    fragColor = mix(old,new,.1); //old+new;
}

