const float PERIOD_S = 1.7;

const float TILE_WIDTH = 36.;

const float DOT_RADIUS = 8.;
const float CIRCLE_RADIUS = 0.8 * TILE_WIDTH;
const float CIRCLE_LINE_WIDTH = 3.;

const float TAU = 8. * atan(1.);

const vec2 k = vec2(TAU / 7., - TAU / 7.);

float disk(vec2 uv, vec2 center, float radiusPx, float scale) {
    return smoothstep((radiusPx - .5) * scale, (radiusPx + .5) * scale, length(uv - center));
}

float circle(vec2 uv, vec2 center, float radius, float lineWidthPx, float scale) {
    return smoothstep(0.0, lineWidthPx * scale, abs(length(uv - center) - radius));
}

float circleAndDot(vec2 ij, vec2 uv, float scale) {
	float circle = circle(uv, vec2(0.), CIRCLE_RADIUS * scale, CIRCLE_LINE_WIDTH, scale);

    float theta = iTime / PERIOD_S * TAU + dot(ij, k);
    vec2 pointPos = CIRCLE_RADIUS * scale * vec2(sin(theta), cos(theta));
    float point = disk(uv, pointPos, DOT_RADIUS, scale);

    return min(circle, point);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    float scale = 1. / TILE_WIDTH;
    vec2 fragCoordScaled = fragCoord * scale;

    vec2 ij = vec2(floor(fragCoordScaled));
	vec2 uv = 2. * fragCoordScaled - 2. * ij - 1.;

    vec2 ijShifted = vec2(floor(fragCoordScaled + 0.5) - 0.5);
	vec2 uvShifted = 2. * fragCoordScaled - 2. * ijShifted - 1.;

	fragColor = vec4(vec3(1. - min(circleAndDot(ij, uv, scale), circleAndDot(ijShifted, uvShifted, scale))), 1.);
}

