//https://www.shadertoy.com/view/ttc3zr
uint murmurHash12(uvec2 src) {
    const uint M = 0x5bd1e995u;
    uint h = 1190494759u;
    src *= M; src ^= src>>24u; src *= M;
    h *= M; h ^= src.x; h *= M; h ^= src.y;
    h ^= h>>13u; h *= M; h ^= h>>15u;
    return h;
}

// 1 output, 2 inputs
float hash12(vec2 src) {
    uint h = murmurHash12(floatBitsToUint(src));
    return uintBitsToFloat(h & 0x007fffffu | 0x3f800000u) - 1.0;
}

float
SmoothNoise(vec2 uv)
{
    vec2 id = floor(uv);
    vec2 fr = fract(uv);

    fr = fr*fr*(3.0 - 2.0*fr);

    float bl = hash12(id);
    float br = hash12(id + vec2(1.0, 0.0));
    float b  = mix(bl, br, fr.x);

    float tl = hash12(id + vec2(0.0, 1.0));
    float tr = hash12(id + vec2(1.0, 1.0));
    float t  = mix(tl, tr, fr.x);

    return mix(b, t, fr.y);
}

float
ValueNoise(vec2 uv, int octaves)
{
    float noise;
    float totalAmplitude;
    float amplitude = 1.0;
    float frequency = 1.0;

    for(int i = 0; i < octaves; ++i)
    {
        totalAmplitude += amplitude;
        noise += SmoothNoise(uv * frequency) * amplitude;
        frequency *= 2.0;
        amplitude /= 2.0;
    }

    return noise / totalAmplitude;
}

mat3
SetCamera(vec3 eye, vec3 target, float roll)
{
    vec3 i, j, k, temp;

    k = normalize(target - eye);
    temp = normalize(vec3(sin(roll), cos(roll), 0.0));
    i = normalize(cross(temp, k));
    j = cross(k, i);

    return mat3(i, j, k);
}

#define HI_LOD 8
#define LO_LOD 2
float
terrain(vec3 p)
{
    return p.y - ValueNoise(p.xz, HI_LOD);
}

// I know I could change MAX_STEPS and remove the weird haze when you get close to the ground
// but I like it :)
#define MAX_STEPS 23
#define MAX_DIST 20.0
#define MIN_DIST 0.01
float
intersectTerrain(vec3 ro, vec3 rd)
{
    float t = 0.0;

    for(int i = 0; i < MAX_STEPS && t < MAX_DIST; ++ i)
    {
        float hit = terrain(ro + t*rd);

        //Some fancy inigo thing I don't fully understand yet
        //	if ray is partially in it's still cool?
        //	we make the min distance larger when further away?
        //  Optimization or stylistic thing?
        if(abs(hit) < MIN_DIST *t)break;

        t += hit;
    }
    return t;
}

vec3
Render(vec3 ro, vec3 rd)
{
    vec3 col;

    //Raymarching the terrain
    float t = intersectTerrain(ro, rd);

    //Simple shading
    col += t / 9.80665; // :) 
    col *= vec3(0.78, 0.9, 1.0);

    return saturate(col);
}

