int pcount(int n, int m) {
  if (n == 0) return 1;
  if (m == 0) return 0;
  return ptable[20*(n-1)+m-1];
}

// Recurrence:
// parts(0,_) = [[]]
// parts(_,0) = []
// parts(n,m) = parts(n,m-1) + parts(n-m,m)
// Use p(n,m) to decide which branch of the recursion to follow.
int unrank(int n, int k, int t) {
  int m = n, i = 0;
  while (true) {
    int k1 = pcount(n,m-1);
    if (k < k1) m = m-1;
    else {
      t -= m;
      if (t < 0) return i;
      i++; n -= m; k -= k1;
    }
  }
}

vec3 bgcolor(vec2 fragCoord) {
  // 490 = 2*5*7*7 items, each 19 squares, arranged in 14 rows of 7
  // blocks of 5 subrows of 19 square. With a border.
  ivec2 pdim = ivec2(iResolution.xy);
  ivec2 pcoord = ivec2(fragCoord);
  pcoord.y = pdim.y-1-pcoord.y;
  vec3 col = vec3(0.2);
  // Width is 7 blocks of 19, plus 8 separators
  // Height is 14 blocks of 5, plus 15 separators
  int W = 7, W1 = 19;
  int H = 14, H1 = 5;
  int M = 1;
  ivec2 bdim = ivec2(W*(W1+M)+M, H*(H1+M)+M); //7*19+8;
  ivec2 bcoord = pcoord*bdim/pdim;
  int nwidth = bdim.x; //14*5+15;
  int nheight = bdim.y; //14*5+15;
  int i = bcoord.x, j = bcoord.y;
  int i1 = (i+W1)%(W1+M); // Index within a horizontal block
  int j1 = (j+H1)%(H1+M);   // Index within a vertical block
  if (i1 < W1 && j1 < H1) {
    int p = unrank(19,(i-M)/(W1+M)*(H*H1)+(j-M)/(H1+M)*H1+j1,i1);
    if (p%2 != 0) col = vec3(0.8);
    else col = vec3(0.5);
  }
  ivec2 boffset = (bcoord*pdim+bdim-1)/bdim; // Index of 1st pixel in block
  if (bcoord.x > 0 && j1 < H1 && pcoord.x == boffset.x) col = vec3(0);
  if (bcoord.y > 0 && i1 < W1 && pcoord.y == boffset.y) col = vec3(0);
  return col;
}

