// Crack occupancy buffer
// xy = fract(xy)       : allows sub-pixel movement of each crack
// z = angle            : angle of crack travel
// w = tag              : 0 empty, 1 occupied, 2 active

// RNG for crack init, branch creation
vec4 hash4( vec3 p )
{
    vec4 q = vec4( dot(p,vec3(127.1,311.7,256.8)), 
				   dot(p,vec3(269.5,183.3,012.5)), 
				   dot(p,vec3(419.2,371.9,167.1)),
                   dot(p,vec3(269.5,183.3,918.1))
                 );
	return fract(sin(q)*43758.5453);
}

// Update cracks
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 invRes = 1. / iChannelResolution[0].xy;
    vec4 data = R(0.0);

    vec4 rng = hash43(vec3(fragCoord * 0.01, iTime + 132.4));

    // Seed initial cracks
    if (RESET) {
        bool init = rng.z < 0.005 && rng.y < 0.005 && rng.w < 0.5;
        float angle = 2. * 3.14159 * rng.x;
        if (init) {
            //float angle = 1.5 + 1.4 * rng.x;
            // Init frac to middle of the pixel
            fragColor = vec4(0.0, 0.0, angle, 2.);
            return;
        } else {
            fragColor = vec4(0.0, 0.0, angle, 0.);
            return;
        }
    }

    // Slow animation
    #if SLOW_ANIMATION
    if (fract(iTime) > 1./30.) {
        fragColor = data;
        return;
    }
    #endif

    // If we're occupied and inactive, stay inactive with current angle
    if (data.w == 1.) {
        // Small chance of turning into an orthogonal line
        // TODO: only do this if we have enough space
        if (rng.y < 0.005 && rng.x < 0.005) {
        	fragColor = vec4(data.xy, data.z - 0.5 * PI, 2.0);
        } else if (rng.y < 0.005 && rng.z < 0.005) {
        	fragColor = vec4(data.xy, data.z + 0.5 * PI, 2.0);
        } else {
            fragColor = data;
        }
        return;
    }

    // Search neighbors for a particle coming into this cell
    bool entered = false;
    for (int i=-1; i<=1; i++) {
        for (int j=-1; j<=1; j++) {
            // There are some cases where the current cell could be the target
            //if (i==0 && j==0) continue;
            if (entered) break;

            vec2 offset = vec2(i, j);
            vec4 other = R(offset);

            bool otherIsCrack = other.w == 2.0;

            vec2 frac = other.xy;

            float angle = other.b;

            vec2 dir = SPEED * vec2(cos(angle), sin(angle));

            // If this falls within our square (-0.5 ... 0.5) x/y then we become this crack
            vec2 minc = vec2(-0.5);
            vec2 maxc = vec2(0.5);
            vec2 otherNext = offset + frac + dir;
            // Are we the cell this crack will enter?
            if (otherIsCrack
                && all(greaterThan(otherNext, minc))
                && all(lessThan(otherNext, maxc))) {
                // Become this crack
                fragColor = vec4(otherNext.xy, other.z, other.w);
                entered = true;
                break;
            }
        }
    }

    // done searching neighbors
    if (!entered) {
        float flag = data.w;
        flag = min(flag, 1.0);
        fragColor = vec4(data.xy, data.z, flag);   
    }
}

