// Jump-flood algorithm for voronoi / min-distance to lines
// Problem is that this wasn't as useful for estimating distance to nearest line as I thought

// xy = coordinate of nearest line
// zw = 

// Take log(N) steps to resolve a distance image
// https://computergraphics.stackexchange.com/questions/4830/understanding-jump-flooding-algorithm-jfa-for-voronoi-diagrams

#define JFA_STEPS 9
#define JFA_MAX_DIST (1<<JFA_STEPS)
#define JFA_HUGE (100000.0)

// Different algorithm

#if WIP_SAND || WIP_WOS

void mainImage(out vec4 fragColor, in vec2 fragCoord )
{
    vec2 invRes = 1. / iChannelResolution[0].xy;
    int fi = iFrame % JFA_STEPS;
    float progress = float(fi) / float(JFA_STEPS);

    // Can go up or down here
    int jump = 1 << (JFA_STEPS - fi - 1);

    // Read data from occupancy texture
    vec4 data = texture(iChannel0, fragCoord * invRes);

    // If we are an active cell, seed with our data
    float minDist = data.w > 0. ? length(data.xy) : JFA_HUGE;
    vec4  minData = data.w > 0. ? vec4(fragCoord.xy + data.xy, 1.0, 1.0) : vec4(0.0);
    // Check -jump, cur, jump neighborhood
    for (int i=-jump; i<=jump; i+=jump) {
        if (RESET) {
            break;
        }
        for (int j=-jump; j<=jump; j+=jump) {
            vec2 off = vec2(i,j);
            // This is our data, xy is coord of nearest point
            vec4 neigh = texture(iChannel1, (fragCoord + off) * invRes);

			// If neighbor dist is less, use it
            float dist = length(neigh.xy - fragCoord);
            if (dist < minDist) {
                minDist = dist;
                minData = neigh;
            }
        }
    }
    if (minDist < JFA_HUGE) {
	    fragColor.rgba = minData;
    } else {
    	fragColor.rgba = vec4(0.0);
    }
}

#else

void mainImage(out vec4 fragColor, in vec2 fragCoord ) {
    fragColor.rgba = vec4(0.0);
}


