// Quaternion left and right multiplication as matrices
// The two forms commute (this is just associativity).
mat4 qmat_left(vec4 q) {
  float x = q.x, y = q.y, z = q.z, t = q.w;
  return mat4( t, z,-y,-x, 
              -z, t, x,-y,
               y,-x, t,-z,
               x, y, z, t );
}

mat4 qmat_right(vec4 q) {
  float x = q.x, y = q.y, z = q.z, t = q.w;
  return mat4( t,-z, y,-x, 
               z, t,-x,-y,
              -y, x, t,-z,
               x, y, z, t );
}

// Farey(10) sequence
const int nfarey = 33;
const int farray[2*nfarey] =
  int[](0,1,1,10,1,9,1,8,1,7,1,6,1,5,2,9,1,4,2,7,3,10,
        1,3,3,8,2,5,3,7,4,9,1,2,5,9,4,7,3,5,5,8,2,3,7,
        10,5,7,3,4,7,9,4,5,5,6,6,7,7,8,8,9,9,10,1,1);

float farey(int i) {
  i %= nfarey;
  return float(farray[2*i])/float(farray[2*i+1]);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 uv = (2.0*fragCoord-iResolution.xy)/iResolution.y;
  float size = 4.0*exp(3.0*iMouse.y / iResolution.y);
  uv *= size;

  float tr,tq;
  tq = atan(1.0);
  float t = 1.0*iTime;
  float i0 = 2.0*floor(0.5*t);
  float i1 = 2.0*floor(0.5*t+0.5);
  int mfactor = 256;
  tr = atan(mix(farey(int(i0)*mfactor),farey(int(i1)*mfactor),fract(t)));

  vec4 q = vec4(sin(tq)*vec3(1,0,0),cos(tq));
  vec4 r = vec4(sin(tr)*vec3(0,1,0),cos(tr));

  vec4 p = vec4(uv,0,0);
  mat4 qm = qmat_left(q)*qmat_right(r);
  float ds = fwidth(p.x);
  p = qm*p;
  p = mod(p,2.0);
  vec4 dp = fract(p);
  dp = min(dp,1.0-dp);
  p = floor(p);
  int parity = int(dot(p,vec4(1)))%2;

  mat4x2 dm = transpose(mat2x4(qm));
  dp /= vec4(length(dm[0]),length(dm[1]),length(dm[2]),length(dm[3]));
  float d = min(min(dp.x,dp.y),min(dp.z,dp.w));
  vec3 color = vec3(smoothstep(-ds,+ds,parity==0?d:-d));
  color = mix(vec3(1,0,0),color,smoothstep(0.01,0.02+ds,d));
  color = pow(color,vec3(0.4545));
  fragColor = vec4(color,1);
}

