#define NB_PIECE	6
#define OFFSET		0.6
#define SCALE		1.2
#define ROT_SPEED	0.1
#define TAU 		radians(360.)
#define MAX_ITER	4.
#define CYCLE		15.

vec2 kaleidoscope(vec2 uv, float iter, float v)
{
    float c = clamp(v - iter, 0., 1.);
    float t = fract(iTime * ROT_SPEED * (iter + 1.)) * TAU / float(NB_PIECE);
    float a = atan(uv.y, uv.x) + t;

    float p = round(a * float(NB_PIECE) / TAU) * TAU / float(NB_PIECE);

    vec2 offset = vec2(cos(p - t), sin(p - t)) * c * OFFSET;

    return  uv * mix(1., pow(SCALE, iter + 1.), c) - offset;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2  uv = (2. * fragCoord - iResolution.xy) / iResolution.x;

    float v = abs(fract((0.5 * iTime - CYCLE / 2.) / CYCLE) * 2. * MAX_ITER - MAX_ITER);

    for(int i = 0; i < int(MAX_ITER); ++i)    
    	uv = kaleidoscope(uv, float(i), v);

    uv = (uv * vec2(1, 16./9.) / 2. + 0.5);

    vec3 col = texture(iChannel0, uv).rgb;

    fragColor = vec4(col,1.0);
}

vec3 sunDir = normalize(vec3(-0.0, 0.2, -1.0));

