}

vec3 rand3d(vec3 i) {
    return fract(sin(vec3(
        dot(i, vec3(12.345, 67.890, 1.56)),
        dot(i, vec3(512.21, 90.511, 80.42)),
        dot(i, vec3(6.28, 30.19, 37.48))
    )) * 421234.567) * 2.0 - 1.0;
}

vec3 solColor(vec3 pos) {
    vec3 baseColor = vec3(2.0, 2.0, 2.2);
    pos = pos * 5.0;
    vec3 u = mod(floor(pos), 2.0) * 0.5;
    vec3 f = fract(pos);
    float m = 0.6 + clamp(abs(u.x - u.z), 0.0, 1.0) * 0.4;
    return vec3(m) * baseColor;
}

vec2 map(vec3 pos) {
    float closest = 1000.0;
    float id = -1.0;

    float dist = sol(pos);
    if (dist < closest) { closest = dist; id = 0.5; }

    return vec2(closest, id);
}

vec2 intersect(vec3 ro, vec3 rd) {
    float depth = 0.0;
    float id = -1.0;
    for (int i = 0; i < 200; i++) {
        vec2 info = map(ro + rd * depth);
        if (info.x <= 0.001) {
            id = info.y;
            break;
        }
        depth += info.x;
    }
    return vec2(depth, id);
}

vec3 getSkyColor(vec3 p, vec3 rd) {
    vec3 c = vec3(0.6, 0.8, 0.9);
    c += rd.y * vec3(0.3, 0.3, 1.1);
    float s = clamp(dot(rd, sunDir), 0.0, 1.0);
    c += vec3(0.3, 0.3, 0.0) * pow(s, 32.0);
    c += 0.1 * vec3(1.0, 0.4, 0.2) * pow(s, 8.0);
    return c;
}

vec3 getColor(float id, vec3 p, vec3 rd) {
    if (id < -0.5) { // ciel
        return getSkyColor(p, rd);
    }
    if (id < 1.0) { return solColor(p); }
    return vec3(1.0, 0.0, 0.0);
}

vec3 getNormal(vec3 pos) {
    const float epsilon = 0.001;
    return normalize(vec3(
        map(pos).x - map(vec3(pos.x - epsilon, pos.yz)).x,
        map(pos).x - map(vec3(pos.x, pos.y - epsilon, pos.z)).x,
        map(pos).x - map(vec3(pos.x, pos.y, pos.z - epsilon)).x
    ));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord.xy / iResolution.xy * 2.0 - 1.0;

    vec3 ro = vec3(iTime * 0.05, 0.4, iTime * 0.01);

    vec3 center = vec3(0.0, 0.5, -500.0);
    vec3 front = normalize(center - ro);
    vec3 right = normalize(cross(front, vec3(0.0, 1.0, 0.0)));
    vec3 up = normalize(cross(right, front));

    mat4 lookAt = mat4(
        vec4(right, 0.0),
        vec4(up, 0.0),
        vec4(front, 0.0),
        vec4(0.0, 0.0, 0.0, 1.0)
    );
    vec3 rd = normalize(vec3(lookAt * vec4(uv, 2.0, 1.0)));

    vec2 info = intersect(ro, rd);
    vec3 pos = ro + rd * info.x;
    vec3 n = getNormal(pos);

    float ambient = 1.0;
    float diffuse = max(dot(n, sunDir), 0.0);
    float back = max(dot(n, vec3(-sunDir.x, 0.0, -sunDir.z)), 0.0);
    float dome = 0.2 + 0.8 * clamp(rd.y, 0.0, 1.0);
    float sol = 0.2 + 0.8 * clamp(-rd.y, 0.0, 1.0);

    vec3 light = vec3(0.0);
    light += ambient * vec3(0.0304, 0.03, 0.02);
    light += diffuse * vec3(0.30, 0.30, 0.3);
    light += back * vec3(0.3, 0.37, 0.3);
    light += dome * vec3(0.515, 0.51, 0.51);
    light += sol * vec3(0.21, 0.2, 0.2);

    if (info.y < -0.5) {
        light = vec3(1.0);
    }
    vec3 color = getColor(info.y, pos, rd) * light;
    color = mix(color, getSkyColor(pos, rd), clamp(pow(info.x / 20.0, 2.0), 0.0, 1.0));
    color = pow(color, vec3(0.4545));

    fragColor = vec4(color, 1.0);
}

