// How many cells around the central_cell we check for ellipses.
#define SAMPLE_RADIUS 2
const float num_layers = 50.;
const float depth_of_field = 30.; // the distance between in-focus and having a blurRadius of 1.

/*
// Debugging things.
const vec3 default_debug = vec3(-100, -20, -10);
const vec3 orange = vec3(1., .5, 0.);
const vec3 cornflowerblue = vec3(100./255., 149./255., 237./255.);
vec3 debug = default_debug;
*/

float monochrome(vec2 uv) {

    const float cell_wd = 6.0;
    vec2 central_cell_center = floor((uv+.5*cell_wd) / cell_wd) * cell_wd;

	float hashTime = iTime + 100.;  // Don't want thing to be synchonous when the shader starts    

    float acc = 0.;
    // Painters algorithm.
    for (float z = 1.0; z>=-0.001; z -= 1./num_layers) {
        vec2 shifted_central_cell_center = central_cell_center + cell_wd * vec2(
            hash13(vec3(10.2, 1.13, z)),
            hash13(vec3(20.3, 2.34, z))
        );

        int j;
        for (int i = -SAMPLE_RADIUS; i<=SAMPLE_RADIUS; ++i) {
            for (j = -SAMPLE_RADIUS; j<=SAMPLE_RADIUS; ++j) {
                vec2 cell_center = shifted_central_cell_center + cell_wd*vec2(ivec2(i,j));
              	//cell_center = round(cell_center);
                //cell_center = round(cell_center);

                float hash_z = 10. * z;
                vec2 cell_hash_pos = cell_center + vec2(1030., 0);
                float blurRadius = depth_of_field*abs((z+.5*hash13(vec3(cell_hash_pos, ++hash_z))) - focalPlane);

                float yaw = .1*hashTime + TAU * hash13(vec3(cell_hash_pos, ++hash_z));
                float angular_speed = 3. + hash13(vec3(cell_hash_pos, ++hash_z));
                vec2 angular_vel = angular_speed * vec2(cos(yaw), sin(yaw));
                float pitch = length(angular_vel) * hashTime * 0.6;
                vec2 a = normalize(angular_vel);  // axis of rotation - major axis of projected ellipse.
                //vec2 b = vec2(-a.y, a.x) * cos(pitch);  // minor axis
                vec2 ab = vec2(1., abs(cos(pitch)));
                vec2 extended_ab = ab + vec2(blurRadius);

                mat2 rot = mat2(a.x, -a.y,
                                a.y, a.x);

                vec3 normal = vec3(0.0, sin(pitch), cos(pitch));
                mat3 rot3 = mat3(a.x, -a.y, 0.,
                                 a.y, a.x, 0.,
                                 0., 0., 1.);
                normal = rot3 * normal;
                const vec3 LIGHT_DIR = normalize(vec3(1.5, -1.0, 2.0));
                float light_per_area = 
                    .2 + // ambient
                    100.0 * smoothstep(.98, .99, abs(dot(normal, LIGHT_DIR)));  // cheap (wrong-ish) specular
                float total_light = light_per_area * ab.x * ab.y;  // total amount of light emitted by this object towards all pixels.
                // Have a random offset from the cell_center.
                vec2 ellipse_center = cell_center + (.5*cell_wd - ab.x) * vec2(
                    sin(hashTime * (.01 + .06*hash13(vec3(cell_hash_pos, ++hash_z)))),
                    cos(hashTime * (.01 + .06*hash13(vec3(cell_hash_pos, ++hash_z))))
                );
                vec2 ellipse_uv = uv - ellipse_center;   

                //float sd = sdEllipse(transpose(rot)*inner_uv, extended_ab);
                //float sd = sdEllipsoidApproximated(transpose(rot)*inner_uv, extended_ab);
                float sd = length(rot*ellipse_uv / extended_ab) - 1.;  // Signed distance approximation only accurate for values near zero.
                float proportional_area = extended_ab.x * extended_ab.y;
                float edge_blurryness = 0.1/length(extended_ab);
                float is_in_ellipse = smoothstep(edge_blurryness, -edge_blurryness, sd);


                float opacity = is_in_ellipse * ab.x / extended_ab.x;
                //acc = mix(acc, total_light / proportional_area, opacity);
                //if (opacity > .9) debug=orange;
                acc *= 1.-(opacity*opacity*opacity*opacity*opacity);  // haax to allow dark in-focus objects in front of blurry backgrounds.
                acc += opacity * total_light / proportional_area;
            }
        }
    }
	return acc;
}

