        		float grav = -(g*2.) / (r*r);

                bA.x -= grav*dir.x*dt;
                bA.y -= grav*dir.y*dt;
            }
    	}

        bA.x = clamp(bA.x, R.x*0.15, R.x-R.x*0.15);
        bA.y = clamp(bA.y, 10., R.y-R.y*.15);

    	if(iFrame == 0){
            if(u.x <= mem && iu.y == 0 ){
                bA.xy = hash22(u)*R.xy*0.5 + R.xy*0.25;
                bA.zw = vec2(0.);
                f = vec4(0);
            }
    	}

        f = bA;
    }
    else return;

}

