////////////////////////////////////////////////////////////////////////////////
//
// More Apollonian circles.
// Matthew Arcus, mla, 2020
//
// <mouse>: move things around
// <up>/<down>: zoom in/out
// a: animation
// p: progressive display
// m: enable mouse
//
////////////////////////////////////////////////////////////////////////////////

const int max_iterations = 20;
const float PI = 3.14159265;

// Smooth HSV to RGB conversion 
// Function by iq, from https://www.shadertoy.com/view/MsS3Wc
vec3 hsv2rgb( in vec3 c ) {
  vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
  rgb = rgb*rgb*(3.0-2.0*rgb); // cubic smoothing	
  return c.z * mix( vec3(1.0), rgb, c.y);
}

vec3 getCol(vec4 n){
  if (n.z == float(max_iterations)) return vec3(0);
  return vec3(1);
  vec3 col = hsv2rgb(vec3(mod(n.z/20.0,1.0),0.8,0.8));
  //col *= 0.6+0.4*smoothstep(0.05,0.1,sqrt(n.w));
  return col;
}

bool keypress(int code) {
  return texelFetch(iChannel3, ivec2(code,2),0).x != 0.0;
}
vec4 store(int i,int j) {
  return texelFetch(iChannel2, ivec2(i,j),0);
}

int keycount(int key) {
  return int(store(0,key).x);
}

const int CHAR_A = 65;
const int CHAR_D = 68;
const int CHAR_H = 72;
const int CHAR_J = 74;
const int CHAR_M = 77;
const int CHAR_P = 80;
const int CHAR_V = 86;
const int CHAR_Z = 90;
const int KEY_LEFT = 37;
const int KEY_RIGHT = 39;
const int KEY_UP = 38;
const int KEY_DOWN = 40;

vec2 cmul(vec2 z, vec2 w) {
  return vec2(z.x*w.x - z.y*w.y, z.x*w.y + z.y*w.x);
}

vec2 cinv(vec2 z) {
  return z/dot(z,z)*vec2(1,-1);
}

vec2 csqrt(vec2 z) {
  float r = length(z);
  return vec2(sqrt(0.5*(r+z.x)),sign(z.y)*sqrt(0.5*(r-z.x)));
}

#define AA 2.0

const float r = 1.0/(sqrt(2.0)+1.0);
const float R = r*tan(3.0*PI/8.0); // R = 1 in fact
const float r0 = r*(sqrt(2.0)-1.0);
const vec4 circles1[] =
  vec4[](vec4(0,0,r0*r0,1.0),
         vec4(r,r,r*r,1.0),
         vec4(r,-r,r*r,1.0),
         vec4(-r,r,r*r,1.0),
         vec4(-r,-r,r*r,1.0),
         vec4(0,0,1,-1.0)
     );
const float R0 = r+2.0*R;
vec4 circles2[] =
  vec4[](vec4(0,0,r*r,1.0),
         vec4(0,r+R,R*R,1.0),
         vec4(0,-r-R,R*R,1.0),
         vec4(r+R,0,R*R,1.0),
         vec4(-r-R,0,R*R,1.0),
         vec4(0,0,R0*R0,-1.0)
         );

float length2(vec2 p) {
  return dot(p,p);
}

bool inside(vec2 p, vec4 c) {
  float r2 = length2(p-c.xy);
  if (c.w < 0.0) return r2 > c.z;
  else return r2 <= c.z;
}

vec2 invert(vec2 p, vec4 c, inout float scale) {
  p -= c.xy;
  float s = c.z/dot(p,p);
  scale *= abs(s);
  p *= s;
  p += c.xy;
  return p;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
  int N = circles1.length();
  int maxsteps = 20;
    if (keypress(CHAR_P)) {
        maxsteps = int(iTime)%40;
        maxsteps = min(maxsteps,40-maxsteps);
    }// Progressive stepping
  vec2 z0 = (2.0*fragCoord.xy - iResolution.xy)/iResolution.y;
  float zoom = 3.0;
  zoom *= exp(0.1*float(keycount(KEY_DOWN)-keycount(KEY_UP)));
  // Transform to put centre at m
  float aadelta = 1.0/(AA*iResolution.y);
  vec3 aacol = vec3(0);
  for (float p = 0.0; p < AA; p++) {
    for (float q = 0.0; q < AA; q++) {
      vec2 z = z0 + aadelta*vec2(p,q);
      z *= zoom;
      float scale = zoom;
      if (!keypress(CHAR_M) && iMouse.x > 0.0) {
        vec2 m = (2.0*iMouse.xy - iResolution.xy)/iResolution.y;
    	m *= zoom;
    	float k = 1.0;
    	float r = length(m);
    	float s = 0.5*(-r + sqrt(r*r + 4.0*k));
    	vec4 tcircle = vec4(-s/r*m,1,0);
        z = invert(z,tcircle,scale);
        z.x = -z.x;
	  }
      if (!keypress(CHAR_A)) {
        vec4 tcircle = vec4(sin(0.5*iTime),0,1,0);
        z = invert(z,tcircle,scale);
        z.x = -z.x;
      }
      vec2 z0 = z;
      int step;
      for (step = 0; step < maxsteps; step++) {
        bool found = false;
        for (int i = 0; i < N; i++) {
          if (inside(z,circles2[i])) {
            z = invert(z,circles2[i],scale);
            found = true;
            break;
          }
        }
        if (!found) break;
      }
      vec3 col = vec3(0);
      float dmin = 1e10;
      if (true) {
        int imin = -1;
        for (int i = 0; i < N; i++) {
          vec4 c = circles2[i];
          float d = abs(length(z-c.xy) - sqrt(c.z));
          d /= scale;
          if (d < dmin) {
            dmin = d;
            imin = i;
          }
        }
        if (imin >= 0) col = hsv2rgb(vec3(float(imin)/float(6),1,1));
      }

      float lwidth = 0.005;

      vec4 tt = texture(iChannel0,0.5*z0);
      float ds = fwidth(dmin);
      float t = 1.0-smoothstep(0.0,max(ds,lwidth*(1.0+tt.y)),dmin);
      col *= tt.x;
      float t1 = 1.0-t;
      t1 *= 0.9+0.1*tt.z;
      vec3 bg = t1*vec3(1,1,0.5);
      col = mix(bg,0.1+0.9*col,t);
      aacol += col;
    }
  }
  aacol.xyz /= AA*AA;
  fragColor = vec4(pow(aacol,vec3(0.4545)),1);
}

