#define TAU 6.283185

vec2 rot(vec2 v, float angle)
{
    float c = cos(angle);
    float s = sin(angle);
    return mat2(c, -s, s, c)*v;
}

// IQ UnevenCapsule distance func
float cro(in vec2 a, in vec2 b ) { return a.x*b.y - a.y*b.x; }
float sdUnevenCapsule( in vec2 p, in vec2 pa, in vec2 pb, in float ra, in float rb )
{
    p  -= pa;
    pb -= pa;
    float h = dot(pb,pb);
    vec2  q = vec2( dot(p,vec2(pb.y,-pb.x)), dot(p,pb) )/h;

    //-----------

    q.x = abs(q.x);

    float b = ra-rb;
    vec2  c = vec2(sqrt(h-b*b),b);

    float k = cro(c,q);
    float m = dot(c,q);
    float n = dot(q,q);

         if( k < 0.0 ) return sqrt(h*(n            )) - ra;
    else if( k > c.x ) return sqrt(h*(n+1.0-2.0*q.y)) - rb;
                       return m                       - ra;
}

float TimerInOut(vec4 v)
{
    return smoothstep(v.y,v.y+v.w,v.x) - smoothstep(v.z-v.w,v.z,v.x);
}
float Stime(float _t, float scale)
{
    return fract(_t*scale)*TAU;
}
float Mtime(float _t, float mval)
{
    return mod(_t,mval);
}

// Trippy spiral calc - Del 06/01/2018
vec2 spiral(vec2 uv,float zoom, float len, float offset,vec2 capoff, float trip, float scount,float rmod)
{
    float d = length(uv) * zoom;
    d=log(d*trip);
    uv = rot(uv,sin(d*offset)+d*scount*rmod);
	float v = 1.0-sdUnevenCapsule(uv,vec2(0.0,0.0)+capoff,vec2(0.4*len,0.0)+capoff,0.0,0.4*len)*14.0;
    return vec2(v,d);	// dx , dy
}

vec2 directionalWaveNormal(vec2 p, float amp, vec2 dir, float freq, float speed, float time, float k)
{	
	float a = dot(p, dir) * freq + time * speed;
	float b = 0.5 * k * freq * amp * pow((sin(a) + 1.0) * 0.5, k) * cos(a);
	return vec2(dir.x * b, dir.y * b);
}

vec3 summedWaveNormal(vec2 p)
{
    float time = iTime;
	vec2 sum = vec2(0.0);
	sum += directionalWaveNormal(p, 0.5, normalize(vec2(1, 1)), 5.0, 1.5, time, 1.0);
	sum += directionalWaveNormal(p, 0.25,normalize(vec2(1.4, 1.0)), 11.0, 2.4, time, 1.5);
	sum += directionalWaveNormal(p, 0.125, normalize(vec2(-0.8, -1.0)), 10.0, 2.0, time, 2.0);
	sum += directionalWaveNormal(p, 0.0625, normalize(vec2(1.3, 1.0)), 15.0, 4.0, time, 2.2);
	sum += directionalWaveNormal(p, 0.03125, normalize(vec2(-1.7, -1.0)), 5.0, 1.8, time, 3.0);
	return normalize(vec3(-sum.x, -sum.y, 1.0));
}
vec3 background(vec2 p)
{
	vec3 normal = summedWaveNormal(p);
	vec3 c = mix(vec3(0.1, 0.15, 0.1), vec3(0.2, 0.25, 0.4),  dot(normal, normalize(vec3(0.2, 0.2, 0.5))) * 0.5 + 0.5);
	c = mix(c, vec3(0.7, 0.9, 1.0), pow(dot(normal, normalize(vec3(-0.4, 0.1, 1.0))) * 0.5 + 0.5, 2.0));
	c = mix(c, vec3(0.95, 0.98, 1.0), pow(dot(normal, normalize(vec3(-0.1, -0.3, 0.5))) * 0.5 + 0.5, 10.0));
    return clamp(c,0.0,1.0);
}

float Bub(vec2 uv,float scale)
{
    float time = iTime*0.75;
    uv.y-=time*2./scale;
    //uv.x += time*0.2;
    uv.x+=sin(uv.y+time*.95)*0.025;	///scale;
	uv*=scale;
    vec2 s=floor(uv);
    vec2 f=fract(uv);
    float k=3.0;
	vec2 p =.5+.35*sin(11.*fract(sin((s+scale)*mat2(7.0,3.0,6.0,5.0))*5.))-f;
    float d=length(p);
    k=min(d,k);
	k=smoothstep(0.0,k,sin(f.x+f.y)*0.01);
   	return k;
}

vec3 Bubbles(vec2 uv,float head,float d)
{
	float c = Bub(uv,30.)*.3;
	c+=Bub(uv,20.)*.5;
	c+=Bub(uv,15.)*.8;
	c+=Bub(uv,10.);
	c+=Bub(uv,8.);
	c+=Bub(uv,6.);
	c+=Bub(uv,5.);
    vec3 scol = vec3(clamp(c*0.3,0.0,0.3));
    return scol;
}

vec3 checks(vec2 p)
{
    float d = length(p);
    float head = 1.0-length(p);
    d+=(0.5+sin(iTime*2.0)*0.5)*0.25;
    p *= 0.25;
    return Bubbles(p*(d+0.5), head,d);
}

vec3 hsv2rgb_smooth( in vec3 c )
{
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );

	rgb = rgb*rgb*(3.0-2.0*rgb); // cubic smoothing	

	return c.z * mix( vec3(1.0), rgb, c.y);
}

float pMod1(inout float p, float size)
{
	float halfsize = size*0.5;
	float c = floor((p + halfsize)/size);
	p = mod(p + halfsize, size) - halfsize;
	return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;
    float _t = iTime+2.8;


    // Create some blends over time...
    float t1 = TimerInOut(vec4(Mtime(_t,9.6),1.0, 7.2, 1.8));
    float t2 = TimerInOut(vec4(Mtime(_t,12.0),1.0, 9.0, 1.5));
    float t3 = TimerInOut(vec4(Mtime(_t,21.0),6.0, 14.2, 2.8));
    float t4 = TimerInOut(vec4(Mtime(_t,60.0),15.0,45.0,1.0));
    float t5 = TimerInOut(vec4(Mtime(_t,85.0),57.0, 67.0, 3.1));
    float t6 = TimerInOut(vec4(Mtime(_t,123.0),77.0,101.0,10.0));
    float t7 = TimerInOut(vec4(Mtime(_t,50.0),28.0,45.0,3.5));
    float t8 = TimerInOut(vec4(Mtime(_t,20.0),1.0,13.0,3.1));
    float t9 = TimerInOut(vec4(Mtime(_t,200.0),100.0,159.0,0.5));
    float t10 = TimerInOut(vec4(Mtime(_t,45.0),21.0,31.0,0.75));
    float t11 = TimerInOut(vec4(Mtime(_t,30.0),7.0,11.0,0.75));

    // various spiral modifiers applied over time
    float t = Stime(_t,0.4);
    float offset = 25.0 * t1;
	float zoom = 1.0 + (sin(Stime(_t,0.17))*0.5) * t2;
    vec2 capoff = vec2(sin(uv.y*4.0+Stime(_t,0.125))*0.25, sin(uv.x*2.0+Stime(_t,0.25))*0.75) * t3;
    float m = 0.48+sin(Stime(_t,0.15)+uv.y+uv.x)*0.5;
    float trip = 1.0-(m*t4);
    float scmod = sin(Stime(_t,0.176));
    float scount = 5.0+((3.0+(scmod*5.0))*t5);

    float rmod = mix (1.0,sin(Stime(_t,0.1))*2.5,t11);

    // debug... (disable effects)
    //scount = 5.0;
    //offset=0.0;
    //zoom = 1.0;
    //trip = 1.0;
    //capoff = vec2(0.0);

	vec2 dist = spiral(uv, zoom, t, offset, capoff, trip, scount,rmod);

    float distmod = 0.49+sin(dist.x*0.34+dist.y*1.6+sin(Stime(_t,.1)))*0.5;
    dist.x = mix(dist.x, dist.x*distmod, t10);

//	float v2 = smoothstep(0.5-0.1,0.5+0.1,dist.x);
//	float v2 = smoothstep(0.0,0.5,dist.x);
	float v2 = smoothstep(0.0, 1.5*fwidth(dist.x-0.5), dist.x-0.5);

	float v = mix(v2,clamp(dist.x,0.0,3.0), t6);
    //vec3 col = vec3(v*1.0-l/t);
    vec3 col1 = checks(uv);	////vec3(0.0,0.0,0.2);
    vec3 col2 = vec3(1.0,0.6,0.5);

    // rainbow flavour...
	col2 = mix(col2,hsv2rgb_smooth(vec3(fract(iTime*0.2)+dist.x*0.09+dist.y*0.2,0.8,clamp(v,0.35,1.0)))*0.7,t9);

    vec3 col3 = background(uv);	//vec3(0.5,0.7,0.5);
    col1 = mix(col1,col3*0.3, t7);
    col2 = mix(col2,col3*0.9, t8);
    //vec3 col = mix(col1,col2,v*1.0-dist.y/t);

    float finalblend = clamp(v*1.0-dist.y/t,0.0,5.0);
    vec3 col = mix(col1,col2,finalblend);

    // vignette
 	vec2 q = fragCoord/iResolution.xy;
    col *= 0.3 + 0.7*pow(16.0*q.x*q.y*(1.0-q.x)*(1.0-q.y),0.25);

    // utter hack, overlay some coloured circles to show active mods... :)
    if (iMouse.z>0.5)
    {
        uv.xy += 0.05;
        float c = pMod1(uv.y,0.1);
        if (c<-3.0)
        {
            uv.x -= 0.3;
            c = pMod1(uv.x,0.1);
            if (c>=-8.0)
            {
                float dd = length(uv)*2.0;
                dd = 1.0-smoothstep(0.0,0.1,dd);
                vec3 cf = vec3(0.0);
                vec3 cc = hsv2rgb_smooth( vec3((c+8.0)/12.0,1.0,dd));
                if (c<-7.0) cf = cc*t1;
                else if (c<-6.0) cf = cc*t2;
                else if (c<-5.0) cf = cc*t3;
                else if (c<-4.0) cf = cc*t4;
                else if (c<-3.0) cf = cc*t5;
                else if (c<-2.0) cf = cc*t6;
                else if (c<-1.0) cf = cc*t7;
                else if (c<-0.0) cf = cc*t8;
                else if (c<1.0) cf = cc*t9;
                else if (c<2.0) cf = cc*t10;
                else if (c<3.0) cf = cc*t11;
                float _mm = smoothstep(0.0,0.1,length(cf)-0.2);
                col = mix(col,cf,_mm);
           	}
        }
    }


    fragColor = vec4(col, 1.0);
}

// based on www.shadertoy.com/view/Xds3zN Created by inigo quilez - iq/2013
