#define PI_16TH 0.39269908169 
#define HALF_PI 1.57079632679
#define PI 3.14159265359

float sdBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float sdSphere( vec3 p, float s , float a, float b)
{
    mat3 rot1 = mat3(vec3(sin(b),cos(b),0.0), 
                     vec3(cos(b),-sin(b),0.0),
                     vec3(0.0,0.0,1.0));
    mat3 rot2 = mat3(vec3(sin(a),0.0,cos(a)), 
                     vec3(0.0, 1.0, 0.0),
                     vec3(cos(a), 0.0, -sin(a)));
    p -= rot2*rot1*vec3(0.0,0.9,0.0);
    return length(p)-s;
}

float sdTorus( vec3 p, vec2 t , float a)
{
  mat3 rot = mat3(vec3(sin(a),0.0,cos(a)), 
                  vec3(0.0, 1.0, 0.0),
                  vec3(cos(a), 0.0, -sin(a)));

  p = rot*p;
  p -= -vec3( 0.0,0.0, 0.0);
  return length( vec2(length(p.yz)-t.x,p.x) )-t.y;
}

vec2 opU( vec2 d1, vec2 d2 )
{
	return (d1.x<d2.x) ? d1 : d2;
}

void pR(inout vec2 p, float a) {
	p = cos(a)*p + sin(a)*vec2(p.y, -p.x);
}

vec2 map( in vec3 pos )
{
    pos.z += .7;
    float time = 2.0 * iTime;
 	float sphere_size = 0.35;
    vec2 res = vec2(11111111.0,0.0);

    float balls = 11111111.;

    for( int i=0; i<4; i++ )
    {
    	//res = opU( res, vec2( sdTorus(pos, vec2(0.90,0.01), float(i) * PI_16TH ), 25.0 ) );  
        //res = opU(res, vec2( sdSphere(    pos, 0.13 , float(i) * PI_16TH, time + PI_16TH * float(i)), time + float(i)));
        balls = min(balls, sdSphere( pos + vec3(float(i - 2) * sphere_size, 0., 0.), sphere_size / 2. , 0., 0.));
    }
    float pendulum = sin(time * 0.6) * .7;
    //balls = opU(balls, vec2( sdSphere( pos + vec3(2. * sphere_size + sin(pendulum), 0., 1. - cos(pendulum)), sphere_size / 2. , 0., 0.), time));
    balls = min(balls, sdSphere( pos + vec3(2. * sphere_size + sin(pendulum) * .9, (-1. + cos(pendulum)) * .5, 0.), sphere_size / 2. , 0., 0.));
    vec3 cp = pos - vec3(0., 0., 0.875);

    float mount = sdBox( cp, vec3(1., 1., .51) );
    mount = max(mount, -sdBox( cp - vec3(0., .1, 0.), vec3(2., 1., .42) ));
    mount = max(mount, -sdBox( cp - vec3(0., -.1, 0.), vec3(.9, 1., 2.) ));

    vec3 pt = cp;

    pt.yz -= vec2(.65, .3);
    pR(pt.yz, .53);
    mount = min(mount, sdBox( pt - vec3(0., -.1, 0.), vec3(.02, .45, .02) ));
    pt = cp;

    pt.yz -= vec2(.65, -.3);
    pR(pt.yz, -.53);
    mount = min(mount, sdBox( pt - vec3(0., -.1, 0.), vec3(.02, .45, .02) ));
    pt = cp;

    pt.x += sphere_size;
    pt.yz -= vec2(.65, .3);
    pR(pt.yz, .53);
    mount = min(mount, sdBox( pt - vec3(0., -.1, 0.), vec3(.02, .45, .02) ));
    pt = cp;

    pt.x += sphere_size;
    pt.yz -= vec2(.65, -.3);
    pR(pt.yz, -.53);
    mount = min(mount, sdBox( pt - vec3(0., -.1, 0.), vec3(.02, .45, .02) ));
    pt = cp;

    pt.x -= sphere_size;
    pt.yz -= vec2(.65, .3);
    pR(pt.yz, .53);
    mount = min(mount, sdBox( pt - vec3(0., -.1, 0.), vec3(.02, .45, .02) ));
    pt = cp;

    pt.x -= sphere_size;
    pt.yz -= vec2(.65, -.3);
    pR(pt.yz, -.53);
    mount = min(mount, sdBox( pt - vec3(0., -.1, 0.), vec3(.02, .45, .02) ));
    pt = cp;

    pt.x -= sphere_size * 2.;
    pt.yz -= vec2(.65, .3);
    pR(pt.yz, .53);
    mount = min(mount, sdBox( pt - vec3(0., -.1, 0.), vec3(.02, .45, .02) ));
    pt = cp;

    pt.x -= sphere_size * 2.;
    pt.yz -= vec2(.65, -.3);
    pR(pt.yz, -.53);
    mount = min(mount, sdBox( pt - vec3(0., -.1, 0.), vec3(.02, .45, .02) ));
    pt = cp;

    pt.x += sphere_size * 2.;
    pt.x += sin(pendulum) * 0.3;
    pt.yz -= vec2(.65, .3);
    pR(pt.xy, -sin(pendulum));
    pR(pt.yz, .53);
    mount = min(mount, sdBox( pt - vec3(0., -.1, 0.), vec3(.02, .45, .02) ));
    pt = cp;

    pt.x += sphere_size * 2.;
    pt.x += sin(pendulum) * 0.3;
    pt.yz -= vec2(.65, -.3);
