float fbm(float x) {
	float v = 0.0;
	float a = 0.6666;
	float seed = 61.0;
	for (int i = 0; i < NUM_NOISE_OCTAVES; ++i) {
		v += a * foam(seed, x);
        seed += 42.0;
		x = x * 2.0 + seed;
		a *= 0.5;
	}
	return v;
}


float fbm(vec2 x) {
	float v = 0.0;
