vec2 R;

float mass = 1.;
float g = 18.;

void mainImage( out vec4 f, in vec2 u ){
    R = iResolution.xy;
    ivec2 iu = ivec2(u);

    if(iu.x <= int(mem) && iu.y == 0){
        vec4 bA = ch(iChannel0, u); 

        for(int i = 0; i < int(mem); i++){
            if( i != iu.x){
                vec2 subj = ch(iChannel0, vec2(i,0.)).xy;
     			float r = max(length(bA.xy - subj), 10.0);	   

                vec2 dir = normalize(bA.xy - subj);
        		float grav = -(g*2.*mass) / (r*r);

                bA.z += grav*dir.x*dt;
                bA.w += grav*dir.y*dt;  

                bA.x -= step(seperation, r) * stick*dir.x*dt;
                bA.y -= step(seperation, r) * stick*dir.y*dt;
            }
    	}

        if(iMouse.z > 0.){
         	float r = max(length(iMouse.xy - bA.xy), 10.0);
            vec2 dir = normalize(bA.xy - iMouse.xy);
            float ef = -(200.*mass) / r;

            bA.z -= ef*dir.x*dt;
            bA.w -= ef*dir.y*dt;  

            f = vec4(0);
        }

        bA.z -= 0.0015*bA.z;
        bA.w -= 0.0015*bA.w;

        bA.x -= bA.z*dt;
        bA.y -= bA.w*dt;

        bA.x = clamp(bA.x, 10., R.x);
        bA.y = clamp(bA.y, 10., R.y);


    	if(iFrame == 0){
            if(u.x <= mem && iu.y == 0 ){
                bA.xy = hash22(u)*R.xy;
                bA.zw = vec2(0.);
            }
    	}

        f = bA;
    }
    else return;

}

