// the following lines are copied from mattdesl's Quadratic Bezier Stroke (fork)
// https://www.shadertoy.com/view/lts3Df

float det(vec2 a, vec2 b) { return a.x*b.y-b.x*a.y; }

vec2 closestPointInSegment( vec2 a, vec2 b )
{
  vec2 ba = b - a;
  return a + ba*clamp( -dot(a,ba)/dot(ba,ba), 0.0, 1.0 );
}

// From: http://research.microsoft.com/en-us/um/people/hoppe/ravg.pdf
vec2 get_distance_vector(vec2 b0, vec2 b1, vec2 b2) {

  float a=det(b0,b2), b=2.0*det(b1,b0), d=2.0*det(b2,b1); // Ã°ÂâºÂ¼,Ã°ÂâºÂ½,Ã°ÂâºÂ¿(Ã°ÂâÂ)

  if( abs(2.0*a+b+d) < 1000.0 ) return closestPointInSegment(b0,b2);

  float f=b*d-a*a; // Ã°Âââ(Ã°ÂâÂ)
  vec2 d21=b2-b1, d10=b1-b0, d20=b2-b0;
  vec2 gf=2.0*(b*d21+d*d10+a*d20);
  gf=vec2(gf.y,-gf.x); // Ã¢Ëâ¡Ã°Âââ(Ã°ÂâÂ)
  vec2 pp=-f*gf/dot(gf,gf); // Ã°ÂâÂÃ¢â¬Â²
  vec2 d0p=b0-pp; // Ã°ÂâÂÃ¢â¬Â² to origin
  float ap=det(d0p,d20), bp=2.0*det(d10,d0p); // Ã°ÂâºÂ¼,Ã°ÂâºÂ½(Ã°ÂâÂÃ¢â¬Â²)
  // (note that 2*ap+bp+dp=2*a+b+d=4*area(b0,b1,b2))
  float t=clamp((ap+bp)/(2.0*a+b+d), 0.0 ,1.0); // Ã°ÂâÂ¡Ãâ¦
  return mix(mix(b0,b1,t),mix(b1,b2,t),t); // Ã°ÂâÂ£Ã°Âââ= Ã°ÂâÂ(Ã°ÂâÂ¡Ãâ¦)

}

float approx_distance(vec2 p, vec2 b0, vec2 b1, vec2 b2) {
  return length(get_distance_vector(b0-p, b1-p, b2-p));
}

// extracted from mainImage
vec4 overlaySpline(inout vec4 rgba, vec2 uv, vec4 strokeCol, vec2 p1, vec2 p2, vec2 p3, vec3 iResolution){
	float d = approx_distance((1. - uv)*iResolution.xy, p1*iResolution.xy, p2*iResolution.xy, p3*iResolution.xy);
	float thickness = 1.0;
	float a;
	if(d < thickness) {
	  a = 1.;
	} else {
	  a = 1. - smoothstep(d, thickness, thickness+0.5);
	}
    rgba = mix(rgba, strokeCol, a * strokeCol.a); 
    return rgba;
}

