////////////////////////////////////////////////////////////////////////////////
//
// 4D Lattice Slice
//
// Matthew Arcus, mla, 2020
//
// Slices through a 4 dimensional cubic lattice.
// <mouse x>: slice angle
// <mouse-y>: scale
//
////////////////////////////////////////////////////////////////////////////////

const float PI = 3.1415927;

mat4 qmat_left(vec4 q) {
  float x = q.x, y = q.y, z = q.z, t = q.w;
  return mat4( t, z,-y,-x, 
              -z, t, x,-y,
               y,-x, t,-z,
               x, y, z, t );
}

mat4 qmat_right(vec4 q) {
  float x = q.x, y = q.y, z = q.z, t = q.w;
  return mat4( t,-z, y,-x, 
               z, t,-x,-y,
              -y, x, t,-z,
               x, y, z, t );
}

vec4 qmul(vec4 p, vec4 q) {
  vec3 P = p.xyz, Q = q.xyz;
  return vec4(p.w*Q+q.w*P+cross(P,Q),p.w*q.w-dot(P,Q));
}

vec4 qrot(vec4 p, vec4 q, vec4 r) {
  p = qmul(q,p);
  p = qmul(p,r);
  return p;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 uv = (2.0*fragCoord-iResolution.xy)/iResolution.y;
  float size = 5.0;
  float time = 0.1*iTime+0.1;
  float theta = 0.618*time;
  if (iMouse.x > 0.0) {
    size *= exp((2.0*iMouse.y -iResolution.y)/iResolution.y);
    theta = PI*(2.0*iMouse.x-iResolution.x)/iResolution.x;
  }
  uv *= size;
  float tq = time;
  float tr = theta;
  vec4 q = vec4(sin(tq)*vec3(1,0,0),cos(tq));
  vec4 r = vec4(sin(tr)*vec3(0,1,0),cos(tr));
  vec4 p = vec4(uv,0,0);
  mat4 qm = qmat_left(q)*qmat_right(r);
  float ds = fwidth(p.x);
  p = qm*p; // qrot(p,q,r);
  p = mod(p,2.0);
  vec4 dp = fract(p);
  dp = min(dp,1.0-dp);
  p = floor(p);
  int parity = int(dot(p,vec4(1)))%2;

  mat4x2 dm = transpose(mat2x4(qm));
  dp /= vec4(length(dm[0]),length(dm[1]),length(dm[2]),length(dm[3]));
  float d = min(min(dp.x,dp.y),min(dp.z,dp.w));
  vec3 color = vec3(smoothstep(-ds,+ds,parity==0?d:-d));
  color = mix(vec3(1,0,0),color,smoothstep(0.01,0.02+ds,d));
  color = pow(color,vec3(0.4545));
  fragColor = vec4(color,1);
}

#define PI          3.141592654
#define TAU         (2.0*PI)
#define LAYERS      6
#define FBM         3
#define LIGHTNING   3
#define DISTORT     1.4
#define TIME        iTime
#define TTIME       (TAU*TIME)

float wave(float theta, vec2 p) {
  return (cos(dot(p,vec2(cos(theta),sin(theta)))));
}

float noise(vec2 p, float time) {
  float sum = 0.;
  float a = 1.0;
  for(int i = 0; i < LAYERS; ++i)  {
    float theta = float(i)*PI/float(LAYERS);
    sum += wave(theta, p)*a;
    a*=DISTORT;
  }

  return abs(tanh(sum+1.0+0.75*cos(time)));
}

float fbm(vec2 p, float time) {
  float sum = 0.;
  float a = 1.0;
  float f = 1.0;
  for(int i = 0; i < FBM; ++i)  {
    sum += a*noise(p*f, time);
    a *= 2.0/3.0;
    f *= 2.31;
  }

  return 0.45*(sum);
}

vec3 lightning(vec2 pos, float offset) {
  vec3 col = vec3(0.0);
  vec2 f = vec2(0);

  const float w=0.15;

  for (int i = 0; i < LIGHTNING; i++) {
    float time = TIME + 0.5*float(i);   
    float d1 = abs(offset * w / (0.0 + offset - fbm((pos + f) * 3.0, time)));
    float d2 = abs(offset * w / (0.0 + offset - fbm((pos + f) * 2.0, 0.9 * time + 10.0)));
    col += vec3(clamp(d1, 0.0, 1.0) * vec3(0.1, 0.5, 0.8));
    col += vec3(clamp(d2, 0.0, 1.0) * vec3(0.7, 0.5, 0.3));
  }

  return (col);
}

vec3 postProcess(vec3 col, vec2 q) {
  col=pow(clamp(col,0.0,1.0),vec3(0.75)); 
  col=col*0.6+0.4*col*col*(3.0-2.0*col);  // contrast
  col=mix(col, vec3(dot(col, vec3(0.33))), -0.4);  // satuation
  col*=0.5+0.5*pow(19.0*q.x*q.y*(1.0-q.x)*(1.0-q.y),0.7);  // vigneting
  return col;
}

void rot(inout vec2 p, float a) {
  float c = cos(a);
  float s = sin(a);
  p = vec2(p.x*c + p.y*s, -p.x*s + p.y*c);
}

vec3 normal(vec2 p, float time) {
  vec2 v;
  vec2 w;
  vec2 e = vec2(0.00001, 0);

  vec3 n;
  n.x = fbm(p + e.xy, time) - fbm(p - e.xy, time);
  n.y = 2.0*e.x;
  n.z = fbm(p + e.yx, time) - fbm(p - e.yx, time);

  return normalize(n);
}

vec3 dragonEye(vec2 p) {
  vec2 pp = 10.0*p;

  rot(p, -0.75); 
  p *= vec2(1.1/tanh(1.0 + length(p)), 1.0);
  float l = length(p);

  float dd = 0.2 + 0.65*(-0.5 + 1.75*(0.5 + 0.5*cos(3.0*l-TTIME/12.0)))*tanh(1.0/((pow(l, 4.0) + 2.0)));
  dd *= smoothstep(9.0, 12.0, TIME-l*2.0);
  vec3 col = vec3(0.0);
  float f = fbm(pp, TIME*0.1);
  vec3 ld = normalize(vec3(p.x, 0.5, p.y));
  vec3 n = normal(pp, TIME*0.1);
  float diff = max(dot(ld, n), 0.0);
  col += vec3(0.5, 1.0, 0.8)*pow(diff, 20.0)/(0.5+dot(p, p));
  col += lightning(pp, dd);
  col *= pow(vec3(f), vec3(1.5, 5.0, 5.0));
//  col += -0.1+0.3*vec3(0.7, 0.2, 0.4)*vec3(tanh((pow(0.6/f, 10))));
  return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 q = fragCoord.xy/iResolution.xy;    
  vec2 p = -1.0 + 2.0*q;

  p.x *= iResolution.x/iResolution.y;    

  vec3 col = dragonEye(p);

  col = postProcess(col, q);

  col *= smoothstep(0.0, 4.0, TIME);

  fragColor = vec4(col, 1.0);  
}

float radio = 1000.0;
float rimSize = 4.0;
vec2 handView = vec2(10.0,100.0);	
float scale = 0.4;		

vec2 curvatureGenerator(vec2 toPow,  float dis){
    float x = dis/radio;
    return toPow*(1.0-x)*exp(-2.0*x*x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    fragColor = texture(iChannel0, uv).rgba;

    vec2 center = iMouse.xy;

    float dis = distance(fragCoord.xy, center);

    vec2 disV = fragCoord.xy - center;

    if(dis > radio && dis < radio + rimSize){
    	fragColor = vec4(0.0, 0.0, 0.0, 1.0);
    } 

    if(abs(disV.x) < (handView.x / 2.0) && abs(disV.y + radio) < ((handView.y))&& dis >= radio + rimSize)
    {
    	fragColor = vec4(0.0, 0.0, 0.0, 1.0);
    }

    if(dis < radio){
        //dis = distance(fragCoord.xy, center + radio*0.5*vec2(cos(iTime),sin(iTime)));

        vec2 trueUV = (fragCoord.xy - (curvatureGenerator(disV,dis) *scale) ) / iResolution.xy;
    	fragColor = texture(iChannel0, trueUV).rgba;
    }
}

float smin(float a, float b, float k) {
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

float snowhead(vec3 p, vec3 off) {
    float body = length(p - off) - 1.0;
    float head = length(p - vec3(0.0, 1.4, 0.0) - off) - 0.6;
    return smin(body, head, 0.2);
}

float carrot(vec3 p, vec3 off) {
    p = p - off;
    if (length(p) > 1.0) {
        return 1.0;
    }
    float rad = radians(75.0);
    float q = length(p.xy);
    return dot(vec2(sin(rad), cos(rad)), vec2(q, p.z));
}

float snoweye(vec3 p, vec3 off) {
    float left = length(p - vec3(-0.25, 0.0, 0.0) - off) - 0.1;
    float right = length(p - vec3(0.25, 0.0, 0.0) - off) - 0.1;
    return min(left, right);
}

float sol(vec3 p) {
    return p.y;
}

float rect(vec3 p, vec3 b, vec3 off) {
    vec3 q = abs(p - off) - b;
    return length(max(q, 0.0)) + min(max(q.x, max(q.y, q.z)), 0.0) - 0.1;
}

float window(vec3 p, vec3 off) {
    return max(rect(p, vec3(2.0, 2.0, 0.1), off),
        -rect(p, vec3(1.8, 1.7, 1.0), off + vec3(0.0, 0.0, 0.1)));
}

float wall(vec3 p, vec3 windowOff) {
    if (p.z < -3.0) {
        return 1.0;
    }
    float x = abs(p.x + 3.0) - 0.001;
    float z = abs(p.z + 3.0) - 0.001;
    return max(min(x, z),
        -rect(p, vec3(2.0, 2.0, 1.0), windowOff));
}

float cubic(vec3 p, vec3 off) {
    float sol = rect(p, vec3(3.0, 0.01, 3.0), off);
    float plafond = smin(sol, rect(p, vec3(3.0, 0.01, 3.0), off + vec3(0.0, 6.0, 0.0)), 0.1);
    float wall = smin(plafond, rect(p, vec3(3.0, 6.0, 0.01), off + vec3(0.0, 0.0, -3.0)), 0.1);
    wall = smin(wall, rect(p, vec3(0.01, 6.0, 3.0), off + vec3(-3.0, 0.0, 0.0)), 0.1);
    wall = smin(wall, rect(p, vec3(0.01, 6.0, 3.0), off + vec3(3.0, 0.0, 0.0)), 0.1);
    return wall;
}

float lamp(vec3 p, vec3 off) {
    return rect(p, vec3(0.5, 0.1, 0.4), off);
}

vec2 map(vec3 p) {
    float closest = 1000.0;
    float id = -1.0;

    float dist = sol(p);
    if (dist < closest) { closest = dist; id = 0.5; }

    dist = snowhead(p, vec3(0.0, 0.5, 0.0));
    if (dist < closest) { closest = dist; id = 1.5; }

    dist = snoweye(p, vec3(0.0, 2.2, 0.5));
    if (dist < closest) { closest = dist; id = 2.5; }

    dist = carrot(p, vec3(0.0, 1.9, 1.0));
    if (dist < closest) { closest = dist; id = 3.5; }

    dist = wall(p, vec3(0.0, 3.8, -4.0));
    if (dist < closest) { closest = dist; id = 4.5; }

    dist = window(p, vec3(0.0, 3.8, -3.1));
    if (dist < closest) { closest = dist; id = 5.5; }

    dist = cubic(p, vec3(1.0, 0.0, -6.15));
    if (dist < closest) { closest = dist; id = 6.5; }

    dist = lamp(p, vec3(1.0, 6.0, -6.15));
    if (dist < closest) { closest = dist; id = 7.5; }

    return vec2(closest, id);
}

vec3 calcNormal(vec3 p) {
    const float epsilon = 0.001;
    return normalize(vec3(
        map(p).x - map(vec3(p.x - epsilon, p.yz)).x,
        map(p).x - map(vec3(p.x, p.y - epsilon, p.z)).x,
        map(p).x - map(vec3(p.xy, p.z - epsilon)).x
    ));
}

vec2 intersect(vec3 ro, vec3 rd) {
    vec2 dv = vec2(0.0, -1.0);

    for (int i = 0; i < 300; i++) {
        vec2 distId = map(ro + rd * dv.x);
        if (distId.x <= 0.001) {
            dv.y = distId.y;
            break;
        }
        dv.x += distId.x;
    }
    return dv;
}

vec3 wallColor(vec3 p) {
    const vec3 base = vec3(0.8, 0.6, 0.7);
    vec3 f = fract(p);
    float clamped = length(clamp(p, 0.0, 1.0));
    return base * (f.y + 0.4);
}

vec3 carrotColor(vec3 p) {
    const vec3 base = vec3(1.5, 0.3, 0.0);
    vec3 f = fract(p * 10.0);
    vec3 color = base * (1.0 - pow(f.z, 12.0) * 0.3);
    return color;
}

vec3 getColor(vec3 p, float id) {
    if (id <= -0.5) { return vec3(0.0, 0.0, 0.0); }
    if (id <= 1.0) { return vec3(1.0, 1.0, 1.0); }
    if (id <= 2.0) { return vec3(1.0, 1.0, 1.0); }
    if (id <= 3.0) { return vec3(0.1, 0.1, 0.1); }
    if (id <= 4.0) { return carrotColor(p); } // carrot
    if (id <= 5.0) { return wallColor(p); } // wall
    if (id <= 6.0) { return vec3(0.5, 0.2, 0.1); }
    if (id <= 7.0) { return vec3(2.2, 2.2, 2.2); }
    if (id <= 8.0) { return vec3(100.0, 100.0, 100.0); }
    return vec3(1.0, 0.0, 0.0);
}

float softShadow(vec3 ro, vec3 rd, float k) {
    float res = 1.0;
    float depth = 0.0001;
    vec2 dv = vec2(1.0, -1.0);

    for (int i = 0; i < 50; i++) {
        dv = map(ro + rd * depth);
        res = min(res, smoothstep(0.0, 1.0, k * dv.x / depth));
        if (res < 0.0001) { break; }
        depth += clamp(dv.x, 0.01, 0.06);
    }
    return clamp(res, 0.0, 1.0);
}

float directLighting(vec3 p, vec3 n, vec3 lightPos, vec3 lightDir) {
    vec3 u = normalize(cross(lightDir, vec3(0.0, 1.0, 0.0)));
    vec3 v = cross(u, lightDir);
    float shadowIntensity = softShadow(p + 0.001 * n, lightDir, 10.0);

    vec3 toLight = lightPos - p;
    float attenuation = smoothstep(0.9, 1.0, dot(normalize(toLight), lightDir));

    return attenuation * shadowIntensity;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord.xy / iResolution.xy * 2.0 - 1.0;
    float aspect = iResolution.x / iResolution.y;
    uv.x *= aspect;

    const float r = 2.0;
    vec3 ro = vec3(r * (sin(iTime * 0.3) * 0.5 + 0.5) * 0.9 + r, 3.0 * (sin(iTime * 0.5) * 0.1 + 0.9), r * (cos(iTime * 0.3) * 0.5 + 0.5) + r);
    vec3 center = vec3(0.0, 2.0 + sin(iTime * 0.2) * 0.8 + 0.2, 0.0);
    vec3 front = normalize(vec3(center - ro));
    vec3 right = normalize(cross(front, vec3(0.0, 1.0, 0.0)));
    vec3 up = normalize(cross(right, front));
    mat4 lookAt = mat4(
        vec4(right, 0.0),
        vec4(up, 0.0),
        vec4(front, 0.0),
        vec4(0.0, 0.0, 0.0, 1.0)
    );
    vec3 rd = normalize(vec3(lookAt * vec4(uv, 2.0, 1.0)));

    vec2 result = intersect(ro, rd);
    vec3 pos = ro + rd * result.x;
    vec3 n = calcNormal(pos);

    vec3 lightPos = vec3(-1.5, 5.5, -4.65);
    vec3 lightDir = normalize(lightPos);
    vec3 hal = normalize(lightDir - rd); // WHAT?

    float ambient = 1.0;
    float diffuse = max(dot(lightDir, n), 0.0);
    float attenuation = 0.1 + 0.9 * smoothstep(0.9, 1.0, dot(normalize(lightPos - pos), lightDir));
    float back = max(dot(n, normalize(vec3(-lightDir.x, 0.0, -lightDir.z))), 0.0);
    float fr = pow(clamp(1.0 + dot(n , rd), 0.0, 1.0), 3.0);
    float dome = clamp(n.y * 0.8 + 0.2, 0.0, 1.0);
    float sol = clamp(-n.y * 0.8 + 0.2, 0.0, 0.0) * pow(clamp(1.0 - pos.y, 0.0, 1.0), 2.0);
    float specular = pow(clamp(dot(n, hal), 0.0, 1.0), 8.0);
    float shadow = directLighting(pos, n, lightPos, lightDir);

    vec3 light = vec3(0.0, 0.0, 0.0);
    light += ambient * vec3(0.03, 0.02, 0.01) * attenuation;
    light += diffuse * vec3(1.01, 1.0, 1.0) * pow(vec3(shadow), vec3(1.0, 1.0, 1.0)) * attenuation;
    light += back * vec3(0.42, 0.20, 0.1) * attenuation;
    light += fr * vec3(3.0, 3.0, 3.0) * attenuation * (0.25 + 0.25 * diffuse);
    light += dome * vec3(0.15, 0.1, 0.1) * attenuation;
    light += sol * vec3(0.2, 0.2, 0.2) * attenuation;
    light += specular * vec3(0.5, 0.5, 0.5) * attenuation;

    vec3 color = getColor(pos, result.y) * light;
    color = pow(color, vec3(0.4545));

    fragColor = vec4(color, 1.0);
}

// j= sub component, for 8x8 matrix there are 4 sub 4x4 matrices
mat4 getWeightSubMatrix(int layer, int j) {
    mat4 m;
    for (int i=0; i<N_HIDDEN; i++) {
	    m[i] = 2.0 * texelFetch(iChannel0, ivec2(layer, j * (N_HIDDEN / 4) + i), 0) - 1.0;
    }
	return m;
}

// j= sub component, for 8x1 bias there are 2 sub 4x1 vectors
vec4 getSubBias(int layer, int j) {
    vec4 b;
    b = 2.0 * texelFetch(iChannel0, ivec2(layer, WEIGHTS_END_Y + j), 0) - 1.0;
	return b;
}

vec4 hash4( vec3 p )
{
    vec4 q = vec4( dot(p,vec3(127.1,311.7,256.8)), 
				   dot(p,vec3(269.5,183.3,012.5)), 
				   dot(p,vec3(419.2,371.9,167.1)),
                   dot(p,vec3(269.5,183.3,918.1))
                 );
	return fract(sin(q)*43758.5453);
}

vec4 noise( in vec2 x )
{
    vec2 p = floor(x);
    vec2 f = fract(x);

	vec4 color = vec4(0.0);
	float wt = 0.0;
    // Sum contributions from neighboring pixels
    for( int j=-2; j<=2; j++ )
    for( int i=-2; i<=2; i++ )
    {
        vec2 g = vec2(float(i), float(j));
		vec4 value = hash4( vec3(p + g, 0.0) );
		vec2 r = g - f;
		float d = dot(r,r);
		float ww = 1.0-smoothstep(0.0,1.414,sqrt(d));
		color += value*ww;
		wt += ww;
    }
    //return color;
	// Normalize weight
    return 2.0*color/wt -1.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv = 2. * uv - 1.;
    uv.x *= iResolution.x / iResolution.y;

    vec2 fr = fract(uv * 8.);

    // Input features
    vec4 v0 = 0.3*vec4(
        uv.x,
        uv.y,
        2.0 - 4.*length(uv + vec2(0.0, 0.0)),
        sin(3.14159*uv.x)
    );
    float scale = 3.;
    vec4 v1 = 1.0*noise(scale * uv + vec2(1.0*iTime, 0.0));

    // Weight and bias factors
    float k_w = 1.8;
    float k_b = 0.45;
    for (int layer=0; layer<N_LAYERS; layer++) {

        vec4 v00 = getWeightSubMatrix(layer, 0) * v0;
        vec4 v11 = getWeightSubMatrix(layer, 1) * v1;
        vec4 v10 = getWeightSubMatrix(layer, 2) * v0;
        vec4 v01 = getWeightSubMatrix(layer, 3) * v1;

        v0 = k_w * (v00 + v01) + k_b * getSubBias(layer, 0);
        v1 = k_w * (v10 + v11) + k_b * getSubBias(layer, 1);

        if (layer == N_LAYERS - 1) {
            v0 *= 0.5;
            v1 *= 0.5;
        }

        // Apply activation
        v0 = tanh(v0);
        v1 = tanh(v1);
    }

    fragColor.rgb = 0.5 + 0.5 * (v0 + v1).rgb;

    // Debug visualization
    if (fragCoord.x <= float(DATA_WIDTH) * DEBUG_SCALE && fragCoord.y <= float(DATA_HEIGHT) * DEBUG_SCALE) {
	    fragColor = texture(iChannel0, fragCoord.xy / iResolution.xy / DEBUG_SCALE);
    }
}

