/*
Buffer A is responsible for generating a number of random weight matrices and bias vectors.

They are stored packed in the 4 channels of this output texture.
Since only some pixels are used, discard on other pixels (may not be any more efficient tho)
*/

// Standard shader RNG
vec4 hash4( vec3 p )
{
    vec4 q = vec4( dot(p,vec3(127.1,311.7,256.8)), 
				   dot(p,vec3(269.5,183.3,012.5)), 
				   dot(p,vec3(419.2,371.9,167.1)),
                   dot(p,vec3(269.5,183.3,918.1))
                 );
	return fract(sin(q)*43758.5453);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    bool weight = int(fragCoord.x) < DATA_WIDTH && int(fragCoord.y) < WEIGHTS_END_Y;
    bool bias = int(fragCoord.x) < DATA_WIDTH 
        && int(fragCoord.y) >= BIAS_START_Y
        && int(fragCoord.y) < BIAS_END_Y;

    // This pixel is unused
    if (!weight && !bias) {
        fragColor = vec4(0.0);
        discard;
    }

    // Interpolate between each weight breakpoint here
    float t = iTime * 0.25;
    vec4 a = hash4(vec3(fragCoord, floor(t)));
    vec4 b = hash4(vec3(fragCoord, ceil(t)));
    float k = fract(t);
    fragColor = mix(a, b, smoothstep(0.0, 1.0, k));
}

