float blurRadius;


#define TAU 6.28318530718
#define PI TAU/2.
// Dave Hoskins hash
float hash13(in vec3 p3) {
	p3  = fract(p3 * .1031);
    p3 += dot(p3, p3.yzx + 19.19);
    return fract((p3.x + p3.y) * p3.z);
}

// https://www.shadertoy.com/view/4sS3zz
float sdEllipse(vec2 p, in vec2 ab) {
    if (abs(ab.x-ab.y) < .00001) return length(p) - ab.x;  // haaax due to numerical stability issues.

	p = abs(p); 
    if( p.x > p.y ){ p=p.yx; ab=ab.yx; }

	float l = ab.y*ab.y - ab.x*ab.x;

    float m = ab.x*p.x/l; 
	float n = ab.y*p.y/l; 
	float m2 = m*m;
	float n2 = n*n;

    float c = (m2 + n2 - 1.0)/3.0; 
	float c3 = c*c*c;

    float q = c3 + m2*n2*2.0;
    float d = c3 + m2*n2;
    float g = m + m*n2;

    float co;

    if (d<0.0) {
        float h = acos(q/c3)/3.0;
        float s = cos(h);
        float t = sin(h)*sqrt(3.0);
        float rx = sqrt( -c*(s + t + 2.0) + m2 );
        float ry = sqrt( -c*(s - t + 2.0) + m2 );
        co = ( ry + sign(l)*rx + abs(g)/(rx*ry) - m)/2.0;
    } else {
        float h = 2.0*m*n*sqrt( d );
        float s = sign(q+h)*pow( abs(q+h), 1.0/3.0 );
        float u = sign(q-h)*pow( abs(q-h), 1.0/3.0 );
        float rx = -s - u - c*4.0 + 2.0*m2;
        float ry = (s - u)*sqrt(3.0);
        float rm = sqrt( rx*rx + ry*ry );
        co = (ry/sqrt(rm-rx) + 2.0*g/rm - m)/2.0;
    }

    float si = sqrt( 1.0 - co*co );

    vec2 r = ab * vec2(co,si);

    return length(r-p) * sign(p.y-r.y);
}
float sdEllipsoidApproximated( in vec2 p, in vec2 r ) {
    float k0 = length(p/r);
 	return length(p/r) - 1.;
    float k1 = length(p/(r*r));
    return k0*(k0-1.0)/k1;
}


// How many cells around the central_cell we check for ellipses.
#define SAMPLE_RADIUS 2

float monochrome(vec2 uv) {

    const float cell_wd = 5.5;
    vec2 central_cell_center = floor((uv+.5*cell_wd) / cell_wd) * cell_wd;

	float hashTime = iTime + 100.;  // Don't want thing to be synchonous when the shader starts    

    int j;
    float acc = 0.;
    for (int i = -SAMPLE_RADIUS; i<=SAMPLE_RADIUS; ++i) {
        for (j = -SAMPLE_RADIUS; j<=SAMPLE_RADIUS; ++j) {
            vec2 cell_center = central_cell_center + cell_wd*vec2(ivec2(i,j));

            vec2 cell_hash_pos = cell_center + vec2(1030., 0);
            float yaw = TAU * hash13(vec3(cell_hash_pos, 2.));
            float angular_speed = 3. + hash13(vec3(cell_hash_pos, 3.));
            vec2 angular_vel = angular_speed * vec2(cos(yaw), sin(yaw));
            float pitch = length(angular_vel) * hashTime * 0.1;
            vec2 a = normalize(angular_vel);  // axis of rotation - major axis of projected ellipse.
            vec2 b = vec2(-a.y, a.x) * cos(pitch);  // minor axis
            vec2 ab = vec2(length(a), length(b));
            mat2 rot = mat2(a.x, a.y,
                            -a.y, a.x);

            vec3 normal = vec3(0.0, sin(pitch), cos(pitch));
            mat3 rot3 = mat3(a.x, a.y, 0.,
                             -a.y, a.x, 0.,
                             0., 0., 1.);
            normal = transpose(rot3) * normal;
            const vec3 LIGHT_DIR = normalize(vec3(1.5, -1.0, 2.0));
            float light_per_area = 
                .2 + // ambient
                50.0 * smoothstep(.98, .99, abs(dot(normal, LIGHT_DIR)));  // cheap (wrong-ish) specular
            float total_light = light_per_area * ab.x * ab.y;  // total amount of light emitted by this object towards all pixels.

            // Have a random offset from the cell_center.
            vec2 ellipse_center = cell_center + (.5*cell_wd - ab.x) * vec2(
            	sin(hashTime * (.01 + .06*hash13(vec3(cell_hash_pos, 4.)))),
                cos(hashTime * (.01 + .06*hash13(vec3(cell_hash_pos, 4.))))
            );
			vec2 ellipse_uv = uv - ellipse_center;   

            vec2 extended_ab = ab + vec2(blurRadius);
            //float sd = sdEllipse(transpose(rot)*inner_uv, extended_ab);
            //float sd = sdEllipsoidApproximated(transpose(rot)*inner_uv, extended_ab);
            float sd = length(transpose(rot)*ellipse_uv / extended_ab) - 1.;  // Signed distance approximation only accurate for values near zero.
            float proportional_area = extended_ab.x * extended_ab.y;
            float edge_blurryness = 0.1/length(extended_ab);
            float is_in_ellipse = smoothstep(edge_blurryness, -edge_blurryness, sd);
            acc += is_in_ellipse * (total_light / proportional_area);

            // Debug major/minor axes.
            //if (abs(dot(uv-ellipse_center, normalize(b))) <.01 && length(uv-ellipse_center) < 1.) acc += .9;
            //if (abs(dot(uv-ellipse_center, normalize(a))) <.01 && length(uv-ellipse_center) < length(b))  acc += .9;

            acc += 1.-smoothstep(0., .1, abs(length(uv) - blurRadius));
        }
    }
	return acc;
}

vec2 project(vec2 pixel) {
    vec2 uv = pixel/iResolution.xy - vec2(0.5);
    uv.x *= iResolution.x / iResolution.y;
    return uv *50.;
}


// antialiasing
float sampleSubpixel(vec2 pixels) {
	const int size = 0; 
    const float numContrb = float(size+1)*float(size+1);
	float disp = 1.0 / (float(size) + 2.0);
	float contrb = 0.0;
	for (int j = -size; j <= size; j++) {
		for (int i = -size; i <= size; i++) {
			contrb += monochrome(project(
                pixels + vec2(float(i) * (disp / 3.0), float(j) * disp)
            ));
		}
	}
	return contrb / numContrb;
}


