vec3 rotateY(vec3 p, float a) 
{
    return vec3(cos(a) * p.x + sin(a) * p.z, p.y, -sin(a) * p.x + cos(a) * p.z);
}

vec3 rotateX(vec3 p, float amt) 
{
    return vec3(p.x, cos(amt) * p.y - sin(amt) * p.z, sin(p.y) + cos(p.z));
}

// from https://www.shadertoy.com/view/wdSGWK
float shadowMap3D(vec3 pos)
{
    float t = plane(pos, FLOOR_VEC);
    t = min(t, box(rotateY(pos + LONG_BOX_POS, 27.5 * PI_OVER_180), LONG_BOX_SCALE));
    t = min(t, sphere(pos, 2.0, SPHERE_POS));
    t = min(t, torus(pos + TORUS_POS, TORUS_RADIUS));
    return t;
}

// from https://www.shadertoy.com/view/wdSGWK
float softShadow(vec3 dir, vec3 origin, float min_t, float k) 
{
    float res = 1.0;
    float t = min_t;
    for(int i = 0; i < RAY_STEPS; ++i) 
    {
        float m = shadowMap3D(origin + t * dir);
        if(m < 0.0001) {
            return 0.0;
        }
        res = min(res, k * m / t);
        t += m;
    }
    return res;
}

// from https://www.shadertoy.com/view/wdSGWK
float shadow(vec3 dir, vec3 origin, float min_t)
{
    return softShadow(dir, origin, min_t, 24.0);
}

float fiveTapAO(vec3 p, vec3 n, float k) 
{
    float aoSum = 0.0;
    for(float i = 0.0; i < 5.0; ++i)
    {
        float coeff = 1.0 / pow(2.0, i);
        aoSum += coeff * (i * AO_DIST - shadowMap3D(p + n * i * AO_DIST));
    }

    return 1.0 - k * aoSum;
}

// from https://www.shadertoy.com/view/wdSGWK
float subsurface(vec3 lightDir, vec3 normal, vec3 viewVec, float thickness)
{
    vec3 scatteredLightDir = lightDir + normal * DISTORTION;
    float lightReachingEye = pow(clamp(dot(viewVec, -scatteredLightDir), 0.0, 1.0), GLOW) * BSSRDF_SCALE;
    float attenuation = 1.0;
    #if ATTENUATION
    attenuation = max(0.0, dot(normal, lightDir) + dot(viewVec, -lightDir));
    #endif
	float totalLight = attenuation * (lightReachingEye + AMBIENT) * thickness;
    return totalLight;
}

void sceneMap3D(vec3 pos, out float t, out int objId, vec3 lightPos)
{
    // floor
    t = plane(pos, FLOOR_VEC);  
    objId = FLOOR_ID;  

    float t2;
    if((t2 = box(rotateY(pos + LONG_BOX_POS, 27.5 * PI_OVER_180), LONG_BOX_SCALE)) < t)  // long bix
    {
        t= t2;
        objId = LONG_BOX_ID;
    }
    if((t2 = sphere(pos, 2.0, SPHERE_POS)) < t)
    {
        t= t2;
        objId = SPHERE_ID;
    }
    if((t2 = sphere(pos, 1.0, lightPos)) < t)
    {
        t= t2;
        objId = LIGHT_ID;
    }
    if((t2 = torus(pos + TORUS_POS, TORUS_RADIUS)) < t)
    {
        t= t2;
        objId = TORUS_ID;
    }
}

float sceneMap3D(vec3 pos, vec3 lightPos)
{
    float t = plane(pos, FLOOR_VEC);
    t = min(t, box(rotateY(pos + LONG_BOX_POS, 27.5 * PI_OVER_180), LONG_BOX_SCALE));
    t = min(t, sphere(pos, 2.0, SPHERE_POS));
    t = min(t, sphere(pos, 1.0, lightPos));
    t = min(t, torus(pos + TORUS_POS, TORUS_RADIUS));
    return t;
}

void march(vec3 origin, vec3 dir, out float t, out int objId, vec3 lightPos)
{
    t = 0.001;
    for(int i = 0; i < RAY_STEPS; ++i)
    {
        vec3 pos = origin + t * dir;
        float m;
        sceneMap3D(pos, m, objId, lightPos);
        if(m < 0.01)
        {
            return;
        }
        t += m;
    }

    t = -1.0;
    objId = -1;
}

vec3 computeMaterial(int objId, vec3 p, vec3 n, vec3 light, vec3 view, float thick)
{
    switch(objId) 
    {
    case FLOOR_ID:  // floor
    {
        return vec3(1.0, 0.95, 0.85) * shadow(light, p, 0.1);
        break;
    }
    case LONG_BOX_ID:  // Cuboid, the long box
    {
        vec3 longBoxCol = vec3(1.0, 0.4, 0.0);
        vec3 color = LIGHT_COL * longBoxCol * max(0.0, dot(n, light)) * shadow(light, p, 0.1);
        return color + longBoxCol * subsurface(light, n, view, thick) * LIGHT_COL;
        break;
    }
    case SPHERE_ID:  // Cube, the short box
    {
        vec3 sphereCol = vec3(0.0, 1.0, 1.0);
        vec3 color = LIGHT_COL * sphereCol * max(0.0, dot(n, light)) * shadow(light, p, 0.1);
        return color + sphereCol * subsurface(light, n, view, thick) * LIGHT_COL;
        break;
    }
    case LIGHT_ID:  // Light
    {
        return LIGHT_COL;
        break;
    }   
    case TORUS_ID:  // Torus
    {
        vec3 torusCol = vec3(0.9, 0.2, 0.9);
        vec3 color = LIGHT_COL * torusCol * max(0.0, dot(n, light)) * shadow(light, p, 0.1);
        return color + torusCol * subsurface(light, n, view, thick) * LIGHT_COL;
        break;
    }
    default:
        return vec3(0.0);
        break;
    }

    return vec3(1.0);
}

vec3 computeNormal(vec3 pos, vec3 lightPos)
{
    vec3 epsilon = vec3(0.0, 0.001, 0.0);
    return normalize(vec3(sceneMap3D(pos + epsilon.yxx, lightPos) - sceneMap3D(pos - epsilon.yxx, lightPos),
                          sceneMap3D(pos + epsilon.xyx, lightPos) - sceneMap3D(pos - epsilon.xyx, lightPos),
                          sceneMap3D(pos + epsilon.xxy, lightPos) - sceneMap3D(pos - epsilon.xxy, lightPos)));
}

Intersection sdf3D(vec3 dir, vec3 eye, vec3 lightPos)
{	
    float t;
    int hitObj;

    march(eye, dir, t, hitObj, lightPos);

    vec3 isect = eye + t * dir;
    vec3 nor = computeNormal(isect, lightPos);
    vec3 lightDir = normalize(lightPos - isect);
    float thickness = fiveTapAO(isect, -nor, FIVETAP_K);

    vec3 surfaceColor = computeMaterial(hitObj, isect, nor, lightDir, normalize(eye - isect), thickness);
    return Intersection(t, surfaceColor, isect, hitObj);
}

void raycast(vec2 uv, out vec3 dir, out vec3 eye, out vec3 ref)
{
    eye = EYE_POS;
    ref = REF_POS;

    float len = tan(3.14159 * 0.125) * distance(eye, ref);
    vec3 H = normalize(cross(vec3(0.0, 1.0, 0.0), ref - eye));
    vec3 V = normalize(cross(H, eye - ref));
    V *= len;
    H *= len * float(iResolution.x) / float(iResolution.y);
    vec3 p = ref + uv.x * H + uv.y * V;
    dir = normalize(p - eye);
} 

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 lightPos = rotateY(vec3(0, 5.0, -5.0), sin(iTime / 150.0) * 180.0);

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    // [-1, 1]
    vec2 uv2 = 2.0 * uv - vec2(1.0);

    vec3 dir, eye, ref;
    raycast(uv2, dir, eye, ref);
    Intersection intersection = sdf3D(dir, eye, lightPos);

    // Output to screen
    fragColor = vec4(intersection.color, 1.0);
}

