float PI = 3.1415927;

// Keep track of scaling factor
void invert(inout vec2 z, inout float ds) {
  float k = 1.0/dot(z,z);
  z *= k;
  ds *= k;
}

void invert(inout vec2 z, float r2, inout float ds) {
  float k = r2/dot(z,z);
  z *= k;
  ds *= k;
}

// (a,b,c,d) => (a,a+b,c,c+d);
mat2 L = mat2(1,1,0,1); // {b+=a; d+=c;}
mat2 R = mat2(1,0,1,1); // {a+=b; c+=d;}

bool alert = false;

vec2 color(vec2 z, float ds) {
  bool parity = false;
  for(int i = 0; i < 60; i++) {
    z.x = fract(z.x); // Unit grid
    if (z.x > 0.5) {
      z.x = 1.0-z.x; // Reflected
      parity = !parity;
    }
    if (dot(z,z) >= 1.0) break;
    // Unit circle inversion
    invert(z,ds);
    parity = !parity;
  }
  // ds = scale factor = pixel width (more or less)
  float c = 0.0;
  float d = min(z.x,0.5-z.x);
  // d = |z|-1 = sqrt(z.z) - 1 = 0.5*z.z - 1 (approx)
  float d1 = 0.5*(dot(z,z) - 1.0);
  d = min(d,d1);
  if (d < ds) {
    c = 0.5 - 0.5*d/ds;
  }
  //c = min(max(c,2.0*ds),0.5); // Grey out highly magnified areas
  if (parity) c = 1.0 - c;
  return vec2(c,d1);
}

bool key(int code) {
  return texelFetch(iChannel3, ivec2(code,2),0).x != 0.0;
}

const int CHAR_A = 65;
const int CHAR_B = 66;
const int CHAR_C = 67;
const int CHAR_D = 68;
const int CHAR_F = 70;
const int CHAR_H = 72;
const int CHAR_J = 74;
const int CHAR_L = 76;
const int CHAR_R = 82;
const int CHAR_S = 83;
const int CHAR_T = 84;
const int CHAR_U = 85;
const int CHAR_V = 86;
const int CHAR_Z = 90;
const int KEY_LEFT = 37;
const int KEY_RIGHT = 39;
const int KEY_UP = 38;
const int KEY_DOWN = 40;

void mainImage(out vec4 fragColor, vec2 fragCoord) {
  vec2 z = 2.0 * (fragCoord - iResolution.xy * 0.5)/iResolution.y;
  float ds = 1.0/iResolution.y;

  vec3 col;
  float r2 = dot(z,z);
  bool halfplane = key(CHAR_H);
  if (halfplane || r2 < 1.0) {
    // Map to halfplane,
    if (halfplane) {
      z.y += 1.0;
    } else {
      z.y = -z.y;
      z.y -= 1.0;
      invert(z,2.0,ds);
      z.y += 1.0;
    }
    // Construct our Mobius transform starting from the identity.
    mat2 T = mat2(1,0,0,1);
    // L and R preserve det(T) = 1. Stern-Brocot connection here.
    T = L*R*R*L*T; // (3,2,4,3)
#if 0
    if (a+d<=2) {
      // treat null rotations specially
      if (c>0) {
        z.x += 0.5;
      } else {
        z.x = 0.5 - z.x;
      }
      invert(z,ds);
      z.x -= 0.1 * iTime;
      vec2 cdata = color(z,ds);
      fragColor = vec4(cdata.x,1.0);  // gamma correction suggested by FabriceNeyret2
      fragColor = pow(fragColor, vec4(1./2.2));
      return;
    }
#endif
    // Fixed points are (a-d +- sqrt(disc))/(2c)
    // Note that ad-bc = 1, so we get a simple form for the discriminant.
    float a = T[0].x, b = T[1].x, c = T[0].y, d = T[1].y;
    float disc = sqrt((a+d)*(a+d)-4.0);
    float fixed0 = (a-d+disc) / (2.0*c); // Map to 0
    float fixed1 = (a-d-disc) / (2.0*c); // Map to infinity
    float inverse_diff = float(c) / disc; 
    float period = 2.0*log(0.5*(a+d+disc)); // Period - where does this come from?

    vec2 z0 = z;
    float k = 1.0;
    if (!key(CHAR_B)) k *= 0.8+0.2*color(z,ds).x; // Show unshifted map

    // Conjugate dilation with map of fixed points to (0,inf).
    z.x -= fixed0;
    invert(z,ds);
    z.x += inverse_diff;

    float t = 0.5*iTime;
    t = mod(t,period);
    z *= exp(t); ds *= exp(t);

    z.x -= inverse_diff;
    invert(z,ds);
    z.x += fixed0;

    vec2 cdata = color(z,ds);
    k *= 0.3+0.7*cdata.x;
    col = vec3(1);
    if (!key(CHAR_T)) col *= k;
    if (!key(CHAR_R)) col = mix(vec3(1,0,0),col,smoothstep(-0.05,+0.05,cdata.y));
    if (!key(CHAR_F)) {
      float d = distance(z0,vec2(fixed0,0));
      d = min(d,distance(z0,vec2(fixed1,0)));
      col = mix(vec3(1,0,0),col,smoothstep(0.0,0.05,d));
    }
  } else {
    col = vec3(1,1,0.75);
  }
  if (alert) col.r = 1.0;
  fragColor = vec4(pow(col, vec3(0.4545)),1);  // gamma correction suggested by FabriceNeyret2
}

const vec4 FLOOR_VEC       = vec4(0.0, 1.0, 0.0, 2.5);
const vec4 LEFT_WALL_VEC   = vec4(1.0, 0.0, 0.0, 5.0);
const vec4 RIGHT_WALL_VEC  = vec4(-1.0, 0.0, 0.0, 5.0);
const vec4 BACK_WALL_VEC   = vec4(0.0, 0.0, -1.0, 5.0);
const vec4 CEILING_VEC     = vec4(0.0, -1.0, 0.0, 7.5);
const vec3 LONG_BOX_POS    = vec3(2.1, -0.5, -2);
const vec3 LONG_BOX_SCALE  = vec3(1.5, 3.0, 1.5);
const vec3 SPHERE_POS   = vec3(2.0, -1.0, -0.25);

const vec3 EYE_POS         = vec3(0.0, 5, -18.0);
const vec3 REF_POS         = vec3(0.0, 2.5, 0.0);

const int RAY_STEPS     = 256;
const int FLOOR_ID      = 0;
const int LEFT_WALL_ID  = 1;
const int RIGHT_WALL_ID = 2;
const int BACK_WALL_ID  = 3;
const int CEILING_ID    = 4;
const int LONG_BOX_ID   = 5;
const int SPHERE_ID  = 6;
const float PI_OVER_180 = 3.1415926 / 180.0;

struct Intersection
{
    float t;
    vec3 color;
    vec3 p;
    int object;
};

float plane(vec3 p, vec4 n)
{
  // n must be normalized
  return dot(p, n.xyz) + n.w;
}

// Box with side lengths b
float box(vec3 p, vec3 b)
{
  return length(max(abs(p) - b, 0.0));
}


float sphere(vec3 p, float r, vec3 c)
{
    return distance(p, c) - r;
}

vec3 rotateY(vec3 p, float a) 
{
    return vec3(cos(a) * p.x + sin(a) * p.z, p.y, -sin(a) * p.x + cos(a) * p.z);
}

vec3 rotateX(vec3 p, float amt) 
{
    return vec3(p.x, cos(amt) * p.y - sin(amt) * p.z, sin(p.y) + cos(p.z));
}

vec2 sphereUV(vec3 center, float r, vec3 p)
{
    vec3 pDir = normalize(p - center);
    vec3 worldUp = vec3(0.0, 1.0, 0.0);
    float theta = acos(dot(pDir, worldUp));

    // If p is located at the positive part of z axis, then phi is 0-180.
    // If p is located at the negative part of z axis, then phi is 180-360.
    vec3 xzDir = normalize(vec3(pDir.x, 0.0, pDir.z));
    float phi = acos(dot(xzDir, vec3(1.0, 0.0, 0.0)));

    if(pDir.z < 0.0)
    {
        phi = phi + 3.14159;
    }

    return vec2(theta / 3.14159, phi / (2.0 * 3.14159));
}

void planeUVAxis(in vec3 p, in vec3 n, out vec3 u, out vec3 v)
{
    vec3 worldUp = vec3(0.0, 1.0, 0.0);
    u = normalize(cross(worldUp, n));
    v = normalize(cross(n, u));
}

vec2 planeUV(vec3 origin, vec3 n, vec3 p)
{
    vec3 uAxis;
    vec3 vAxis;
    planeUVAxis(origin, n, uAxis, vAxis);

    vec3 diff = p - origin;
    float uVal = dot(diff, uAxis);
    float vVal = dot(diff, vAxis);

    return vec2(uVal, vVal);
}

float hash(float x)
{
    return fract(sin(x * 97.79) * 653.767);
}

vec2 hash2(vec2 p)
{
    return vec2(hash(p.x * 27.79 - 300.0 + p.y * 34.35), hash(p.x * 56.3 + 256.0 + p.y * 89.37));
}

// Modify color to the burning fire color
vec3 cosinePalette2(float t)
{
    const vec3 a = vec3(0.5, 0.5, 0.5);
	const vec3 b = vec3(0.5, 0.5, 0.5);
	const vec3 c = vec3(1.0, 1.0, 1.0);
	const vec3 d = vec3(0.0, 0.33, 0.67);
    return clamp(a + b * cos(2.0 * 3.14159 * (c * t + d)), 0.0, 1.0);
}

vec3 cosinePalette(float t)
{
    const vec3 a = vec3(0.5, 0.5, 0.4);
	const vec3 b = vec3(0.5, 0.5, 0.43);
	const vec3 c = vec3(0.46, 0.57, 0.4);
	const vec3 d = vec3(0.63, 0.4233, -1.573);
    return clamp(a + b * cos(2.0 * 3.14159 * (c * t + d)), 0.0, 1.0);
}

float random1(vec3 p) 
{
    return fract(sin(dot(p, vec3(127.1, 311.7, 191.999))) * 43758.5453);
}

float random1(vec2 p)
{
  	return fract(sin(dot(p, vec2(456.789, 20487145.123))) * 842478.5453);
}

vec2 random2(vec2 p)
{
    return fract(sin(vec2(dot(p, vec2(271.11, 717.7)),
                          dot(p, vec2(692.5, 83.31))))
                 * 35789.537);
}

vec3 random3( vec3 p )
{
    return fract(sin(vec3(dot(p,vec3(127.1, 311.7, 191.999)),
                          dot(p,vec3(269.5, 183.3, 765.54)),
                          dot(p, vec3(420.69, 631.2,109.21))))
                 *43758.5453);
}

// Worley-noise function to make the Mario twisted and warped
float worley(float x, float y, float multiple)
{
    vec2 uv = vec2(x, y);
    uv *= multiple;
    vec2 uvInt = floor(uv);
    vec2 uvFract = fract(uv);

    float minDist = 1.0;
    for(int y = -1; y <= 1; ++y)
    {
        for(int x = -1; x <= 1; ++x)
        {
            vec2 neighbor = vec2(float(x), float(y));
            vec2 point = (vec2(1.0) + cos(random2(uvInt + neighbor) * 7.987 + iTime / 180.0)) / 2.0;
            vec2 diff = neighbor + point - uvFract;
            float dist = length(diff);
            minDist = min(minDist, dist);
        }
    }
    return minDist / multiple;
}

// Worley-noise function to generate the burning fire effect
float worleyFire1(vec2 c, float time)
{
    float dis = 1.0;
    for(int x = -1; x <= 1; x++)
    {
        for(int y = -1; y <= 1; y++)
        {
            vec2 p = floor(c) + vec2(x, y);
            vec2 a = hash2(p) * time;
            vec2 rnd = 0.5 + sin(a) * 0.5;
            float d = length(rnd + vec2(x, y) - fract(c));
            dis = min(dis, d);
        }
    }

    return dis;
}

// Worley-noise function to generate the burning fire effect
float worleyFire2(vec2 c, float time)
{
    float w = 0.0;
    float a = 0.5;
    for (int i = 0; i<5; i++)
    {
        w += worleyFire1(c, time) * a;
        c *= 2.0;
        time *= 2.0;
        a *= 0.5;
    }
    return w;
}

// from https://www.shadertoy.com/view/3sjGz3
float mySmootherStep(float a, float b, float t)
{
  	t = t * t * t * (t * (t * 6.18 - 18.0) + 12.0);
  	return mix(a, b, t);
}

// from https://www.shadertoy.com/view/3sjGz3
float interpNoise2D(vec2 uv) 
{
    vec2 uvFract = fract(uv);
    float ll = random1(floor(uv));
    float lr = random1(floor(uv) + vec2(1,0));
    float ul = random1(floor(uv) + vec2(0,1));
    float ur = random1(floor(uv) + vec2(1,1));

    float lerpXL = mySmootherStep(ll, lr, uvFract.x);
    float lerpXU = mySmootherStep(ul, ur, uvFract.x);

    return mySmootherStep(lerpXL, lerpXU, uvFract.y);
}

float fbm(vec2 uv) 
{
    float amp = 0.5;
    float freq = 8.0;
    float sum = 0.0;
    for(int i = 0; i < 4; i++) 
    {
        sum += interpNoise2D(uv * freq) * amp;
        amp *= 0.5;
        freq *= 2.0;
    }

    return sum;
}

float surflet3D(vec3 p, vec3 gridPoint)
{
    vec3 t3 = abs(p - gridPoint);

    float tX = 1.f - 6.f * pow(t3.x, 5.f) - 15.f * pow(t3.x, 4.f) + 10.f * pow(t3.x, 3.f);
    float tY = 1.f - 6.f * pow(t3.y, 5.f) - 15.f * pow(t3.y, 4.f) + 10.f * pow(t3.y, 3.f);
    float tZ = 1.f - 6.f * pow(t3.z, 5.f) - 15.f * pow(t3.z, 4.f) + 10.f * pow(t3.z, 3.f);

    vec3 t = vec3(tX, tY, tZ);

    vec3 gradient = random3(gridPoint);
    vec3 diff = p - gridPoint;
    float height = dot(diff, gradient);
    return height * t.x * t.y * t.z;
}

float PerlinNoise3D(vec3 p)
{
    float surfletSum = 0.0;
    vec3 pXLYLZL = floor(p);

    for(int dx = 0; dx <= 1; ++dx)
    {
        for(int dy = 0; dy <= 1; ++dy)
        {
            for(int dz = 0; dz <= 1; ++dz)
            {
                surfletSum += surflet3D(p, pXLYLZL + vec3(dx, dy, dz));
            }
        }
    }

    return surfletSum;
}

void sceneMap3D(vec3 pos, out float t, out int objId, vec3 lightPos)
{
    // floor
    t = plane(pos, FLOOR_VEC);  
    objId = FLOOR_ID;  

    float t2;
    if((t2 = plane(pos, LEFT_WALL_VEC)) < t)  
    {
        t= t2;
        objId = LEFT_WALL_ID;
    } 
    if((t2 = plane(pos, RIGHT_WALL_VEC)) < t)  
    {
        t= t2;
        objId = RIGHT_WALL_ID;
    }
    if((t2 = plane(pos, BACK_WALL_VEC)) < t)
    {
        t= t2;
        objId = BACK_WALL_ID;
    }
    if((t2 = plane(pos, CEILING_VEC)) < t)
    {
        t= t2;
        objId = CEILING_ID;
    }
    if((t2 = box(rotateY(pos + LONG_BOX_POS, 27.5 * PI_OVER_180), LONG_BOX_SCALE)) < t)  // long bix
    {
        t= t2;
        objId = LONG_BOX_ID;
    }
    if((t2 = sphere(pos, 1.5, SPHERE_POS)) < t)
    {
        t= t2;
        objId = SPHERE_ID;
    }
}

float sceneMap3D(vec3 pos, vec3 lightPos)
{
    float t = plane(pos, FLOOR_VEC);
    t = min(t, plane(pos, LEFT_WALL_VEC));
    t = min(t, plane(pos, RIGHT_WALL_VEC));
    t = min(t, plane(pos, BACK_WALL_VEC));
    t = min(t, plane(pos, CEILING_VEC));
    t = min(t, box(rotateY(pos + LONG_BOX_POS, 27.5 * PI_OVER_180), LONG_BOX_SCALE));
    t = min(t, sphere(pos, 1.5, SPHERE_POS));
    return t;
}

void march(vec3 origin, vec3 dir, out float t, out int objId, vec3 lightPos)
{
    t = 0.001;
    for(int i = 0; i < RAY_STEPS; ++i)
    {
        vec3 pos = origin + t * dir;
        float m;
        sceneMap3D(pos, m, objId, lightPos);
        if(m < 0.01)
        {
            return;
        }
        t += m;
    }

    t = -1.0;
    objId = -1;
}

vec3 computeMaterial(int objId, vec3 p, vec3 n, vec3 light, vec3 view)
{
    float lambert = dot(light, n);

    switch(objId) 
    {
    case FLOOR_ID:  // floor
        return vec3(1.0, 1.0, 1.0) * lambert;
        break;
    case LEFT_WALL_ID:  // Left wall, which is a large red plane
        return vec3(0.63, 0.065, 0.05) * lambert;
        break;
    case RIGHT_WALL_ID:  // Right wall, which is a large green plane
        return vec3(0.14, 0.45, 0.091) * lambert;
        break;
    case BACK_WALL_ID:  // Back wall, which is a large white plane
    {
        vec3 normal = vec3(0.0, 0.0, 1.0);
        vec2 backUV = planeUV(normal * 5.0, normal, p);

        float m = 5.0;
    	float x = backUV.x;
    	float y = backUV.y;
        vec2 gradient = vec2(0.0);
        float delta = 1.0;
    	gradient.x = worley(x + delta, y, m) - worley(x - delta, y, m);
    	gradient.y = worley(x, y + delta, m) - worley(x, y - delta, m);

        backUV += gradient;
        float r = 0.48;
        float dis = worleyFire2(backUV * iResolution.xy / 160.0, iTime * 5.0);
        vec3 fire = mix(vec3(1.0, 0.95, 0.5), vec3(0.8, 0.0, 0.0), dis);

        vec3 color = cosinePalette(r) * 0.618 + fire * 0.75;
        return color * lambert;
        break;
    }
    case CEILING_ID:  // Ceiling, which is a large white plane
        return vec3(1.0, 1.0, 1.0) * lambert;
        break;
    case LONG_BOX_ID:  // Cuboid, the long box
    {
        vec3 noisePos = vec3(p.x + sin(iTime), p.y + cos(iTime), p.z + cos(iTime));
        float perlin = PerlinNoise3D(noisePos);
        return 1.8 * cosinePalette(perlin) * lambert;
        break;
    }
    case SPHERE_ID:  // Sphere
    {
        vec2 sphereUV = sphereUV(vec3(2.0, 0.0, 1.0), 1.0, p);        
        sphereUV.x = (sphereUV.x + cos(iTime * 0.5) + 4.25) / 2.97;
        sphereUV.y = (sphereUV.y + sin(iTime * 0.14) + 1.25) / 1.48;      

        float fbmCol = fbm(sphereUV);
        vec3 color = cosinePalette2(fbmCol) * 1.2 + vec3(0.12);
        return color * lambert;
        break;
    }        
    default:
        return vec3(0.0);
        break;
    }

    return vec3(1.0);
}

vec3 computeNormal(vec3 pos, vec3 lightPos)
{
    vec3 epsilon = vec3(0.0, 0.001, 0.0);
    return normalize(vec3(sceneMap3D(pos + epsilon.yxx, lightPos) - sceneMap3D(pos - epsilon.yxx, lightPos),
                          sceneMap3D(pos + epsilon.xyx, lightPos) - sceneMap3D(pos - epsilon.xyx, lightPos),
                          sceneMap3D(pos + epsilon.xxy, lightPos) - sceneMap3D(pos - epsilon.xxy, lightPos)));
}

Intersection sdf3D(vec3 dir, vec3 eye, vec3 lightPos)
{
    float t;
    int objId;
    march(eye, dir, t, objId, lightPos);

    vec3 isect = eye + t * dir;
    vec3 nor = computeNormal(isect, lightPos);
    vec3 lightDir = normalize(lightPos - isect);

    vec3 surfaceColor = computeMaterial(objId, isect, nor, lightDir, normalize(eye - isect));

    return Intersection(t, surfaceColor, isect, objId);
}

void raycast(vec2 uv, out vec3 dir, out vec3 eye, out vec3 ref)
{
    eye = EYE_POS;
    ref = REF_POS;

    float len = tan(3.14159 * 0.125) * distance(eye, ref);
    vec3 H = normalize(cross(vec3(0.0, 1.0, 0.0), ref - eye));
    vec3 V = normalize(cross(H, eye - ref));
    V *= len;
    H *= len * float(iResolution.x) / float(iResolution.y);
    vec3 p = ref + uv.x * H + uv.y * V;
    dir = normalize(p - eye);
} 

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 lightPos = vec3(0.0, 7.3, -1.0);

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    // [-1, 1]
    vec2 uv2 = 2.0 * uv - vec2(1.0);

    vec3 dir, eye, ref;
    raycast(uv2, dir, eye, ref);
    Intersection intersection = sdf3D(dir, eye, lightPos);

    // Output to screen
    fragColor = vec4(intersection.color, 1.0);
}

const int AO_SAMPLES = 256;
const float AO_DIST = 0.15;
const float FIVETAP_K = 2.0;

const vec4 FLOOR_VEC       = vec4(0.0, 1.0, 0.0, 2.5);
const vec4 LEFT_WALL_VEC   = vec4(1.0, 0.0, 0.0, 5.0);
const vec4 RIGHT_WALL_VEC  = vec4(-1.0, 0.0, 0.0, 5.0);
const vec4 BACK_WALL_VEC   = vec4(0.0, 0.0, -1.0, 5.0);
const vec4 CEILING_VEC     = vec4(0.0, -1.0, 0.0, 7.5);
const vec3 LONG_BOX_POS    = vec3(2.1, -0.5, -2);
const vec3 LONG_BOX_SCALE  = vec3(1.5, 3.0, 1.5);
const vec3 SHORT_BOX_POS   = vec3(-2.1, 1, -1);
const vec3 SHORT_BOX_SCALE = vec3(1.5, 1.5, 1.5);

const vec3 EYE_POS         = vec3(0.0, 5, -18.0);
const vec3 REF_POS         = vec3(0.0, 2.5, 0.0);

const int RAY_STEPS     = 256;
const int FLOOR_ID      = 0;
const int LEFT_WALL_ID  = 1;
const int RIGHT_WALL_ID = 2;
const int BACK_WALL_ID  = 3;
const int CEILING_ID    = 4;
const int LONG_BOX_ID   = 5;
const int SHORT_BOX_ID  = 6;
const float PI_OVER_180 = 3.1415926 / 180.0;

struct Intersection
{
    float t;
    vec3 color;
    vec3 p;
    int object;
};

void coordinateSystem(const vec3 v1, out vec3 v2, out vec3 v3) 
{

    if (abs(v1.x) > abs(v1.y)) 
    {
        v2 = vec3(-v1.z, 0, v1.x) / length(v1.xz);
    }
    else
    {
        v2 = vec3(0, v1.z, -v1.y) / length(v1.yz);
    }
    v3 = cross(v1, v2);
}

float plane(vec3 p, vec4 n)
{
  // n must be normalized
  return dot(p, n.xyz) + n.w;
}

// Box with side lengths b
float box(vec3 p, vec3 b)
{
  return length(max(abs(p) - b, 0.0));
}

vec3 rotateY(vec3 p, float a) 
{
    return vec3(cos(a) * p.x + sin(a) * p.z, p.y, -sin(a) * p.x + cos(a) * p.z);
}

vec3 rotateX(vec3 p, float amt) 
{
    return vec3(p.x, cos(amt) * p.y - sin(amt) * p.z, sin(p.y) + cos(p.z));
}


void sceneMap3D(vec3 pos, out float t, out int objId, vec3 lightPos)
{
    // floor
    t = plane(pos, FLOOR_VEC);  
    objId = FLOOR_ID;  

    float t2;
    if((t2 = plane(pos, LEFT_WALL_VEC)) < t)  
    {
        t= t2;
        objId = LEFT_WALL_ID;
    } 
    if((t2 = plane(pos, RIGHT_WALL_VEC)) < t)  
    {
        t= t2;
        objId = RIGHT_WALL_ID;
    }
    if((t2 = plane(pos, BACK_WALL_VEC)) < t)
    {
        t= t2;
        objId = BACK_WALL_ID;
    }
    if((t2 = plane(pos, CEILING_VEC)) < t)
    {
        t= t2;
        objId = CEILING_ID;
    }
    if((t2 = box(rotateY(pos + LONG_BOX_POS, 27.5 * PI_OVER_180), LONG_BOX_SCALE)) < t)  // long bix
    {
        t= t2;
        objId = LONG_BOX_ID;
    }
    if((t2 = box(rotateY(pos + SHORT_BOX_POS, -27.5 * PI_OVER_180), SHORT_BOX_SCALE)) < t)
    {
        t= t2;
        objId = SHORT_BOX_ID;
    }
}

float sceneMap3D(vec3 pos)
{
    float t = plane(pos, FLOOR_VEC);
    t = min(t, plane(pos, LEFT_WALL_VEC));
    t = min(t, plane(pos, RIGHT_WALL_VEC));
    t = min(t, plane(pos, BACK_WALL_VEC));
    t = min(t, plane(pos, CEILING_VEC));
    t = min(t, box(rotateY(pos + LONG_BOX_POS, 27.5 * PI_OVER_180), LONG_BOX_SCALE));
    t = min(t, box(rotateY(pos + SHORT_BOX_POS, -27.5 * PI_OVER_180), SHORT_BOX_SCALE));
    return t;
}

vec2 random2(vec3 p)
{
    return fract(sin(vec2(p.x, p.x + 1.0)) * vec2(43758.5453123, 22578.1459123));
}

// from https://www.shadertoy.com/view/3sB3zV
float computeAO(vec3 p, vec3 n, float dist)
{
    float aoSum = 0.0;

    // Sample a few points in the hemisphere around n at p
    vec3 t, b;

    // Make a tangent and bitangent vector
    coordinateSystem(n, t, b);

    for(int i = 0; i < AO_SAMPLES; ++i) 
    {
        // Generate a pair of random [0, 1] floats
        vec2 xi = random2(p + float(i) * 203.1);

        // Convert the xi pair to a vector in the hemisphere
        float len = sqrt(xi.y);
        float rx = len * cos(6.2831 * xi.x);
        float ry = len * sin(6.2831 * xi.x);
        float rz = sqrt(1.0 - xi.y); // z = sqrt(1 - x*x - y*y)
        vec3 dir = vec3(rx * t + ry * b + rz * n);

        aoSum += step(0.0, sceneMap3D(p + dir * dist));
    }

    return aoSum / float(AO_SAMPLES);
}

// from https://www.shadertoy.com/view/3sB3zV
float fiveTapAO(vec3 p, vec3 n, float k) 
{
    float aoSum = 0.0;
    for(float i = 0.0; i < 5.0; ++i)
    {
        float coeff = 1.0 / pow(2.0, i);
        aoSum += coeff * (i * AO_DIST - sceneMap3D(p + n * i * AO_DIST));
    }

    return 1.0 - k * aoSum;
}

void march(vec3 origin, vec3 dir, out float t, out int objId, vec3 lightPos)
{
    t = 0.001;
    for(int i = 0; i < RAY_STEPS; ++i)
    {
        vec3 pos = origin + t * dir;
        float m;
        sceneMap3D(pos, m, objId, lightPos);
        if(m < 0.01)
        {
            return;
        }
        t += m;
    }

    t = -1.0;
    objId = -1;
}

vec3 computeMaterial(int objId, vec3 p, vec3 n, vec3 light, vec3 view)
{
    float lambert = dot(light, n);

