    t = -1.0;
    objId = -1;
}

vec3 computeMaterial(int objId, vec3 p, vec3 n, vec3 light, vec3 view)
{
    float lambert = dot(light, n);

    switch(objId) 
    {
    case FLOOR_ID:  // floor
        return vec3(1.0, 1.0, 1.0) * lambert;
        break;
    case LEFT_WALL_ID:  // Left wall, which is a large red plane
        return vec3(0.63, 0.065, 0.05) * lambert;
        break;
    case RIGHT_WALL_ID:  // Right wall, which is a large green plane
        return vec3(0.14, 0.45, 0.091) * lambert;
        break;
    case BACK_WALL_ID:  // Back wall, which is a large white plane
        return vec3(1.0, 1.0, 1.0) * lambert;
        break;
    case CEILING_ID:  // Ceiling, which is a large white plane
        return vec3(1.0, 1.0, 1.0) * lambert;
        break;
    case LONG_BOX_ID:  // Cuboid, the long box
        return vec3(1.0, 1.0, 1.0) * lambert;
        break;
    case SHORT_BOX_ID:  // Cube, the short box
        return vec3(1.0, 1.0, 1.0) * lambert;
        break;
    default:
        return vec3(0.0);
        break;
    }

    return vec3(1.0);
}

vec3 computeNormal(vec3 pos, vec3 lightPos)
{
    vec3 epsilon = vec3(0.0, 0.001, 0.0);
    return normalize(vec3(sceneMap3D(pos + epsilon.yxx, lightPos) - sceneMap3D(pos - epsilon.yxx, lightPos),
                          sceneMap3D(pos + epsilon.xyx, lightPos) - sceneMap3D(pos - epsilon.xyx, lightPos),
                          sceneMap3D(pos + epsilon.xxy, lightPos) - sceneMap3D(pos - epsilon.xxy, lightPos)));
}

Intersection sdf3D(vec3 dir, vec3 eye, vec3 lightPos)
{
    float t;
    int objId;
    march(eye, dir, t, objId, lightPos);

    vec3 isect = eye + t * dir;
    vec3 nor = computeNormal(isect, lightPos);
    vec3 lightDir = normalize(lightPos - isect);
    vec3 surfaceColor = computeMaterial(objId, isect, nor, lightDir, normalize(eye - isect));

    vec3 sdfColor = clamp(surfaceColor + vec3(0.05, 0.1, 0.15), 0.0, 1.0);
    return Intersection(t, sdfColor, isect, objId);
}

void raycast(vec2 uv, out vec3 dir, out vec3 eye, out vec3 ref)
{
    eye = EYE_POS;
    ref = REF_POS;

    float len = tan(3.14159 * 0.125) * distance(eye, ref);
    vec3 H = normalize(cross(vec3(0.0, 1.0, 0.0), ref - eye));
    vec3 V = normalize(cross(H, eye - ref));
    V *= len;
    H *= len * float(iResolution.x) / float(iResolution.y);
    vec3 p = ref + uv.x * H + uv.y * V;
    dir = normalize(p - eye);
} 

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec3 lightPos = vec3(0.0, 7.3, -1.0);

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    // [-1, 1]
    vec2 uv2 = 2.0 * uv - vec2(1.0);

    vec3 dir, eye, ref;
    raycast(uv2, dir, eye, ref);
    Intersection intersection = sdf3D(dir, eye, lightPos);

    float distAlongCamZ = abs(dot(normalize(ref - eye), (intersection.p - eye)));
    float dofZ = min(1.0, abs(distAlongCamZ - FOCAL_LENGTH) / FOCAL_RANGE);
    dofZ = pow(dofZ, 0.5);

    fragColor = vec4(intersection.color * step(0.0, intersection.t), dofZ);
    float fogT = smoothstep(50.0, 100.0, distance(intersection.p, eye));

    fragColor.rgb = mix(fragColor.rgb, vec3(0.0), fogT);
}

