	return (global - offset) / M_MINIMAP_SCALE;
}

vec2 FromMMinimapToGlobal(vec2 minimap) {
    vec2 offset = vec2(M_MINIMAP_OFFSET_X * iResolution.x / iResolution.y, M_MINIMAP_OFFSET_Y);
	return M_MINIMAP_SCALE * minimap + offset;
}

vec2 FromGlobalToJMinimap(vec2 global) {
    vec2 offset = vec2(J_MINIMAP_OFFSET_X * iResolution.x / iResolution.y, J_MINIMAP_OFFSET_Y);
	return (global - offset) / J_MINIMAP_SCALE;
}

vec2 FromJMinimapToGlobal(vec2 minimap) {
    vec2 offset = vec2(J_MINIMAP_OFFSET_X * iResolution.x / iResolution.y, J_MINIMAP_OFFSET_Y);
	return J_MINIMAP_SCALE * minimap + offset;
