vec3 Draw(vec2 z, vec2 c) {    
    if (NoComputationNeeded(c)) return vec3(0.);

    float z2 = 0.;       
    float sum = 0.0;
    float prev_sum = 0.0;    
    int iter = 0;
    while (z2 / ESCAPE_RADIUS < ESCAPE_RADIUS) {
        if (iter == MAX_STEPS) return vec3(0.);    	
        ++iter;

        // z = z^2 + c
		z = vec2(z.x*z.x - z.y*z.y, 2.*z.x*z.y) + c;
        z2 = dot(z, z);

        // For coloring
        prev_sum = sum;
        sum += atan(z.y, z.x);
    } 

    // The fractional iteration by which we overshot the escape radius    
    float overshot = log2(0.5*log2(z2)/log2(ESCAPE_RADIUS));

    // Compute real values for sum and iter at the point of crossing
    // the escape radius through linear interpolation
	float real_sum = mix(sum, prev_sum, overshot);
    float real_iter = float(iter) - overshot;      
    float avg = real_sum / real_iter;     

    //float val = 2. / (1. + exp(-real_iter * 0.07)) - 1.;
	return GetColor2(avg);
    //return GetColor5(avg * 0.45);// * val;					
}

bool InMMinimap(vec2 uv_m) {
    return uv_m.x > M_MINIMAP_LEFT_BOUND && uv_m.x < M_MINIMAP_RIGHT_BOUND &&
        uv_m.y > M_MINIMAP_BOTTOM_BOUND && uv_m.y < M_MINIMAP_TOP_BOUND;
}

bool InJMinimap(vec2 uv_m) {
    return uv_m.x > J_MINIMAP_LEFT_BOUND && uv_m.x < J_MINIMAP_RIGHT_BOUND &&
        uv_m.y > J_MINIMAP_BOTTOM_BOUND && uv_m.y < J_MINIMAP_TOP_BOUND;
}

vec3 DrawMainImage(vec2 fragCoord, vec2 center, float zoom, mat2 rotation_matrix, vec2 c) {
    // Split each pixel into an ANTI_ALIASING x ANTI_ALIASING grid
    vec3 col = vec3(0.);
    int num_samples = 0;
    for (int i = 0; i < ANTI_ALIASING; ++i) {
    	for (int j = 0; j <= ANTI_ALIASING; ++j) {
    		++num_samples;

            // Apply camera transforms
            vec2 coord = fragCoord + vec2(float(i)/float(ANTI_ALIASING), float(j)/float(ANTI_ALIASING)); 
            vec2 uv = (2.*coord - iResolution.xy)/iResolution.y;            
            uv *= rotation_matrix;
            uv /= zoom;
            uv += center;

            col += Draw(uv, c);
        }
    }

	col /= float(num_samples); 

 	return col;   
}

vec3 DrawMMinimap(vec2 fragCoord, vec2 center, float zoom,
                  mat2 rotation_matrix, vec2 c, out float minimap_opacity) {
	vec2 uv = (2.*fragCoord - iResolution.xy)/iResolution.y;    
    vec2 uv_m = FromGlobalToMMinimap(uv);                    


    vec3 col = vec3(0.);
    minimap_opacity = 0.;

    if (InMMinimap(uv_m)) {
        // Draw the Mandelbrot set for the minimap
    	col = Draw(vec2(0.), uv_m);

        vec2 offset = uv_m - c;        
        float marker_opacity = smoothstep(0.005, 0.0, dot(offset, offset));
        col = mix(col, vec3(1., .1, .1), marker_opacity);        

        // Minimap border fade out
        minimap_opacity = 1.;
        minimap_opacity *= smoothstep(0., MINIMAP_FADE, uv_m.x - M_MINIMAP_LEFT_BOUND);
        minimap_opacity *= smoothstep(0., MINIMAP_FADE, M_MINIMAP_RIGHT_BOUND - uv_m.x);
        minimap_opacity *= smoothstep(0., MINIMAP_FADE, M_MINIMAP_TOP_BOUND - uv_m.y);
        minimap_opacity *= smoothstep(0., MINIMAP_FADE, uv_m.y - M_MINIMAP_BOTTOM_BOUND);        
    } 
	return col;
}

vec3 DrawJMinimap(vec2 fragCoord, vec2 center, float zoom,
                  mat2 rotation_matrix, vec2 c, out float minimap_opacity) {
	vec2 uv = (2.*fragCoord - iResolution.xy)/iResolution.y;    
    vec2 uv_j = FromGlobalToJMinimap(uv);                    


    vec3 col = vec3(0.);
    minimap_opacity = 0.;

    if (InJMinimap(uv_j)) {
        // Draw the Julia set for the minimap
    	col = Draw(uv_j, c);

        // Draw the rectangular framing on the minimap        
        vec2 offset = uv_j - center;
        vec2 framing_uv_j = rotation_matrix * offset * zoom; 
        float aspect = iResolution.x / iResolution.y;
        float dx = aspect - abs(framing_uv_j.x);
        float dy = 1. - abs(framing_uv_j.y);
        float thickness = zoom * 0.03;       
        float outside_rectangle = max(smoothstep(thickness, 0., dx), smoothstep(thickness, 0., dy));        
        float inside_rectangle = min(smoothstep(-thickness, 0., dx), smoothstep(-thickness, 0., dy));
        float framing_opacity = inside_rectangle * outside_rectangle;
        // When the zoom is high, draw a point, so that the framing does not degenerate into nothing.
        framing_opacity += smoothstep(40., 70., zoom) * smoothstep(0.005, 0.0, dot(offset, offset));
        col = mix(col, vec3(1., .1, .1), framing_opacity);        

        // Minimap border fade out
        minimap_opacity = 1.;
        minimap_opacity *= smoothstep(0., MINIMAP_FADE, uv_j.x - J_MINIMAP_LEFT_BOUND);
        minimap_opacity *= smoothstep(0., MINIMAP_FADE, J_MINIMAP_RIGHT_BOUND - uv_j.x);
        minimap_opacity *= smoothstep(0., MINIMAP_FADE, J_MINIMAP_TOP_BOUND - uv_j.y);
        minimap_opacity *= smoothstep(0., MINIMAP_FADE, uv_j.y - J_MINIMAP_BOTTOM_BOUND);        
    } 
	return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {        
    // Read information pixels in the left top corner
    ivec2 camera_pixel = ivec2(0, iResolution.y - 0.5);
    ivec2 state_pixel = ivec2(1, iResolution.y - 0.5);
    ivec2 julia_pixel = ivec2(0, iResolution.y - 1.5);

    // camera.xy - camera position
    // camera.z - zoom exponent = log(zoom) + a
    // camera.w - camera rotation in radians
    vec4 camera = texelFetch(iChannel1, camera_pixel, 0);

    // state.xy - position from where mouse dragging started
    // state.z - currently dragging if 1., not dragging if 0.
    // state.w - camera controlled manually if 1., automated if 0.
    vec4 state = texelFetch(iChannel1, state_pixel, 0);

    // julia.xy - Julia set seed
    // julia.z - using default Julia seed if 0., user selected if 1.
    vec4 julia = texelFetch(iChannel1, julia_pixel, 0);

    if (state.w <= 0.) {
        // Automated camera movement
        camera.xy = vec2(-0.457, 0.26);
    	camera.z = 4. + 2. * sin(iTime * 0.1);
       	camera.w = M_2_PI * sin(iTime * 0.01);
    }       

    if (julia.z <= 0.) {
        // Default Julia set seed
    	julia.z = 1.0;
        julia.x = -0.6;
        julia.y = 0.5;
    }

    // Camera zoom and rotation
    float zoom = exp(camera.z - 1.);    
    float sin_rotation = sin(camera.w);
    float cos_rotation = cos(camera.w);
    mat2 rotation_matrix = mat2(cos_rotation, sin_rotation, -sin_rotation, cos_rotation);

    // Mandelbrot and Julia minimaps toggle flags
    bool m_minimap_enabled = texelFetch(iChannel0, ivec2(KEY_M, 2), 0).x > 0.;    
    bool j_minimap_enabled = texelFetch(iChannel0, ivec2(KEY_J, 2), 0).x > 0.;    

    // Update information pixels
    if (ivec2(fragCoord - 0.5) == camera_pixel || ivec2(fragCoord - 0.5) == state_pixel) {
        vec4 old_camera = camera;

        // Update camera position
        float factor = 1.0 - 0.9 * texelFetch(iChannel0, ivec2(KEY_SHIFT, 0), 0).x;

        vec2 xy_change = vec2(0.);
       	xy_change.x -= 0.1 / zoom * factor * texelFetch(iChannel0, ivec2(KEY_LEFT, 0), 0).x;
        xy_change.x -= 0.1 / zoom * factor * texelFetch(iChannel0, ivec2(KEY_A, 0), 0).x;
        xy_change.x += 0.1 / zoom * factor * texelFetch(iChannel0, ivec2(KEY_RIGHT, 0), 0).x;
        xy_change.x += 0.1 / zoom * factor * texelFetch(iChannel0, ivec2(KEY_D, 0), 0).x;
        xy_change.y -= 0.1 / zoom * factor * texelFetch(iChannel0, ivec2(KEY_DOWN, 0), 0).x;        
        xy_change.y -= 0.1 / zoom * factor * texelFetch(iChannel0, ivec2(KEY_S, 0), 0).x;        
        xy_change.y += 0.1 / zoom * factor * texelFetch(iChannel0, ivec2(KEY_UP, 0), 0).x;
        xy_change.y += 0.1 / zoom * factor * texelFetch(iChannel0, ivec2(KEY_W, 0), 0).x;

        camera.xy += transpose(rotation_matrix) * xy_change;

        camera.z -= 0.1 * factor * texelFetch(iChannel0, ivec2(KEY_PAGE_UP, 0), 0).x;        
        camera.z -= 0.1 * factor * texelFetch(iChannel0, ivec2(KEY_X, 0), 0).x;        
        camera.z += 0.1 * factor * texelFetch(iChannel0, ivec2(KEY_PAGE_DOWN, 0), 0).x;
        camera.z += 0.1 * factor * texelFetch(iChannel0, ivec2(KEY_Z, 0), 0).x;        
        camera.w -= M_PI * 0.01 * factor * texelFetch(iChannel0, ivec2(KEY_E, 0), 0).x;
        camera.w += M_PI * 0.01 * factor * texelFetch(iChannel0, ivec2(KEY_Q, 0), 0).x;

        if (iMouse.z > 0.) {
            vec2 mouse_uv = (2.*iMouse.xy - iResolution.xy)/iResolution.y;    
            vec2 mouse_uv_j = FromGlobalToJMinimap(mouse_uv);            

            if (state.z > 0.) {
                // The image is being dragged with a mouse
            	camera.xy = state.xy - transpose(rotation_matrix) * mouse_uv / zoom;                	
            } else if (j_minimap_enabled && InJMinimap(mouse_uv_j)) {
                // Julia minimap was clicked
            	camera.xy = mouse_uv_j;
            }                    
        }                

        if (camera != old_camera) {
        	state.w = 1.0;
        } else if (texelFetch(iChannel0, ivec2(KEY_SPACE, 0), 0).x > 0.0) {
        	state.w = 0.0;
        }

        // Update dragging state
        if (ivec2(fragCoord - 0.5) == state_pixel) {                                
            if (iMouse.z > 0.) {
                vec2 mouse_uv = (2.*iMouse.xy - iResolution.xy)/iResolution.y;
                if ((!m_minimap_enabled || !InMMinimap(FromGlobalToMMinimap(mouse_uv))) &&
                    (!j_minimap_enabled || !InJMinimap(FromGlobalToJMinimap(mouse_uv)))) {
                    if (state.z <= 0.) { // Not dragging
                        // Set flag that we are now dragging
                        state.z = 1.0;
                        // Set coordinates where we started dragging
                        state.xy = camera.xy + transpose(rotation_matrix) * mouse_uv / zoom;
                    }
                }            
            } else {
                // Set flag that we are not dragging
                state.z = 0.0;        
            }

            fragColor = state;            
        } else {        
        	fragColor = camera;
        }

        return;
    }

    // Update Julia set seed
    if (ivec2(fragCoord - 0.5) == julia_pixel) {
        if (m_minimap_enabled && iMouse.z > 0.) {
            vec2 mouse_uv = (2.*iMouse.xy - iResolution.xy)/iResolution.y;                
            vec2 mouse_uv_m = FromGlobalToMMinimap(mouse_uv);
            if (InMMinimap(mouse_uv_m)) {
				julia.z = 1.0;
            	julia.xy = mouse_uv_m;
            }
        }                
    	fragColor = julia;
        return;
    }

    vec3 col = DrawMainImage(fragCoord, camera.xy, zoom, rotation_matrix, julia.xy);

    if (m_minimap_enabled) {
    	float minimap_opacity = 0.;
    	vec3 minimap_col = DrawMMinimap(fragCoord, camera.xy, zoom,
                                       rotation_matrix, julia.xy, minimap_opacity);             	    
  		col = mix(col, minimap_col, minimap_opacity);           
    }

    if (j_minimap_enabled) {
    	float minimap_opacity = 0.;
    	vec3 minimap_col = DrawJMinimap(fragCoord, camera.xy, zoom,
                                       rotation_matrix, julia.xy, minimap_opacity);             	    
  		col = mix(col, minimap_col, minimap_opacity);           
    }

    fragColor = vec4(col, 1.0);
}

