//	Author: https://www.shadertoy.com/user/andretugan
//	Creative Commons Attribution-NonCommercial 3.0 Unported License
//	https://creativecommons.org/licenses/by-nc/3.0
//	
//  Main image can be dragged with a mouse, the minimap can be clicked
//	M - toggle minimap
// 	A, W, S, D or cursor keys - move
// 	Z, X, PAGE_UP, PAGE_DOWN - zoom 
// 	Q, E - rotate
//  SPACE - reset camera
//
//	Requires WebGL 2.0 support. If you are using Safari, it might be disabled by default.  
//
//	The coloring method is computing atan(y, x) during the iterations and
//	uses their average to seed the color palette.
//	It is similar to the weighted average algorithm described here:
//	http://www.kerrymitchellart.com/tutorials/formulas2/uf2-2.htm
//	I'm not using weighted average to improve FPS, and it also looks nice.

#define ANTI_ALIASING 2

#define MINIMAP_SCALE 0.25
#define MINIMAP_OFFSET_X 0.9
#define MINIMAP_OFFSET_Y -0.7
#define MINIMAP_LEFT_BOUND -2.2
#define MINIMAP_RIGHT_BOUND 1.
#define MINIMAP_TOP_BOUND 1.3
#define MINIMAP_BOTTOM_BOUND -1.3
#define MINIMAP_FADE 0.5

#define M_PI 3.14159265358
#define M_PI_2 (0.5 * M_PI)

#define ESCAPE_RADIUS 500.

//	For a more detailed deep zoom increase this
#define MAX_STEPS 200

#define KEY_SHIFT 16
#define KEY_CTRL 17
#define KEY_SPACE 32
#define KEY_PAGE_UP 33
#define KEY_PAGE_DOWN 34
#define KEY_LEFT 37
#define KEY_UP 38
#define KEY_RIGHT 39
#define KEY_DOWN 40
#define KEY_A 65
#define KEY_D 68
#define KEY_E 69
#define KEY_M 77
#define KEY_Q 81
#define KEY_S 83
#define KEY_W 87
#define KEY_X 88
#define KEY_Y 89
#define KEY_Z 90

//  Color palettes based on article by Inigo Quilez
// 	https://iquilezles.org/articles/palettes
vec3 GetColor1(float t)
{
    return vec3(0.5, 0.5, 0.5) + vec3(0.5, 0.5, 0.5) *
        cos(2. * M_PI * (t * vec3(1., 1., 1.) + vec3(0.00, 0.33, 0.67)));
}

vec3 GetColor2(float t)
{
    return vec3(0.5, 0.5, 0.5) + vec3(0.5, 0.5, 0.5) *
        cos(2. * M_PI * (t * vec3(1., 1., 1.) + vec3(0.0, 0.1, 0.2)));
}

vec3 GetColor3(float t)
{
    return vec3(0.5, 0.5, 0.5) + vec3(0.5, 0.5, 0.5) *
        cos(2. * M_PI * (t * vec3(1., 1., 1.) + vec3(0.3, 0.2, 0.2)));
}

vec3 GetColor4(float t)
{
    return vec3(0.5, 0.5, 0.5) + vec3(0.5, 0.5, 0.5) *
        cos(2. * M_PI * (t * vec3(1.0, 1.0, 0.5) + vec3(0.8, 0.9, 0.3)));
}

vec3 GetColor5(float t)
{
    return vec3(0.5, 0.5, 0.5) + vec3(0.5, 0.5, 0.5) *
        cos(2. * M_PI * (t * vec3(1.0, 0.7, 0.4) + vec3(0.0, 0.15, 0.2)));
}

vec3 GetColor6(float t)
{
    return vec3(0.5, 0.5, 0.5) + vec3(0.5, 0.5, 0.5) *
        cos(2. * M_PI * (t * vec3(2.0, 1.0, 0.0) + vec3(0.5, 0.2, 0.25)));
}

vec3 GetColor7(float t)
{
    return vec3(0.8, 0.5, 0.4) + vec3(0.2, 0.4, 0.2) *
        cos(2. * M_PI * (t * vec3(2.0, 1.0, 1.0) + vec3(0.0, 0.25, 0.25)));
}

bool NoComputationNeeded(vec2 c) {	
    // Do not compute inside the large bulbs
    // Based on https://www.shadertoy.com/view/4sdXWX
    float c2 = dot(c, c);
    if ((256.0 * c2 - 96.0) * c2 + 32.0 * c.x < 3.0) return true;    
    if (16.0 * (c2 + 2. * c.x + 1.) < 1.0) return true;        
    return false;
}

vec2 FromGlobalToMinimap(vec2 global) {
    vec2 offset = vec2(MINIMAP_OFFSET_X * iResolution.x / iResolution.y, MINIMAP_OFFSET_Y);
	return (global - offset) / MINIMAP_SCALE;
}

vec2 FromMinimapToGlobal(vec2 minimap) {
    vec2 offset = vec2(MINIMAP_OFFSET_X * iResolution.x / iResolution.y, MINIMAP_OFFSET_Y);
	return MINIMAP_SCALE * minimap + offset;
}

vec3 Draw(vec2 z, vec2 c) {    
    if (NoComputationNeeded(c)) return vec3(0.);

    float z2 = 0.;       
    float sum = 0.0;
    float prev_sum = 0.0;    
    int iter = 0;
    while (z2 / ESCAPE_RADIUS < ESCAPE_RADIUS) {
        if (iter == MAX_STEPS) return vec3(0.);    	
        ++iter;

		z = vec2(z.x*z.x - z.y*z.y, 2.*z.x*z.y) + c;
        z2 = dot(z, z);                       
        prev_sum = sum;
        sum += atan(z.y, z.x);
    } 

    // The fractional iteration by which we overshot the escape radius    
    float overshot = log2(0.5*log2(z2)/log2(ESCAPE_RADIUS));

    // Compute real values for sum and iter at the point of crossing
    // the escape radius through linear interpolation
	float real_sum = mix(sum, prev_sum, overshot);
    float real_iter = float(iter) - overshot;      
    float avg = real_sum / real_iter;     

    // float val = 2. / (1. + exp(-real_iter * 0.07)) - 1.;
    // return GetColor5(0.3 + 0.3 * avg); 
	return GetColor2(avg);// * val;					
}

bool InMinimap(vec2 uv_m) {
    return uv_m.x > MINIMAP_LEFT_BOUND && uv_m.x < MINIMAP_RIGHT_BOUND &&
        uv_m.y > MINIMAP_BOTTOM_BOUND && uv_m.y < MINIMAP_TOP_BOUND;
}

vec3 DrawMainImage(vec2 fragCoord, vec2 center, float zoom, mat2 rotation_matrix) {
    // Split each pixel into an ANTI_ALIASING x ANTI_ALIASING grid
    vec3 col = vec3(0.);
    int num_samples = 0;
    for (int i = 0; i < ANTI_ALIASING; ++i) {
    	for (int j = 0; j <= ANTI_ALIASING; ++j) {
    		++num_samples;

            vec2 coord = fragCoord + vec2(float(i)/float(ANTI_ALIASING), float(j)/float(ANTI_ALIASING)); 
            vec2 uv = (2.*coord - iResolution.xy)/iResolution.y;            
            uv *= rotation_matrix;
            uv /= zoom;
            uv += center;

            col += Draw(vec2(0.), uv);
        }
    }

	col /= float(num_samples); 

 	return col;   
}

vec3 DrawMinimap(vec2 fragCoord, vec2 center, float zoom,
                 mat2 rotation_matrix, out float minimap_opacity) {
	vec2 uv = (2.*fragCoord - iResolution.xy)/iResolution.y;    
    vec2 uv_m = FromGlobalToMinimap(uv);                    


    vec3 col = vec3(0.);
    minimap_opacity = 0.;

    if (InMinimap(uv_m)) {
        // Draw the Mandelbrot set for the minimap
    	col = Draw(vec2(0.), uv_m);

        // Draw the rectangular framing on the minimap        
        vec2 offset = uv_m - center;
        vec2 framing_uv_m = rotation_matrix * offset * zoom; 
        float aspect = iResolution.x / iResolution.y;
        float dx = aspect - abs(framing_uv_m.x);
        float dy = 1. - abs(framing_uv_m.y);
        float thickness = zoom * 0.03;       
        float outside_rectangle = max(smoothstep(thickness, 0., dx), smoothstep(thickness, 0., dy));        
        float inside_rectangle = min(smoothstep(-thickness, 0., dx), smoothstep(-thickness, 0., dy));
        float framing_opacity = inside_rectangle * outside_rectangle;
        // When the zoom is high, draw a point, so that the framing does not degenerate into nothing.
        framing_opacity += smoothstep(40., 70., zoom) * smoothstep(0.005, 0.0, dot(offset, offset));
        col = mix(col, vec3(1., .1, .1), framing_opacity);        

        // Minimap border fade out
        minimap_opacity = 1.;
        minimap_opacity *= smoothstep(0., MINIMAP_FADE, uv_m.x - MINIMAP_LEFT_BOUND);
        minimap_opacity *= smoothstep(0., MINIMAP_FADE, MINIMAP_RIGHT_BOUND - uv_m.x);
        minimap_opacity *= smoothstep(0., MINIMAP_FADE, MINIMAP_TOP_BOUND - uv_m.y);
        minimap_opacity *= smoothstep(0., MINIMAP_FADE, uv_m.y - MINIMAP_BOTTOM_BOUND);        
    } 
	return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {        
    // Process inputs
    ivec2 camera_pixel = ivec2(0, iResolution.y - 0.5);
    ivec2 state_pixel = ivec2(1, iResolution.y - 0.5);

    // camera.xy - camera position
    // camera.z - zoom exponent = log(zoom) + a
    // camera.w - camera rotation in radians
    vec4 camera = texelFetch(iChannel1, camera_pixel, 0);

    // state.xy - position from where mouse dragging started
    // state.z - currently dragging if 1., not dragging if 0.
    // state.w - camera controlled manually if 1., automated if 0.
    vec4 state = texelFetch(iChannel1, state_pixel, 0);

    if (state.w <= 0.) {
        camera.xy = vec2(-0.2412, 0.8408);
    	camera.z = 6. + 3. * sin(iTime * 0.1);
       	camera.w = 2. * M_PI * sin(iTime * 0.01);
    }        

    float zoom = exp(camera.z - 1.);

    float sin_rotation = sin(camera.w);
    float cos_rotation = cos(camera.w);
    mat2 rotation_matrix = mat2(cos_rotation, sin_rotation, -sin_rotation, cos_rotation);
    bool minimap_enabled = texelFetch(iChannel0, ivec2(KEY_M, 2), 0).x > 0.;    

    if (ivec2(fragCoord - 0.5) == camera_pixel || ivec2(fragCoord - 0.5) == state_pixel) {
        vec4 old_camera = camera;

        float factor = 1.0 - 0.9 * texelFetch(iChannel0, ivec2(KEY_SHIFT, 0), 0).x;

        vec2 xy_change = vec2(0.);
       	xy_change.x -= 0.1 / zoom * factor * texelFetch(iChannel0, ivec2(KEY_LEFT, 0), 0).x;
        xy_change.x -= 0.1 / zoom * factor * texelFetch(iChannel0, ivec2(KEY_A, 0), 0).x;
        xy_change.x += 0.1 / zoom * factor * texelFetch(iChannel0, ivec2(KEY_RIGHT, 0), 0).x;
        xy_change.x += 0.1 / zoom * factor * texelFetch(iChannel0, ivec2(KEY_D, 0), 0).x;
        xy_change.y -= 0.1 / zoom * factor * texelFetch(iChannel0, ivec2(KEY_DOWN, 0), 0).x;        
        xy_change.y -= 0.1 / zoom * factor * texelFetch(iChannel0, ivec2(KEY_S, 0), 0).x;        
        xy_change.y += 0.1 / zoom * factor * texelFetch(iChannel0, ivec2(KEY_UP, 0), 0).x;
        xy_change.y += 0.1 / zoom * factor * texelFetch(iChannel0, ivec2(KEY_W, 0), 0).x;

        camera.xy += transpose(rotation_matrix) * xy_change;

        camera.z -= 0.1 * factor * texelFetch(iChannel0, ivec2(KEY_PAGE_UP, 0), 0).x;        
        camera.z -= 0.1 * factor * texelFetch(iChannel0, ivec2(KEY_X, 0), 0).x;        
        camera.z += 0.1 * factor * texelFetch(iChannel0, ivec2(KEY_PAGE_DOWN, 0), 0).x;
        camera.z += 0.1 * factor * texelFetch(iChannel0, ivec2(KEY_Z, 0), 0).x;        
        camera.w -= M_PI * 0.01 * factor * texelFetch(iChannel0, ivec2(KEY_E, 0), 0).x;
        camera.w += M_PI * 0.01 * factor * texelFetch(iChannel0, ivec2(KEY_Q, 0), 0).x;

        if (iMouse.z > 0.) {
            vec2 mouse_uv = (2.*iMouse.xy - iResolution.xy)/iResolution.y;    
            vec2 mouse_uv_m = FromGlobalToMinimap(mouse_uv);

            if (state.z > 0.) {
            	camera.xy = state.xy - transpose(rotation_matrix) * mouse_uv / zoom;                	
            } else if (minimap_enabled && InMinimap(mouse_uv_m)) {
            	camera.xy = mouse_uv_m;
            }
        }                

        if (camera != old_camera) {
        	state.w = 1.0;
        } else if (texelFetch(iChannel0, ivec2(KEY_SPACE, 0), 0).x > 0.0) {
        	state.w = 0.0;
        }

        if (ivec2(fragCoord - 0.5) == state_pixel) {                                
            if (iMouse.z > 0.) {
                vec2 mouse_uv = (2.*iMouse.xy - iResolution.xy)/iResolution.y;
                if (!minimap_enabled || !InMinimap(FromGlobalToMinimap(mouse_uv))) {
                    if (state.z <= 0.) { // Not dragging
                        // Set flag that we are now dragging
                        state.z = 1.0;
                        // Set coordinates where we started dragging
                        state.xy = camera.xy + transpose(rotation_matrix) * mouse_uv / zoom;
                    }
                }            
            } else {
                // Set flag that we are not dragging
                state.z = 0.0;        
            }

            fragColor = state;            
        } else {        
        	fragColor = camera;
        }

        return;
    }        

    vec3 col = DrawMainImage(fragCoord, camera.xy, zoom, rotation_matrix);

    if (minimap_enabled) {
    	float minimap_opacity = 0.;
    	vec3 minimap_col = DrawMinimap(fragCoord, camera.xy, zoom,
                                       rotation_matrix, minimap_opacity);             	    
  		col = mix(col, minimap_col, minimap_opacity);           
    }

    fragColor = vec4(col, 1.0);
}

