//=======================================================
//= LEAN / LEADR Mapping
//=======================================================
//== Ressources :
//== *- 2013 - Filtering Distributions of Normals for Shading Antialiasing
//== 	by J. Dupuy, E. Heitz, J-C Iehl, P. Poulin, F. Neyret & V. Ostromoukhov 
//== *- 2010 - LEAN mapping
//==  	by M. Olano & D. Baker
//==
//== demo : https://www.shadertoy.com/view/wlXXRn by FabriceNeyret2
//==
//=======================================================
//== Code :
//== *- Common   : Tangent math + Plane intersection + a dirty-tiled-LRPN-version 
//== *- Buffer A : Return the slope of a height field (texture(iChannel3) or procedure)	  				
//== *- Buffer B : Compute the 2nd moments -> ( slope.x^2 ; slope.y^2 , slope.x * slope.y )
//==
//== *- Image 	 : LEAN/LEADR using Shifted Anisotropic Beckmann NDF 				
//==
//== Usage :
//== *- Uncomment #define ANIMATED_TEXTURE in Common to see a phase-shifted LRPN animation 
//== *- Mouse X   : changing isotropic roughness
//== *- Mouse Y   : changing LOD Bias
//== 
//=======================================================

//-----------------------------------------------------------------------------
//-- Constants ----------------------------------------------------------------
const vec4  UNIT_PLANE     	= vec4(0.,1.,0.,0.);

const float LIGHT_INTENSITY = 5.;
const vec3  LIGHT_DIRECTION = normalize(vec3(0.,0.1,1.));
const vec3  BACKGROUND_GREY = vec3(0.2);

//-------------------------------------------------------------------------------
//-- Beckmann Shifted Anisotropic distribution ----------------------------------
//-------------------------------------------------------------------------------
float p22_beckmann_shifted(	float x, float y, vec2 mean,
                        	float sigma_x, float sigma_y, float rho)
{
    x -= mean.x;
    y -= mean.y;
    float x_sqr = x*x;
    float y_sqr = y*y;
    float sigma_x_sqr = sigma_x*sigma_x;
    float sigma_y_sqr = sigma_y*sigma_y;
    float rho_sqr = rho*rho;

    return( 
            exp( -( 1.f /( 2.f*(1.f-rho_sqr))) * ((x_sqr/sigma_x_sqr) + (y_sqr/sigma_y_sqr) - ((2.f*rho*x*y)/(sigma_x*sigma_y))) )
    / //----------------------------------------------------------------------------------------------------
                        ( 2.f * m_pi * sigma_x * sigma_y * sqrt(1.f - rho_sqr) )
    );
}

float ndf_beckmann_shifted(	vec3 omega_h, vec2 mean,
                            float sigma_x, float sigma_y, float rho)
{
    float slope_x = - (omega_h.x/omega_h.z);
    float slope_y = - (omega_h.y/omega_h.z);
    float cos_theta = cos_theta(omega_h);
    float cos_2_theta = cos_theta * cos_theta;
    float cos_4_theta = cos_2_theta * cos_2_theta;
    float beckmann_p22 = p22_beckmann_shifted(slope_x,slope_y,mean, sigma_x,sigma_y,rho);
    return(
                beckmann_p22
    / //---------------------------
                cos_4_theta
    );
}

float lambda_beckmann_shifted(	vec3 omega, vec2 mean, 
                                float sigma_x, float sigma_y, float rho)
{
    float lambda 	= 0.f;
    float cos_phi 	= cos_phi(omega);
    float sin_phi 	= sin_phi(omega);
    float tan_theta = tan_theta(omega);
    float sigma_sqr = cos_phi*cos_phi * sigma_x*sigma_x 
                    + sin_phi*sin_phi * sigma_y*sigma_y
                    + 2.f*cos_phi*sin_phi * rho*sigma_x*sigma_y;

    float sigma     = sqrt(sigma_sqr); 

    float cot_theta  = 1.f / tan_theta;
    float mu_phi     = cos_phi*mean.x + sin_phi*mean.y;
    float nu         = (cot_theta - mu_phi) / (sigma * m_sqrt_2);

    if(nu < 1.6f)
        lambda = (1.f - 1.259f*nu + 0.396f*nu*nu) / (3.535f*nu + 2.181f*nu*nu); 
    return(lambda);
}


//-----------------------------------------------------------------------------
//-- Fresnel ------------------------------------------------------------------
vec3 fresnel_schlick(in float wo_dot_wh, in vec3 F0)
{
    return F0 + (1. - F0) * pow(1. - wo_dot_wh, 5.);
}

//-----------------------------------------------------------------------------
//-- World Functions ----------------------------------------------------------
float 	get_light_intensity()            { return LIGHT_INTENSITY; }
vec3 	get_light_direction()            { return LIGHT_DIRECTION; }
vec3 	get_background_color(in vec3 rd) { return BACKGROUND_GREY; }

//-----------------------------------------------------------------------------
//-- Main ---------------------------------------------------------------------
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{      
    vec2 screen_coords = ( 2.*gl_FragCoord.xy - iResolution.xy ) / iResolution.y;
    vec3 ro = vec3( 0., 1. , -3. );
    vec3 rd = compute_camera_ray_direction(screen_coords,ro,vec3(0.),0.);

    vec2  mouse 	= clamp(iMouse.xy / iResolution.xy ,0.,1.);
    float m_alpha 	= clamp( 0.2  + mouse.x , 0., 0.6 ); 
	float m_lod 	= clamp( 0.01 + mouse.y , 0., 1.0 ) * 5.;


    vec3  color 	= get_background_color(rd);
    float plane_hit = plane_get_hit(ro,rd,UNIT_PLANE);

    if(plane_hit > 0.0) 
    {
        vec3	P = ro + plane_hit * rd;
        vec3    N = plane_get_normal(P,UNIT_PLANE);
    #ifdef ANIMATED_TEXTURE
        vec2 	T = plane_get_texcoords(P,0.2);
    #else
        vec2 	T = plane_get_texcoords(P,1.);
    #endif

        vec3    L = get_light_direction();
        vec3    V = -rd;

        mat3 TBN    = orthonormal_basis(N);
        mat3 TBN_t  = transpose(TBN);
        vec3 wo 	= normalize(TBN_t * V);
        vec3 wi 	= normalize(TBN_t * L);
        vec3 wg 	= normalize(TBN_t * N); // ( should be (0,0,1)^t )
        vec3 wh 	= normalize(wo+wi);		// ( could check it not zero)

        vec2 slope 		= texture(iChannel0,T,m_lod).rg;
    	vec3 moments 	= texture(iChannel1,T,m_lod).rgb;

       	/* Square roughness mapping */
        float base_alpha = m_alpha*m_alpha;
        /* converting base alpha in rms roughness convention */
        float base_sigma = base_alpha * m_i_sqrt_2; 

  		/* Retrieve covariance matrix elements (eq. 9) */
        /** Bivariate Normal Distribution covariance matrix : 
		 * 
		 * |     sigma_x^2       rho*sigma_x*sigma_y |
		 * | rho*sigma_x*sigma_y      sigma_y^2      |
		 * 
		 */

        float sigma_x_sqr = base_sigma*base_sigma + (moments.x - slope.x*slope.x);
        float sigma_y_sqr = base_sigma*base_sigma + (moments.y - slope.y*slope.y);
        float cor_x_y = moments.z - slope.x*slope.y;

        float sigma_x = sqrt(sigma_x_sqr);
        float sigma_y = sqrt(sigma_y_sqr);
        float rho = cor_x_y / (sigma_x * sigma_y);
        rho = clamp(rho,-0.999,0.999);

        if(screen_coords.x > 0.) //naive version
        {
        	sigma_x = base_sigma;
        	sigma_y = base_sigma;
         	rho 	= 0.;   
        }

        float h_dot_v = clamp(dot(wo,wh),0.,1.);
        float n_dot_l = clamp(dot(wg,wi),0.,1.);

        float projected_area = 1. / dot( vec3(-slope.x,-slope.y,1) , wo);
        float lambda_wo = lambda_beckmann_shifted(wo,slope,sigma_x,sigma_y,rho);
        float lambda_wi = lambda_beckmann_shifted(wi,slope,sigma_x,sigma_y,rho);
        float D = ndf_beckmann_shifted(wh,slope,sigma_x,sigma_y,rho);
        float G = 1. / (1. + lambda_wo + lambda_wi);

		vec3 F  = fresnel_schlick(h_dot_v,vec3(1.022, 0.782, 0.344));
        vec3 BRDF = 0.25 * projected_area * (D * F * G) ;

        color = n_dot_l * get_light_intensity() * vec3(BRDF);
    }

    fragColor = vec4(pow(color.rgb,vec3(0.4545)),1.);

	if(screen_coords.x <= 0.01 && screen_coords.x >= -0.01) 
        fragColor = vec4(1.,0.,0.,1.);
}

